<script>
// ---------- Helper Functions ----------
function gevPDF(x, mu, sigma, xi){
    if(sigma <= 0) return 0;
    if(Math.abs(xi) < 1e-6){
        // Gumbel
        const z = (x - mu)/sigma;
        return (1/sigma) * Math.exp(-z) * Math.exp(-Math.exp(-z));
    }else{
        const tx = 1 + xi * (x - mu)/sigma;
        if(tx <= 0) return 0;
        const t = Math.pow(tx, -1/xi);
        return (1/sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
    }
}

function gevReturnLevel(p, mu, sigma, xi){
    if(p <= 1) return NaN;
    if(Math.abs(xi) < 1e-6){
        return mu - sigma * Math.log(-Math.log(1 - 1/p));
    }else{
        const qp = -Math.log(1 - 1/p);
        return mu - (sigma/xi)*(1 - Math.pow(qp, -xi));
    }
}

// ---------- DOM Elements ----------
const sliders = [
    'slider-mu1','slider-sigma1','slider-xi1',
    'slider-mu2','slider-sigma2','slider-xi2',
    'slider-pmax'
];
const displays = {
    'slider-mu1':'display-mu1',
    'slider-sigma1':'display-sigma1',
    'slider-xi1':'display-xi1',
    'slider-mu2':'display-mu2',
    'slider-sigma2':'display-sigma2',
    'slider-xi2':'display-xi2',
    'slider-pmax':'display-pmax'
};

// ---------- Update Function ----------
function update(){
    // Read slider values & update readouts
    const vals = {};
    sliders.forEach(id=>{
        const slider = document.getElementById(id);
        vals[id] = parseFloat(slider.value);
        document.getElementById(displays[id]).value = slider.value;
    });

    // Unpack parameters
    const mu1 = vals['slider-mu1'], sigma1 = vals['slider-sigma1'], xi1 = vals['slider-xi1'];
    const mu2 = vals['slider-mu2'], sigma2 = vals['slider-sigma2'], xi2 = vals['slider-xi2'];
    const pMax = vals['slider-pmax'];

    // ---------- PDF computation ----------
    const xMin = 2.5, xMax = 7, nX = 500;
    const xs = Array.from({length:nX}, (_,i)=> xMin + (xMax - xMin)*i/(nX-1));
    const pdf1 = xs.map(x=>gevPDF(x, mu1, sigma1, xi1));
    const pdf2 = xs.map(x=>gevPDF(x, mu2, sigma2, xi2));

    const pdfData = [
        {
            x: xs,
            y: pdf1,
            mode:'lines',
            name:'GEV 1',
            line:{color:'blue'},
            fill:'tozeroy',
            fillcolor:'rgba(0,0,255,0.2)'
        },
        {
            x: xs,
            y: pdf2,
            mode:'lines',
            name:'GEV 2',
            line:{color:'red'},
            fill:'tozeroy',
            fillcolor:'rgba(255,0,0,0.2)'
        }
    ];

    const pdfLayout = {
        title:'PDFs of GEV Distributions 1 and 2',
        xaxis:{range:[xMin,xMax]},
        yaxis:{automargin:true},
        margin:{l:50,r:20,t:40,b:40},
        showlegend:true
    };

    Plotly.react('plot-pdf', pdfData, pdfLayout, {responsive:true});

    // ---------- Return Level computation ----------
    const nP = 200;
    const ps = Array.from({length:nP}, (_,i)=> 1 + (pMax - 1) * (i/(nP-1)));
    const rl1 = ps.map(p=>gevReturnLevel(p, mu1, sigma1, xi1));
    const rl2 = ps.map(p=>gevReturnLevel(p, mu2, sigma2, xi2));

    const rlData = [
        {
            x: ps,
            y: rl1,
            mode:'lines',
            name:'GEV 1',
            line:{color:'blue'}
        },
        {
            x: ps,
            y: rl2,
            mode:'lines',
            name:'GEV 2',
            line:{color:'red'}
        }
    ];

    const rlLayout = {
        xaxis:{title:'Return Period (years)', range:[0,pMax]},
        yaxis:{title:'Maximum Sea Level (meters)', automargin:true},
        margin:{l:60,r:20,t:20,b:50},
        showlegend:true
    };

    Plotly.react('plot-return-level', rlData, rlLayout, {responsive:true});
}

// ---------- Event Listeners ----------
sliders.forEach(id=>{
    document.getElementById(id).addEventListener('input', update);
});

// ---------- Initial Draw ----------
update();
</script>
