<body>
<div id="main-container">
    <!-- ------------ CONTROL PANEL ------------ -->
    <div id="control-panel">
        <!-- signal selector -->
        <div class="signal-row">
            <label>signal:</label>
            <button id="btn-rampdown"  class="signal-btn">RampDown</button>
            <button id="btn-tophat"    class="signal-btn">TopHat</button>
            <button id="btn-stairstep" class="signal-btn">StairStep</button>
            <button id="btn-triangle"  class="signal-btn active">Triangle</button>
            <button id="btn-uh"        class="signal-btn">UH</button>
        </div>

        <!-- sliders -->
        <div>
            <div class="slider-row">
                <label for="slider-a">a</label>
                <input type="range" id="slider-a" min="0.1" max="4" step="0.1" value="2">
                <span id="slider-a-value">2.0</span>
            </div>
            <div class="slider-row">
                <label for="slider-b">b</label>
                <input type="range" id="slider-b" min="-10" max="10" step="0.1" value="4">
                <span id="slider-b-value">4.0</span>
            </div>
            <div class="slider-row">
                <label for="slider-t">T = b/a</label>
                <input type="range" id="slider-t" min="-25" max="25" step="0.1" value="2">
                <span id="slider-t-value">2.0</span>
            </div>
        </div>

        <!-- display options -->
        <div class="display-row">
            <label>display:</label>

            <input type="checkbox" id="check-x" checked>
            <label for="check-x">\(x(t)\)</label>

            <input type="checkbox" id="check-y" checked>
            <label for="check-y">\(y(t)=x(at-b)=x(a(t-T))\)</label>

            <input type="checkbox" id="check-z" checked>
            <label for="check-z">\(z(t)=x(t-b)\)</label>

            <input type="checkbox" id="check-w">
            <label for="check-w">\(w(t)=x(at)\)</label>
        </div>
    </div>

    <!-- ------------- PLOTS ------------- -->
    <div id="plot-top"></div>
    <div id="plot-bottom"></div>
</div>

<script>
/* ------------------------------------------------------------------
   GLOBAL STATE
-------------------------------------------------------------------*/
let activeSignal = 'triangle';      // default
let a = 2.0;
let b = 4.0;
let T = b / a;

// Shortcuts to DOM elements
const btnIds   = ['btn-rampdown','btn-tophat','btn-stairstep','btn-triangle','btn-uh'];
const checkIds = ['check-x','check-y','check-z','check-w'];

const sliderA = document.getElementById('slider-a');
const sliderB = document.getElementById('slider-b');
const sliderT = document.getElementById('slider-t');

const spanA = document.getElementById('slider-a-value');
const spanB = document.getElementById('slider-b-value');
const spanT = document.getElementById('slider-t-value');

const plotTop    = document.getElementById('plot-top');
const plotBottom = document.getElementById('plot-bottom');

/* ------------------------------------------------------------------
   SIGNAL DEFINITIONS
   Each evaluator returns y given t for the chosen signal.
-------------------------------------------------------------------*/
function evaluateSignal(name, t){
    switch(name){
        case 'triangle':
            if (t>=0 && t<=3) return (4/3)*t;
            if (t>3 && t<=6) return -(4/3)*(t-3)+4;
            return 0;
        case 'tophat':
            return (t>=1 && t<=5)?4:0;
        case 'stairstep':
            if      (t>=1 && t<=2) return 1;
            else if (t>2  && t<=4) return 4;
            else if (t>4  && t<=5) return 1;
            return 0;
        case 'rampdown':
            return (t>=0 && t<=6)?(-2/3)*t + 4:0;
        case 'uh':
            // U-H shaped approximation
            if      (t>=1   && t<=1.5) return 4;
            else if (t>1.5  && t<=2.5) return 1;
            else if (t>2.5  && t<=3 )  return 4;
            else if (t>=4   && t<=4.5) return 4;
            else if (t>4.5  && t<=5.5) return 2;
            else if (t>5.5  && t<=6 )  return 4;
            return 0;
        default:
            return 0;
    }
}

/* ------------------------------------------------------------------
   GENERATE DATA ARRAYS FOR PLOTTING
-------------------------------------------------------------------*/
function generateData(){
    const tMin = -10, tMax = 20, dt = 0.05;
    const nPts = Math.round((tMax - tMin)/dt) + 1;
    const tArr = new Array(nPts);
    const xArr = new Array(nPts);
    const yArr = new Array(nPts);
    const zArr = new Array(nPts);
    const wArr = new Array(nPts);

    for(let i=0;i<nPts;i++){
        const t = tMin + i*dt;
        tArr[i] = t;
        const x = evaluateSignal(activeSignal, t);
        const y = evaluateSignal(activeSignal, a*t - b);
        const z = evaluateSignal(activeSignal, t - b);
        const w = evaluateSignal(activeSignal, a*t);
        xArr[i]=x;
        yArr[i]=y;
        zArr[i]=z;
        wArr[i]=w;
    }
    return {tArr, xArr, yArr, zArr, wArr};
}

/* ------------------------------------------------------------------
   UPDATE / DRAW PLOTS
-------------------------------------------------------------------*/
function updatePlots(){
    // Generate data
    const {tArr,xArr,yArr,zArr,wArr} = generateData();

    /* ------------------ TOP PLOT (x(t)) ------------------ */
    const topTrace = {
        x: tArr,
        y: xArr,
        type:'scatter',
        mode:'lines',
        name:'\\(x(t)\\)',
        line:{color:'rgb(70,130,180)', width:2.5}
    };

    const shapesTop = [
        // red dot T
        {type:'circle',xref:'x',yref:'y',
         x0:T-0.06,x1:T+0.06,y0:-0.06,y1:0.06,
         fillcolor:'red',line:{color:'red'}},
        // green dot b
        {type:'circle',xref:'x',yref:'y',
         x0:b-0.06,x1:b+0.06,y0:-0.06,y1:0.06,
         fillcolor:'green',line:{color:'green'}}
    ];
    const annTop = [
        {xref:'x',yref:'y',x:T,y:0.15,text:'T',font:{color:'red'},showarrow:false},
