<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

<!-- ----------  SCRIPT  ---------- -->
<script>
/* =====================================================
   Basic THREE.js setup
   ===================================================== */
const canvas          = document.getElementById('main-canvas');
const renderer        = new THREE.WebGLRenderer({canvas, antialias:true});
renderer.setPixelRatio(window.devicePixelRatio);

const scene           = new THREE.Scene();
scene.background      = new THREE.Color(0xf0f0f0);

const camera          = new THREE.PerspectiveCamera(45, 2, 0.1, 1000);
camera.position.set(0, -10, 6);

const controls        = new THREE.OrbitControls(camera, canvas);
controls.enableDamping = true;

/* Lights */
scene.add(new THREE.HemisphereLight(0xffffff, 0x444444, 0.9));
const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
dirLight.position.set(5,5,8);
scene.add(dirLight);

/* Resizing */
function resizeRenderer(){
    const {clientWidth: w, clientHeight: h} = canvas.parentElement;
    if (canvas.width  !== w || canvas.height !== h){
        renderer.setSize(w, h, false);
        camera.aspect = w / h;
        camera.updateProjectionMatrix();
    }
}
window.addEventListener('resize', resizeRenderer);

/* =====================================================
   Geometry generation helpers
   ===================================================== */
function createCurvedTriangle(trim, colCenter, colEdge, segments=42){
    // base vertices (equilateral triangle on unit sphere)
    const v0 = new THREE.Vector3( 1, 0, 0).normalize();
    const v1 = new THREE.Vector3(-0.5,  Math.sqrt(3)/2, 0).normalize();
    const v2 = new THREE.Vector3(-0.5, -Math.sqrt(3)/2, 0).normalize();

    // scale to trim radius
    v0.multiplyScalar(trim);
    v1.multiplyScalar(trim);
    v2.multiplyScalar(trim);

    // perimeter points
    const perimeter = [];
    const edgePairs = [[v0,v1],[v1,v2],[v2,v0]];
    edgePairs.forEach(pair=>{
        const [a,b] = pair;
        for(let i=0;i<segments;i++){
            const t = i/segments;
            const p = a.clone().lerp(b, t).normalize().multiplyScalar(trim);
            perimeter.push(p);
        }
    });

    // center point (spherical centroid approximation)
    const center = v0.clone().add(v1).add(v2).normalize().multiplyScalar(trim);

    const positions = [];
    const colors    = [];
    const color1 = new THREE.Color(colCenter);
    const color2 = new THREE.Color(colEdge);

    // push center first
    positions.push(center.x, center.y, center.z);
    colors.push(color1.r,color1.g,color1.b);

    // push perimeter
    perimeter.forEach(p=>{
        positions.push(p.x,p.y,p.z);
        colors.push(color2.r,color2.g,color2.b);
    });

    // indices for triangle fan
    const indices = [];
    const perimLen = perimeter.length;
    for(let i=0;i<perimLen;i++){
        const a = 0;
        const b = i+1;
        const c = ( (i+1) % perimLen ) +1;
        indices.push(a,b,c);
    }

    // build geometry
    const geom = new THREE.BufferGeometry();
    geom.setIndex(indices);
    geom.setAttribute('position', new THREE.Float32BufferAttribute(positions,3));
    geom.setAttribute('color', new THREE.Float32BufferAttribute(colors,3));
    geom.computeVertexNormals();
    return geom;
}

function createOutlineFromGeometry(geom){
    const posAttr = geom.getAttribute('position');
    const center   = new THREE.Vector3().fromBufferAttribute(posAttr,0);
    const linePoints = [];
    for(let i=1;i<posAttr.count;i++){
        const v = new THREE.Vector3().fromBufferAttribute(posAttr,i);
        linePoints.push(v);
    }
    // close loop
    linePoints.push(new THREE.Vector3().fromBufferAttribute(posAttr,1));

    const lineGeom = new THREE.BufferGeometry().setFromPoints(linePoints);
    return lineGeom;
}

/* =====================================================
   Lattice Generation
   ===================================================== */
const everythingGroup = new THREE.Group();
scene.add(everythingGroup);

function updateScene(){
    // clear previous
    while(everythingGroup.children.length){
        everythingGroup.remove(everythingGroup.children[0]);
    }

    // read UI state
    const unitsX   = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
    const unitsY   = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
    const trimVal  = parseFloat(document.getElementById('slider-trim').value);
    const isDouble = document.getElementById('checkbox-double').checked;
    const showSphere     = document.getElementById('checkbox-sphere').checked;
    const showTriangles  = document.getElementById('checkbox-triangles').checked;

    const mainGeom  = createCurvedTriangle(trimVal, 0xffc0cb, 0x904090);
    const mainMat   = new THREE.MeshStandardMaterial({vertexColors:true, side:THREE.DoubleSide});
    const mainMesh  = new THREE.Mesh(mainGeom, mainMat);
    const mainOutline = new THREE.LineSegments(createOutlineFromGeometry(mainGeom),
                              new THREE.LineBasicMaterial({color:0x000000}));

    // geometry for second lattice if required
    let doubleGeom, doubleMat;
    if(isDouble){
        doubleGeom = createCurvedTriangle(trimVal, 0x80ffff, 0x008080);
        doubleMat  = new THREE.MeshStandardMaterial({vertexColors:true, side:THREE.DoubleSide});
    }

    // lattice vectors
    const scale = 3.5; // controls spacing
    const u = new THREE.Vector3(scale, 0, 0);
    const v = new THREE.Vector3(scale/2, Math.sqrt(3)*scale/2, 0);

    // nested loops
    for(let ix=-unitsX; ix<=unitsX; ix++){
        for(let iy=-unitsY; iy<=unitsY; iy++){
            const pos = u.clone().multiplyScalar(ix).add(v.clone().multiplyScalar(iy));

            // primary lattice
            const mesh = mainMesh.clone();
            mesh.position.copy(pos);
            const outline = mainOutline.clone();
            outline.position.copy(pos);

            if( (ix+iy)%2===1 ){
                mesh.rotation.z = Math.PI;
                outline.rotation.z = Math.PI;
            }
            everythingGroup.add(mesh);
            everythingGroup.add(outline);

            // flat triangle overlay
            if(showTriangles){
                const flatGeom = new THREE.CircleGeometry(scale*0.85,3); // simple flat placeholder
                flatGeom.rotateZ(Math.PI/2);
                const flatMat = new THREE.MeshBasicMaterial({color:0x800080, transparent:true, opacity:0.4, side:THREE.DoubleSide});
                const flat = new THREE.Mesh(flatGeom, flatMat);
                flat.position.copy(pos);
                everythingGroup.add(flat);

                // vertex points
                const ptsMat = new THREE.PointsMaterial({color:0x000000, size:6});
                const ptsGeom = new THREE.BufferGeometry();
                ptsGeom.setAttribute('position', new THREE.Float32BufferAttribute([0,0,0],3));
                const pts = new THREE.Points(ptsGeom, ptsMat);
                pts.position.copy(pos);
                everythingGroup.add(pts);
            }

            // second lattice
            if(isDouble){
                const dmesh = new THREE.Mesh(doubleGeom, doubleMat);
                const dOutline = new THREE.LineSegments(createOutlineFromGeometry(doubleGeom),
                                       new THREE.LineBasicMaterial({color:0x000000}));
                // offset by half-sum of lattice vectors
                const offset = u.clone().add(v).multiplyScalar(0.5);
                dmesh.position.copy(pos).add(offset);
                dOutline.position.copy(pos).add(offset);
                dmesh.rotation.z = ( (ix+iy)%2===0 ) ? Math.PI:0; // opposite orientation
                dOutline.rotation.z = dmesh.rotation.z;
                everythingGroup.add(dmesh);
                everythingGroup.add(dOutline);
            }
        }
    }

    // add sphere if required
    if(showSphere){
        const sphGeom = new THREE.SphereGeometry(trimVal, 40, 40);
        const sphMat  = new THREE.MeshPhongMaterial({color:0xdddddd, shininess:80});
        const sphere  = new THREE.Mesh(sphGeom, sphMat);
        everythingGroup.add(sphere);
    }

    renderOnce(); // immediate render after rebuild
}

/* =====================================================
   Controls & Events
   ===================================================== */

// slider label update
const sliderTrim = document.getElementById('slider-trim');
sliderTrim.addEventListener('input', ()=>{
    document.getElementById('label-trim-value').textContent = parseFloat(sliderTrim.value).toFixed(3);
    updateScene();
});

document.querySelectorAll('#control-panel input[type="radio"]').forEach(el=>{
    el.addEventListener('change', updateScene);
});
document.querySelectorAll('#control-panel input[type="checkbox"]').forEach(el=>{
    el.addEventListener('change', updateScene);
});

// reset view button
document.getElementById('btn-reset-view').addEventListener('click', ()=>controls.reset());

/* =====================================================
   Animation loop
   ===================================================== */
function renderOnce(){
    resizeRenderer();
    renderer.render(scene,camera);
}

function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene,camera);
}

/* =====================================================
   Start
   ===================================================== */
resizeRenderer();
updateScene();   // initial build
animate();
</script>
</body>
</html>
