</head>
<body>
<div id="main-container">
    <!-- Info -->
    <button id="info-button">+</button>
    <div id="description-panel" style="display:none;">
        <p>
        This interactive demo illustrates several analog signals and two common time-encoding schemes:
        the Asynchronous Sigma-Delta Modulator (ASDM) and the Integrate-and-Fire (IAF) neuron model.
        Move the sliders to change the visible time window, choose a different signal, or switch
        encoding methods to see how the timing of events adapts to the underlying waveform.
        </p>
    </div>

    <!-- Controls -->
    <div id="control-panel">
        <!-- Row 1 – start time -->
        <div>
            <label for="slider-start-time">start time</label>
            <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
            <span id="start-time-readout">-1.0</span>
        </div>
        <!-- Row 2 – total time -->
        <div>
            <label for="slider-total-time">total time</label>
            <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
            <span id="total-time-readout">2.2</span>
        </div>
        <!-- Row 3 – functions -->
        <div>
            <label>function</label>
            <div id="function-buttons">
                <button id="btn-f">f</button>
                <button id="btn-g" class="active-btn">g</button>
                <button id="btn-sin">sin</button>
                <button id="btn-cos">cos</button>
                <button id="btn-tan">tan</button>
            </div>
        </div>
        <!-- Row 4 – encoding -->
        <div>
            <label>encoding method</label>
            <div id="encoding-buttons">
                <button id="btn-asdm" class="active-btn">ASDM</button>
                <button id="btn-iaf">IAF</button>
            </div>
        </div>
    </div>

    <!-- Visualization area -->
    <div id="canvas-container"></div>
</div>

<script>
/* ────────────────────── GLOBAL STATE ────────────────────── */
const state = {
    startTime   : -1.0,
    totalTime   : 2.2,
    func        : 'g',
    encoding    : 'ASDM'
};

/* ────────────────────── DOM UTILITIES ───────────────────── */
function setActive(groupId, clickedBtn){
    document.querySelectorAll('#'+groupId+' button').forEach(btn=>{
        btn.classList.remove('active-btn');
    });
    clickedBtn.classList.add('active-btn');
}

/* ────────────────────── EVENT LISTENERS ─────────────────── */
document.getElementById('info-button').addEventListener('click',()=>{
    const panel = document.getElementById('description-panel');
    panel.style.display = (panel.style.display==='none' || panel.style.display==='') ? 'block':'none';
});

/* sliders */
const startSlider = document.getElementById('slider-start-time');
const totalSlider = document.getElementById('slider-total-time');
startSlider.addEventListener('input', e=>{
    state.startTime = parseFloat(e.target.value);
    document.getElementById('start-time-readout').innerText = state.startTime.toFixed(1);
    pInst.redraw();
});
totalSlider.addEventListener('input', e=>{
    state.totalTime = parseFloat(e.target.value);
    document.getElementById('total-time-readout').innerText = state.totalTime.toFixed(1);
    pInst.redraw();
});

/* function buttons */
document.getElementById('function-buttons').addEventListener('click', e=>{
    if(e.target.tagName!=="BUTTON") return;
    state.func = e.target.id.replace('btn-','');
    setActive('function-buttons', e.target);
    pInst.redraw();
});

/* encoding buttons */
document.getElementById('encoding-buttons').addEventListener('click', e=>{
    if(e.target.tagName!=="BUTTON") return;
    state.encoding = e.target.textContent; // "ASDM" or "IAF"
    setActive('encoding-buttons', e.target);
    pInst.redraw();
});

/* ────────────────────── p5.js SKETCH ────────────────────── */
const sketch = (p)=>{
    const margin = {left:60, right:20, top:20, bottom:40};
    let W, H;

    /* -------- helpers -------- */
    const xPixel = t => p.map(t, state.startTime, state.startTime+state.totalTime, margin.left, W-margin.right);
    const yPixel = y => p.map(y, -1.2, 1.2, H-margin.bottom, margin.top);

    function analogFunc(t){
        switch(state.func){
            case 'f':   return 0.5*t;
            case 'g':   return Math.sin(2*Math.PI*t)+0.3*Math.cos(6*Math.PI*t);
            case 'sin': return Math.sin(2*Math.PI*t);
            case 'cos': return Math.cos(2*Math.PI*t);
            case 'tan': return Math.tan(Math.PI*t); /* handle later */
            default:    return 0;
        }
    }

    /* -------- simulations -------- */
    function computeASDM(){
        const B=0.25, delta=0.05;
        let outLvl=B, integral=0;
        let t=state.startTime;
        const tEnd = state.startTime+state.totalTime;
        const dt = state.totalTime/1000; // #steps ≈1000
        const flips = [{time:t, level:outLvl}];

        while(t < tEnd){
            const diff = analogFunc(t) - outLvl;
            integral += diff*dt;
            if( (outLvl===B && integral<-delta) ||
                (outLvl===-B && integral>delta) ){
                outLvl = -outLvl;
                flips.push({time:t, level:outLvl});
                integral=0;
            }
            t+=dt;
        }
        flips.push({time:tEnd, level:outLvl}); // ensure last segment reaches end
        return flips;
    }

    function computeIAF(){
        const theta=0.05, ySpike=0.3;
        let integral=0;
        let t=state.startTime;
        const tEnd = state.startTime+state.totalTime;
        const dt = state.totalTime/1000;
        const spikes=[];
        while(t<tEnd){
            integral += analogFunc(t)*dt;
            if(integral>=theta){
                spikes.push(t);
                integral=0;
            }
            t+=dt;
        }
        return {spikes, y:ySpike};
    }

    /* -------- draw helpers -------- */
    function drawAxes(){
        p.stroke(0);
        p.strokeWeight(1);
        // x-axis (y=0)
        p.line(margin.left, yPixel(0), W-margin.right, yPixel(0));
        // y-axis (x = startTime==0 ? maybe inside) at t=0 if inside range else at margin.left
        const x0 = (state.startTime<=0 && 0<=state.startTime+state.totalTime) ? xPixel(0):margin.left;
        p.line(x0, margin.top, x0, H-margin.bottom);

        // ticks
        p.textSize(10);
        p.fill(0);
        p.noStroke();

        // x ticks 0.5
        p.stroke(0);
        for(let t=Math.ceil(state.startTime*2)/2; t<=state.startTime+state.totalTime; t+=0.5){
            const x=xPixel(t);
            p.line(x, yPixel(0)-3, x, yPixel(0)+3);
            p.noStroke(); p.text(t.toFixed(1), x-8, yPixel(0)+15); p.stroke(0);
        }
        // y ticks
        for(let y=-1; y<=1; y+=0.5){
            const yp=yPixel(y);
            p.line(x0-3, yp, x0+3, yp);
            p.noStroke(); p.text(y.toFixed(1), x0-30, yp+4); p.stroke(0);
        }
    }

    function drawAnalog(){
        p.noFill();
        p.stroke(0,0,255);
        p.strokeWeight(2);
        let first=true;
        let prevX, prevY;
        // sample points
        const steps=1000;
        for(let i=0;i<=steps;i++){
            const t = state.startTime + i/steps*state.totalTime;
            let y = analogFunc(t);
            // handle tan discontinuities: skip if |y|>1.2*?; we restrict
            if(!isFinite(y) || Math.abs(y)>1.2){
                first=true;
                continue;
            }
            const px = xPixel(t);
            const py = yPixel(y);
            if(first){
                p.beginShape();
                p.vertex(px, py);
                first=false;
            }else{
                p.vertex(px, py);
            }
        }
        if(!first) p.endShape();
    }

    function drawASDM(){
        const flips = computeASDM();
        p.stroke(255,0,0);
        p.strokeWeight(2);
        for(let i=0;i<flips.length-1;i++){
            const t1=flips[i].time, lvl=flips[i].level, t2=flips[i+1].time;
            // horizontal
            p.line(xPixel(t1), yPixel(lvl), xPixel(t2), yPixel(lvl));
            // vertical flip line (except first segment)
            if(i>0){
                const prevLvl = flips[i-1].level;
                p.line(xPixel(t1), yPixel(prevLvl), xPixel(t1), yPixel(lvl));
            }
        }
    }

    function drawIAF(){
        const res = computeIAF();
        p.stroke(255,0,0);
        p.fill(255,0,0);
        p.strokeWeight(1.5);
        res.spikes.forEach(ts=>{
            const x = xPixel(ts);
            const y0 = yPixel(0);
            const y1 = yPixel(res.y);
            p.line(x, y0, x, y1);
            p.circle(x, y1, 5);
        });
    }

    /* -------- p5 lifecycle -------- */
    p.setup = ()=>{
        W = document.getElementById('canvas-container').clientWidth;
        H = document.getElementById('canvas-container').clientHeight || 400;
        p.createCanvas(W, H).parent('canvas-container');
        p.noLoop();
        p.redraw();
    };

    p.draw = ()=>{
        p.clear();
        p.background(255);
        drawAxes();
        drawAnalog();
        if(state.encoding==='ASDM'){
            drawASDM();
        }else{
            drawIAF();
        }
    };

    /* handle resize */
    window.addEventListener('resize', ()=>{
        W = document.getElementById('canvas-container').clientWidth;
        H = document.getElementById('canvas-container').clientHeight || 400;
        p.resizeCanvas(W,H);
        p.redraw();
    });
};

/* Start p5 instance */
const pInst = new p5(sketch);
</script>
</body>
</html>
