    let showLabels = false,
        showCircle = false,
        showAxes   = false,
        showGrid   = false;

    const pts = {};  // will hold all point coordinates

    /* -------------------- Utility -------------------- */
    function intRand(min, max){
        return Math.floor(Math.random()*(max-min+1))+min;
    }

    function coeffToStr(coef, variable, power){
        if(coef === 0) return '';
        let abs = Math.abs(coef);
        let sign = coef > 0 ? ' + ' : ' - ';
        let core = (abs === 1 && variable) ? '' : abs;
        return sign + core + (variable ? variable + (power>1?'^'+power:'') : '');
    }

    function polynomialString(a,b,c){
        // Build human-readable polynomial string (a is never 0 here)
        let str = (a===1) ? 'z^2' : (a===-1 ? '-z^2' : a + 'z^2');
        str += coeffToStr(b,'z',1);
        str += coeffToStr(c,'',0);
        return str.replace(/^\+ /,'').replace(/\s+/g,' ');
    }

    function dist(p,q){
        return Math.hypot(p.x - q.x, p.y - q.y);
    }

    function calcPoints(){
        // Base path
        pts.O  = {x:0,        y:0};
        pts.L1 = {x:a,        y:0};
        pts.L2 = {x:a,        y:b};
        pts.L3 = {x:a - c,    y:b};

        // Test path
        pts.L1p = {x:a, y: -z*a};
        pts.L2p = {x: a + b*z + a*z*z, y:b};

        // Circle
        pts.C  = {x:(a-c)/2, y:b/2};
        pts.d  = Math.sqrt((a-c)*(a-c) + b*b);  // diameter
    }

    function updateResults(){
        const pz = a*z*z + b*z + c;
        const err = dist(pts.L3, pts.L2p);
        valPz.textContent  = pz.toFixed(4);
        valErr.textContent = err.toFixed(4);
        valZ.textContent   = z.toFixed(2);
    }

    function updateUI(){
        labelZ.textContent     = z.toFixed(2);
        labelRange.textContent = plotRange.toFixed(1);

        formulaSpan.textContent = polynomialString(a,b,c);
        if(window.MathJax) MathJax.typesetPromise([document.getElementById('formula-display')]);
    }

    /* -------------------- Main Update -------------------- */
    function updateAll(){
        // Sync state from controls
        z         = parseFloat(sliderZ.value);
        plotRange = parseFloat(sliderRange.value);

        showLabels = chkLabels.checked;
        showCircle = chkCircle.checked;
        showAxes   = chkAxes.checked;
        showGrid   = chkGrid.checked;

        calcPoints();
        updateResults();
        updateUI();
        if(window.requestRedraw) window.requestRedraw();
    }

    /* -------------------- Event Listeners -------------------- */
    sliderZ.addEventListener('input', updateAll);
    sliderRange.addEventListener('input', updateAll);
    chkLabels.addEventListener('change', updateAll);
    chkCircle.addEventListener('change', updateAll);
    chkAxes.addEventListener('change', updateAll);
    chkGrid.addEventListener('change', updateAll);

    btnGolden.addEventListener('click', () => {
        a = 1; b = -1; c = -1;
        updateAll();
    });

    btnNew.addEventListener('click', () => {
        const Achoices = [-3,-2,-1,1,2,3];
        a = Achoices[intRand(0,Achoices.length-1)];
        b = intRand(-5,5);
        c = intRand(-5,5);
        updateAll();
    });

    /* -------------------- p5.js Sketch -------------------- */
    new p5(p => {
        const parent = document.getElementById('canvas-container');

        p.setup = () => {
            p.createCanvas(parent.clientWidth, parent.clientHeight).parent(parent);
            p.noLoop();
        };

        p.windowResized = () => {
            p.resizeCanvas(parent.clientWidth, parent.clientHeight);
            p.redraw();
        };

        function toPx(u){ return u; } // placeholder, scaling via p5's scale()

        function drawGrid(range, step){
            p.stroke(200);
            p.strokeWeight(1/scaleVal);
            for(let x=-range; x<=range; x+=step){
                p.line(x,-range, x, range);
            }
            for(let y=-range; y<=range; y+=step){
                p.line(-range, y, range, y);
            }
        }

        let scaleVal = 1;  // pixels per unit (updated every frame)

        p.draw = () => {
            p.clear();

            // Prepare transform
            p.push();
            p.translate(p.width/2, p.height/2);
            scaleVal = p.width / (2*plotRange);
            p.scale(scaleVal, -scaleVal);        // y-axis up

            /* ----- Optional Layers ----- */
            if(showGrid) drawGrid(plotRange, 1);

            if(showAxes){
                p.stroke(0);
                p.strokeWeight(2/scaleVal);
                p.line(-plotRange,0, plotRange,0);
                p.line(0,-plotRange,0,plotRange);
            }

            if(showCircle){
                p.noFill();
                p.stroke(0);
                p.strokeWeight(2/scaleVal);
                p.ellipse(pts.C.x, pts.C.y, pts.d, pts.d);
            }

            /* ----- Paths ----- */
            p.noFill();
            p.strokeWeight(2/scaleVal);

            // Base path
            p.stroke(0);
            p.beginShape();
            p.vertex(pts.O.x,  pts.O.y);
            p.vertex(pts.L1.x, pts.L1.y);
            p.vertex(pts.L2.x, pts.L2.y);
            p.vertex(pts.L3.x, pts.L3.y);
            p.endShape();

            // Test path: O-L1'
            p.line(pts.O.x, pts.O.y, pts.L1p.x, pts.L1p.y);

            // Test path: L1'-L2' in magenta
            p.stroke(255,0,255);
            p.line(pts.L1p.x, pts.L1p.y, pts.L2p.x, pts.L2p.y);

            // Error segment: L2'-L3 in red
            p.stroke(255,0,0);
            p.line(pts.L2p.x, pts.L2p.y, pts.L3.x, pts.L3.y);

            /* ----- Points ----- */
            function drawPt(pt){
                p.fill(0);
                p.noStroke();
                p.ellipse(pt.x, pt.y, 6/scaleVal, 6/scaleVal);
            }
            [pts.O,pts.L1,pts.L2,pts.L3,pts.L1p,pts.L2p].forEach(drawPt);

            /* ----- Labels ----- */
            if(showLabels){
                const off = 0.15;
                p.fill(0);
                p.noStroke();
                p.textSize(14/scaleVal);
                p.text('O',   pts.O.x  -off, pts.O.y  -off);
                p.text('L1',  pts.L1.x +off, pts.L1.y -off);
                p.text('L2',  pts.L2.x +off, pts.L2.y +off);
                p.text('L3',  pts.L3.x +off, pts.L3.y +off);
                p.text("L1'", pts.L1p.x+off, pts.L1p.y+off);
                p.text("L2'", pts.L2p.x+off, pts.L2p.y+off);
            }

            p.pop();
        };

        // allow outer world to trigger redraw
        window.requestRedraw = () => p.redraw();
    });

    /* ----- initial computation ----- */
    updateAll();
});
</script>
</body>
</html>
