<body>

<!-- Main container for the application -->
<div id="app-container">

    <!-- Top-level controls for switching views -->
    <div id="header-controls">
        <div id="view-switcher">
            <button id="btn-plot" class="active">T₂(α)</button>
            <button id="btn-table">solution list</button>
            <button id="btn-turning-points">turning points</button>
        </div>
        <div id="info-button-container">
            <button id="btn-info">+</button>
        </div>
    </div>

    <!-- Slider control for the alpha parameter -->
    <div id="parameter-controls">
        <label for="slider-alpha">α</label>
        <input type="range" id="slider-alpha" min="0" max="0.07" step="0.0001" value="0.039">
        <span id="alpha-value-display">0.039</span>
    </div>

    <!-- Main display area for plot or table -->
    <div id="main-display">
        <!-- p5.js canvas will be created here for the plots -->
        <div id="plot-view"></div>

        <!-- Table view for the solution list -->
        <div id="table-view" style="display:none;">
            <table id="solutions-table">
                <thead>
                    <tr>
                        <th>root</th>
                        <th>X₁</th>
                        <th>X₂</th>
                        <th>T₁</th>
                        <th>T₂</th>
                    </tr>
                </thead>
                <tbody id="solutions-table-body">
                    <!-- Dynamically filled -->
                </tbody>
            </table>
        </div>
    </div>

</div>

<script>
/* -------------------------------------------------------------------------
   GLOBAL STATE
---------------------------------------------------------------------------*/
let currentView = 'plot_solutions';   // 'plot_solutions' | 'table' | 'plot_turning_points'
const sliderAlpha     = document.getElementById('slider-alpha');
const alphaDisplay    = document.getElementById('alpha-value-display');
const plotView        = document.getElementById('plot-view');
const tableView       = document.getElementById('table-view');
const paramControls   = document.getElementById('parameter-controls');
const tableBody       = document.getElementById('solutions-table-body');
const btnPlot         = document.getElementById('btn-plot');
const btnTable        = document.getElementById('btn-table');
const btnTurning      = document.getElementById('btn-turning-points');

/* -------------------------------------------------------------------------
   DATASET  (synthetic but multi–valued in α)
---------------------------------------------------------------------------*/
const solutionCurveData = [];                 // [{alpha,x1,x2,t1,t2},...]
const turningPointsData = [];                 // [{alpha,t2},...]

// Build a looping dataset using a parameter t
for(let t=0; t<=Math.PI*4; t+=0.05){
    const alpha = 0.035 + 0.035*Math.sin(t);  // 0 ↔ 0.07 range
    const t2    = 4 + 3*Math.cos(t);          // 1 ↔ 7 range
    const x1    = 10*alpha * (1+0.2*Math.sin(2*t));
    const x2    = 20*alpha * (1+0.1*Math.cos(1.5*t));
    const t1    = 2 + 1.5*Math.sin(t/2);
    solutionCurveData.push({alpha,x1,x2,t1,t2});
}

// Pre-select 6 turning points (every ~20% along dataset)
for(let i=0;i<6;i++){
    const index = Math.floor(i*(solutionCurveData.length-1)/5);
    const pt    = solutionCurveData[index];
    turningPointsData.push({alpha:pt.alpha, t2:pt.t2});
}

/* -------------------------------------------------------------------------
   p5.js SKETCH
---------------------------------------------------------------------------*/
let sketch = p => {
    const CANVAS_W = 600, CANVAS_H = 600;
    const MARGIN = 60;
    let loopDisabled = false;

    p.setup = function(){
        p.createCanvas(CANVAS_W, CANVAS_H).parent('plot-view');
        p.noLoop();  // draw on demand
    };

    p.draw = function(){
        p.background(255);

        drawAxesAndGrid();
        drawSolutionCurve();

        if(currentView === 'plot_solutions'){
            drawVerticalAlphaLine();
            drawSolutionDots();
        }else if(currentView === 'plot_turning_points'){
            drawTurningPointDots();
        }
    };

    /* ---------- helpers -------------------------------------------------*/
    function mapAlphaToX(a){
        return p.map(a, 0, 0.07, MARGIN, CANVAS_W-MARGIN);
    }
    function mapT2ToY(t2){
        // y axis reversed
        return p.map(t2, 0, 8, CANVAS_H-MARGIN, MARGIN);
    }

    function drawAxesAndGrid(){
        p.stroke(200);
        p.strokeWeight(1);

        // Vertical grid lines (α)
        for(let a=0;a<=0.07; a+=0.01){
            const x=mapAlphaToX(a);
            p.line(x,MARGIN,x,CANVAS_H-MARGIN);
        }
        // Horizontal grid lines (T2)
        for(let t=0;t<=8;t+=1){
            const y=mapT2ToY(t);
            p.line(MARGIN,y,CANVAS_W-MARGIN,y);
        }
        // Axes
        p.stroke(0);
        p.strokeWeight(2);
        // X axis
        p.line(MARGIN, CANVAS_H-MARGIN, CANVAS_W-MARGIN, CANVAS_H-MARGIN);
        // Y axis
        p.line(MARGIN, CANVAS_H-MARGIN, MARGIN, MARGIN);

        // labels
        p.noStroke();
        p.fill(0);
        p.textSize(14);
        p.text('α', CANVAS_W - MARGIN + 15, CANVAS_H - MARGIN + 4);
        p.text('T₂(α)', MARGIN - 10, MARGIN - 15);
    }

    function drawSolutionCurve(){
        p.noFill();
        p.stroke(255,0,0);
        p.strokeWeight(2);
        p.beginShape();
        solutionCurveData.forEach(pt=>{
            p.vertex(mapAlphaToX(pt.alpha), mapT2ToY(pt.t2));
        });
        p.endShape();
    }

    function drawVerticalAlphaLine(){
        const aVal = parseFloat(sliderAlpha.value);
        const x = mapAlphaToX(aVal);
        p.stroke(0,180,0);
        p.strokeWeight(2);
        p.drawingContext.setLineDash([6,6]);
        p.line(x, MARGIN, x, CANVAS_H-MARGIN);
        p.drawingContext.setLineDash([]);
    }

    function drawSolutionDots(){
        const intersections = calculateIntersections(parseFloat(sliderAlpha.value));
        p.noStroke();
        p.fill(0,0,255);
        intersections.forEach(pt=>{
            const x = mapAlphaToX(pt.alpha);
            const y = mapT2ToY(pt.t2);
            p.circle(x,y,10);
        });
    }

    function drawTurningPointDots(){
        p.noStroke();
        p.fill(0,200,0);
        turningPointsData.forEach(pt=>{
            const x = mapAlphaToX(pt.alpha);
            const y = mapT2ToY(pt.t2);
            p.circle(x,y,10);
        });
    }

}; // end sketch

// instantiate p5
new p5(sketch);

/* -------------------------------------------------------------------------
   GEOMETRY – intersection between vertical α and solution curve
---------------------------------------------------------------------------*/
function calculateIntersections(alphaTarget){
    const hits = [];
    for(let i=0;i<solutionCurveData.length-1;i++){
        const p1=solutionCurveData[i], p2=solutionCurveData[i+1];
        const diff1 = p1.alpha - alphaTarget;
        const diff2 = p2.alpha - alphaTarget;
        if(diff1===0){    // exact data point
            hits.push({...p1});
            continue;
        }
        if(diff1*diff2<0){          // opposite signs = crossing
            const ratio = diff1/(diff1-diff2);   // linear interpolation weight toward p2
            const interp = {
                alpha: alphaTarget,
                x1  : p1.x1 + ratio*(p2.x1 - p1.x1),
                x2  : p1.x2 + ratio*(p2.x2 - p1.x2),
                t1  : p1.t1 + ratio*(p2.t1 - p1.t1),
                t2  : p1.t2 + ratio*(p2.t2 - p1.t2)
            };
            hits.push(interp);
        }
    }
    // sort by descending T2 for neat order
    hits.sort( (a,b)=> b.t2 - a.t2 );
    return hits;
}

/* -------------------------------------------------------------------------
   TABLE UPDATE
---------------------------------------------------------------------------*/
function updateTable(){
    tableBody.innerHTML='';
    const alphaVal = parseFloat(sliderAlpha.value);
    const sols = calculateIntersections(alphaVal);
    sols.forEach((sol,idx)=>{
        const tr=document.createElement('tr');
        function td(content){
            const cell=document.createElement('td');
            cell.textContent=content;
            return cell;
        }
        tr.appendChild( td(idx+1) );
        tr.appendChild( td(sol.x1.toFixed(4)) );
        tr.appendChild( td(sol.x2.toFixed(4)) );
        tr.appendChild( td(sol.t1.toFixed(4)) );
        tr.appendChild( td(sol.t2.toFixed(4)) );
        tableBody.appendChild(tr);
    });
}

/* -------------------------------------------------------------------------
   VIEW SWITCHING
---------------------------------------------------------------------------*/
function setActiveButton(btn){
    [btnPlot,btnTable,btnTurning].forEach(b=>b.classList.remove('active'));
    btn.classList.add('active');
}

btnPlot.addEventListener('click',()=>{
    currentView='plot_solutions';
    setActiveButton(btnPlot);
    plotView.style.display='block';
    tableView.style.display='none';
    paramControls.style.display='flex';
    updateTable();          // keep table up-to-date even if hidden
    redrawPlot();
});

btnTable.addEventListener('click',()=>{
    currentView='table';
    setActiveButton(btnTable);
    plotView.style.display='none';
    tableView.style.display='block';
    paramControls.style.display='flex';
    updateTable();
});

btnTurning.addEventListener('click',()=>{
    currentView='plot_turning_points';
    setActiveButton(btnTurning);
    plotView.style.display='block';
    tableView.style.display='none';
    paramControls.style.display='none';
    redrawPlot();
});

/* -------------------------------------------------------------------------
   SLIDER   α
---------------------------------------------------------------------------*/
sliderAlpha.addEventListener('input',()=>{
    alphaDisplay.textContent=Number(sliderAlpha.value).toFixed(3);
    if(currentView==='plot_solutions'){
        redrawPlot();
    }else if(currentView==='table'){
        updateTable();
    }
});

/* -------------------------------------------------------------------------
   UTILITY
---------------------------------------------------------------------------*/
function redrawPlot(){
    // p5 automatically stores references of sketches in window._sketches
    if(window._sketches && window._sketches[0]){
        window._sketches[0].redraw();
    }
}

/* -------------------------------------------------------------------------
   INITIAL DRAW/TABLE
---------------------------------------------------------------------------*/
updateTable();
redrawPlot();

</script>
</body>
</html>
