<script>
//--------------------- STATE VARIABLES ---------------------//
let spiralType   = 'double';  // 'single' or 'double'
let itemType     = 'point';   // 'none','point','fox','filled-fox'
let spiralGrowth = 0.15;

let z0_loc      = {x:0,y:0};           // for single spiral
let z_plus_loc  = {x:-100,y:0};        // first pole
let z_minus_loc = {x:100,y:0};         // second pole

const locatorRadius = 10;

let dragging = {z0:false, zplus:false, zminus:false};

//--------------------- UTILITY ------------------------------//
function addActive(btn, group){
    group.forEach(b=>b.classList.remove('active'));
    btn.classList.add('active');
}

// Screen <-> World conversion (considering translate + scale)
function screenToWorld(sx,sy){
    let wx = sx - width/2;
    let wy = -(sy - height/2);
    return {x:wx, y:wy};
}

//--------------------- BUTTON HANDLERS ----------------------//
const btnTypeSingle = document.getElementById('btn-type-single');
const btnTypeDouble = document.getElementById('btn-type-double');

btnTypeSingle.addEventListener('click', ()=>{
    spiralType = 'single';
    addActive(btnTypeSingle,[btnTypeSingle,btnTypeDouble]);
});
btnTypeDouble.addEventListener('click', ()=>{
    spiralType = 'double';
    addActive(btnTypeDouble,[btnTypeSingle,btnTypeDouble]);
});

// Item buttons
const btnItemNone = document.getElementById('btn-item-none');
const btnItemPoint = document.getElementById('btn-item-point');
const btnItemFox = document.getElementById('btn-item-fox');
const btnItemFilledFox = document.getElementById('btn-item-filled-fox');
btnItemNone.addEventListener('click', ()=> { itemType='none'; addActive(btnItemNone,[btnItemNone,btnItemPoint,btnItemFox,btnItemFilledFox]); });
btnItemPoint.addEventListener('click',()=> { itemType='point'; addActive(btnItemPoint,[btnItemNone,btnItemPoint,btnItemFox,btnItemFilledFox]); });
btnItemFox.addEventListener('click',()=> { itemType='fox'; addActive(btnItemFox,[btnItemNone,btnItemPoint,btnItemFox,btnItemFilledFox]); });
btnItemFilledFox.addEventListener('click',()=> { itemType='filled-fox'; addActive(btnItemFilledFox,[btnItemNone,btnItemPoint,btnItemFox,btnItemFilledFox]); });

// Growth slider
const sliderGrowth = document.getElementById('slider-growth');
const displayGrowth = document.getElementById('display-growth');
sliderGrowth.addEventListener('input',()=>{
    spiralGrowth = parseFloat(sliderGrowth.value);
    displayGrowth.textContent = spiralGrowth.toFixed(4);
});

// Reset button
document.getElementById('btn-reset-growth').addEventListener('click',()=>{
    spiralGrowth = 0.25;
    sliderGrowth.value = spiralGrowth;
    displayGrowth.textContent = spiralGrowth.toFixed(4);
});

//--------------------- p5.js SKETCH -------------------------//
let foxVertices;

function setup(){
    const canvasParent = document.getElementById('canvas-container');
    const c = createCanvas(canvasParent.clientWidth, canvasParent.clientHeight);
    c.parent('canvas-container');
    foxVertices = [
        {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5}, {x:5, y:-5},
        {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6}, {x:15, y:0}, {x:12, y:6},
        {x:10, y:6}, {x:8, y:2}, {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
    ];
    angleMode(RADIANS);
}

function windowResized(){
    const canvasParent = document.getElementById('canvas-container');
    resizeCanvas(canvasParent.clientWidth, canvasParent.clientHeight);
}

function draw(){
    background(255);
    translate(width/2,height/2);
    scale(1,-1);      // y axis upward

    stroke('#4A6DA8');
    strokeWeight(2);
    noFill();

    (spiralType==='single') ? drawSingleSpiral() : drawDoubleSpiral();

    drawLocators();
}

//---------------- SPIRAL DRAWING FUNCTIONS ------------------//
function drawSingleSpiral(){
    const T = 8*PI;
    const step = 0.05;
    beginShape();
    for(let t=-T;t<=T;t+=step){
        const r = Math.exp(spiralGrowth*t);
        const x = z0_loc.x + r*Math.cos(t);
        const y = z0_loc.y + r*Math.sin(t);
        vertex(x,y);
    }
    endShape();

    if(itemType!=='none'){
        const decoStep = 1.5;   // sparser for decorations
        for(let t=-T;t<=T;t+=decoStep){
            const r = Math.exp(spiralGrowth*t);
            const x = z0_loc.x + r*Math.cos(t);
            const y = z0_loc.y + r*Math.sin(t);
            const pt = {x,y};

            // tangent vector for orientation
            const r2 = Math.exp(spiralGrowth*(t+0.001));
            const x2 = z0_loc.x + r2*Math.cos(t+0.001);
            const y2 = z0_loc.y + r2*Math.sin(t+0.001);
            const angle = atan2(y2-y,x2-x);

            drawDecoration(pt, angle);
        }
    }
}

function drawDoubleSpiral(){
    const p = z_plus_loc;
    const q = z_minus_loc;
    const T = 8*PI;
    const step = 0.05;
    const eps = 0.01;

    // helper to compute z(t)
    function zOf(t){
        const rk = Math.exp(spiralGrowth*t);
        const w_re = rk * Math.cos(t);
        const w_im = rk * Math.sin(t);
        const w_minus_1_re = w_re - 1;
        const w_minus_1_im = w_im;

        // numerator: p*w - q
        const num_re = p.x*w_re - p.y*w_im - q.x;
        const num_im = p.x*w_im + p.y*w_re - q.y;

        // denominator: w-1
        const den_re = w_minus_1_re;
        const den_im = w_minus_1_im;
        const denom2 = den_re*den_re + den_im*den_im;

        return {
            x: (num_re*den_re + num_im*den_im)/denom2,
            y: (num_im*den_re - num_re*den_im)/denom2
        };
    }

    // two branches
    function drawBranch(tStart,tEnd){
        beginShape();
        for(let t=tStart; (tStart<tEnd)? t<=tEnd : t>=tEnd; t+=(tStart<tEnd?step:-step)){
            const z = zOf(t);
            vertex(z.x,z.y);
        }
        endShape();
    }

    drawBranch(-T,-eps);
    drawBranch(eps,T);

    // decorations
    if(itemType!=='none'){
        const decoStep = 1.5;
        for(let t=-T; t<=-eps; t+=decoStep){
            placeDecoAt(t);
        }
        for(let t=eps; t<=T; t+=decoStep){
            placeDecoAt(t);
        }
    }

    function placeDecoAt(t){
        const z  = zOf(t);
        const z2 = zOf(t+0.001);
        const angle = atan2(z2.y - z.y, z2.x - z.x);
        drawDecoration(z, angle);
    }

    // draw label z0Loc near midpoint
    push();
    scale(1,-1); // flip back for text
    noStroke();
    fill(0);
    const midX = (p.x + q.x)/2;
    const midY = -(p.y + q.y)/2; // invert y for screen text
    text('z0Loc', midX, midY);
    pop();
}

//---------------- DECORATIONS -------------------------------//
function drawDecoration(pt, angle){
    if(itemType==='point'){
        push();
        noStroke();
        fill('#CB3335');
        circle(pt.x, pt.y, 5);
        pop();
    }else if(itemType==='fox' || itemType==='filled-fox'){
        push();
        translate(pt.x, pt.y);
        rotate(angle);
        const s = 0.8;
        scale(s, s);
        if(itemType==='fox'){
            noFill();
            stroke('#D6292D');
            strokeWeight(1);
        }else{
            noStroke();
            fill('#D6292D');
        }
        beginShape();
        foxVertices.forEach(v=> vertex(v.x, v.y));
        endShape(CLOSE);
        pop();
    }
}

//---------------- LOCATOR RENDERING -------------------------//
function drawLocators(){
    stroke(0);
    strokeWeight(1);

    if(spiralType==='single'){
        drawLocator(z0_loc, 'yellow');
    }else{
        drawLocator(z_plus_loc, 'yellow');
        drawLocator(z_minus_loc, 'green');
    }
}

function drawLocator(pos, colorName){
    push();
    fill(colorName==='yellow' ? 'rgba(255,255,0,0.8)' : 'rgba(0,255,0,0.8)');
    circle(pos.x, pos.y, locatorRadius*2);
    line(pos.x - locatorRadius, pos.y, pos.x + locatorRadius, pos.y);
    line(pos.x, pos.y - locatorRadius, pos.x, pos.y + locatorRadius);
    pop();
}

//---------------- MOUSE INTERACTIONS -----------------------//
function mousePressed(){
    const m = screenToWorld(mouseX, mouseY);
    if(spiralType==='single'){
        if(dist(m.x,m.y,z0_loc.x,z0_loc.y)<locatorRadius){
            dragging.z0 = true;
        }
    }else{
        if(dist(m.x,m.y,z_plus_loc.x,z_plus_loc.y)<locatorRadius){
            dragging.zplus = true;
        }else if(dist(m.x,m.y,z_minus_loc.x,z_minus_loc.y)<locatorRadius){
            dragging.zminus = true;
        }
    }
}

function mouseDragged(){
    const m = screenToWorld(mouseX, mouseY);
    if(dragging.z0){
        z0_loc.x = m.x; z0_loc.y = m.y;
    }else if(dragging.zplus){
        z_plus_loc.x = m.x; z_plus_loc.y = m.y;
    }else if(dragging.zminus){
        z_minus_loc.x = m.x; z_minus_loc.y = m.y;
    }
}

function mouseReleased(){
    dragging = {z0:false, zplus:false, zminus:false};
}
</script>
</body>
</html>
