<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Dijkstra Demo</title>
    <!-- p5.js CDN -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
    <style>
        /* --- Layout --- */
        body{
            margin:0;
            font-family:Arial,Helvetica,sans-serif;
            display:flex;
            height:100vh;
        }
        #control-panel{
            width:260px;
            padding:15px;
            box-sizing:border-box;
            border-right:2px solid #ddd;
            display:flex;
            flex-direction:column;
            gap:12px;
            background:#fafafa;
        }
        #viz-container{
            flex:1;
            display:flex;
            flex-direction:column;
            align-items:center;
            overflow:auto;
        }
        /* --- Buttons --- */
        button{
            cursor:pointer;
            padding:6px 10px;
            font-size:14px;
        }
        button:disabled{
            background:#ddd;
            cursor:not-allowed;
        }
        /* --- Vertex button grid --- */
        #vertex-buttons{
            display:grid;
            grid-template-columns:repeat(4,55px);
            grid-gap:8px;
            margin-top:6px;
        }
        .vertex-btn.active{
            background:#333;
            color:#fff;
        }
        h1{
            font-size:20px;
            margin:10px 0;
        }
    </style>
</head>
<body>
    <!-- ===== CONTROL PANEL ===== -->
    <div id="control-panel">
        <button id="start-over-btn">start over</button>

        <label>initial vertex</label>
        <div id="vertex-buttons">
            <!-- bottom row -->
            <button id="btn-m" class="vertex-btn">m</button>
            <button id="btn-n" class="vertex-btn">n</button>
            <button id="btn-o" class="vertex-btn">o</button>
            <button id="btn-p" class="vertex-btn">p</button>
            <!-- 3rd row -->
            <button id="btn-i" class="vertex-btn">i</button>
            <button id="btn-j" class="vertex-btn">j</button>
            <button id="btn-k" class="vertex-btn">k</button>
            <button id="btn-l" class="vertex-btn">l</button>
            <!-- 2nd row -->
            <button id="btn-e" class="vertex-btn">e</button>
            <button id="btn-f" class="vertex-btn">f</button>
            <button id="btn-g" class="vertex-btn">g</button>
            <button id="btn-h" class="vertex-btn">h</button>
            <!-- top row -->
            <button id="btn-a" class="vertex-btn">a</button>
            <button id="btn-b" class="vertex-btn">b</button>
            <button id="btn-c" class="vertex-btn">c</button>
            <button id="btn-d" class="vertex-btn">d</button>
        </div>

        <button id="fixed-example-btn">fixed example</button>
        <button id="random-example-btn">random example</button>

        <label>implement algorithm</label>
        <button id="next-step-btn">next step</button>
    </div>

    <!-- ===== VISUALIZATION AREA ===== -->
    <div id="viz-container">
        <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
        <div id="canvas-container"></div>
    </div>

<script>
/* ============================================================
   ========  DATA STRUCTURES & GLOBAL STATE  ==================
   ============================================================ */
const vertices = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p'];

/* Edges in the 4x4 grid */
const edges = [
  ['m','n'],['n','o'],['o','p'],
  ['i','j'],['j','k'],['k','l'],
  ['e','f'],['f','g'],['g','h'],
  ['a','b'],['b','c'],['c','d'],
  ['m','i'],['i','e'],['e','a'],
  ['n','j'],['j','f'],['f','b'],
  ['o','k'],['k','g'],['g','c'],
  ['p','l'],['l','h'],['h','d']
];

/* helper to create key for edge weights, order independent */
const edgeKey = (u,v)=> ([u,v].sort().join('-'));

/* fixed example edge weights */
const fixedWeights = {};
[
  ['m','n',8], ['n','o',7], ['o','p',7],
  ['i','j',9], ['j','k',7], ['k','l',1],
  ['e','f',1], ['f','g',7], ['g','h',6],
  ['a','b',10],['b','c',4], ['c','d',8],
  ['m','i',6], ['i','e',9], ['e','a',9],
  ['n','j',7], ['j','f',6], ['f','b',5],
  ['o','k',1], ['k','g',8], ['g','c',8],
  ['p','l',2], ['l','h',1], ['h','d',7]
].forEach(([u,v,w])=> fixedWeights[edgeKey(u,v)]=w);

/* Current mutable state */
let currentWeights = {};      // edgeKey -> weight
let startVertex = 'm';        // currently selected start vertex
let distances = {};           // vertex -> number (Infinity init)
let visited   = new Set();    // visited vertices
let predecessor = {};         // vertex -> parent in SPT

/* adjacency list (same for any weight) */
const adjacency = {};
vertices.forEach(v=> adjacency[v]=[]);
edges.forEach(([u,v])=>{
    adjacency[u].push(v);
    adjacency[v].push(u);
});

/* ============================================================
   ===========  GRAPHIC POSITIONS FOR P5 RENDERING  ===========
   ============================================================ */
const vertexPos = {};   // vertex -> {x,y}
(function computePositions(){
    /* canvas 600x600, 4x4 grid */
    const margin = 80;
    const spacing = (600 - 2*margin) /3; // 3 intervals between 4 points
    const letterToCoord = {
        'a':[0,0],'b':[1,0],'c':[2,0],'d':[3,0],
        'e':[0,1],'f':[1,1],'g':[2,1],'h':[3,1],
        'i':[0,2],'j':[1,2],'k':[2,2],'l':[3,2],
        'm':[0,3],'n':[1,3],'o':[2,3],'p':[3,3]
    };
    for(const v of vertices){
        const [col,row] = letterToCoord[v];
        vertexPos[v] = {
            x: margin + col*spacing,
            y: margin + row*spacing
        };
    }
})();

/* ============================================================
   ===================  INITIALIZATION  ========================
   ============================================================ */
function resetAlgorithm(){
    distances = {};
    predecessor = {};
    visited = new Set();
    vertices.forEach(v=> distances[v]=Infinity);
    distances[startVertex]=0;
}

function loadFixedExample(){
    currentWeights = {...fixedWeights};
}

function loadRandomExample(){
    currentWeights = {};
    edges.forEach(([u,v])=>{
        currentWeights[edgeKey(u,v)] = Math.floor(Math.random()*10)+1; // 1..10
    });
}

function setStartVertex(v){
    startVertex = v;
    document.getElementById('title-display').textContent = 
        `Dijkstra's algorithm starting at vertex ${v}`;
    /* update active button style */
    document.querySelectorAll('.vertex-btn').forEach(btn=> btn.classList.remove('active'));
    document.getElementById(`btn-${v}`).classList.add('active');
    resetAlgorithm();
    document.getElementById('next-step-btn').disabled=false;
    redraw(); // p5
}

function startOver(){
    loadFixedExample();
    setStartVertex('m'); // includes resetAlgorithm + redraw
}

/* ============================================================
   =================  DIJKSTRA STEP FUNCTION  =================
   ============================================================ */
function nextStep(){
    if(visited.size === vertices.length){
        document.getElementById('next-step-btn').disabled=true;
        return;
    }
    /* find unvisited vertex with smallest tentative distance */
    let u=null, minDist=Infinity;
    vertices.forEach(v=>{
        if(!visited.has(v) && distances[v] < minDist){
            minDist = distances[v];
            u = v;
        }
    });
    if(u===null || minDist===Infinity){
        // remaining vertices unreachable
        document.getElementById('next-step-btn').disabled=true;
        return;
    }
    visited.add(u);

    // relax neighbors
    adjacency[u].forEach(v=>{
        if(visited.has(v)) return;
        const w = currentWeights[edgeKey(u,v)];
        const newDist = distances[u]+w;
        if(newDist < distances[v]){
            distances[v]=newDist;
            predecessor[v]=u;
        }
    });
    if(visited.size === vertices.length){
        document.getElementById('next-step-btn').disabled=true;
    }
    redraw();
}

/* ============================================================
   =================  DOM EVENT LISTENERS  ====================
   ============================================================ */
/* vertex buttons */
vertices.forEach(v=>{
    document.getElementById(`btn-${v}`).addEventListener('click',()=> setStartVertex(v));
});
/* start over */
document.getElementById('start-over-btn').addEventListener('click', startOver);
/* fixed example */
document.getElementById('fixed-example-btn').addEventListener('click',()=>{
    loadFixedExample();
    resetAlgorithm();
    redraw();
});
/* random example */
document.getElementById('random-example-btn').addEventListener('click',()=>{
    loadRandomExample();
    resetAlgorithm();
    redraw();
});
/* next step */
document.getElementById('next-step-btn').addEventListener('click', nextStep);

/* ============================================================
   =====================   P5  SKETCH   =======================
   ============================================================ */
let margin=80, spacing;

function setup(){
    const c = createCanvas(600,600);
    c.parent('canvas-container');
    noLoop();
    spacing = (600 - 2*margin)/3;
    /* initial load */
    loadFixedExample();
    resetAlgorithm();
    /* highlight default button */
    document.getElementById('btn-m').classList.add('active');
    redraw();
}

function draw(){
    background(255);
    drawEdges();
    drawVertices();
}

function drawEdges(){
    textAlign(CENTER,CENTER);
    textSize(14);
    edges.forEach(([u,v])=>{
        const p1 = vertexPos[u];
        const p2 = vertexPos[v];
        const inTree = (predecessor[u]===v)||(predecessor[v]===u);
        stroke(inTree? 'blue' : 'black');
        strokeWeight(inTree? 4:1);
        line(p1.x,p1.y, p2.x,p2.y);

        // weight
        noStroke();
        fill(255);
        // background rectangle for readability
        const midX = (p1.x+p2.x)/2;
        const midY = (p1.y+p2.y)/2;
        const label = currentWeights[edgeKey(u,v)];
        const padding = 4;
        const w = textWidth(label)+padding*2;
        const h = 16;
        rectMode(CENTER);
        rect(midX, midY, w, h);
        fill(0);
        text(label, midX, midY+1);
    });
}

function drawVertices(){
    textSize(16);
    vertices.forEach(v=>{
        const p = vertexPos[v];
        const isVisited = visited.has(v);
        stroke(0);
        fill(isVisited? 'red':'black');
        circle(p.x, p.y, 22);

        // vertex letter
        fill(255);
        noStroke();
        textAlign(CENTER,CENTER);
        text(v, p.x, p.y);

        // distance label above
        const distLabel = (distances[v]===Infinity)? '∞' : distances[v];
        fill(isVisited? 'red':'black');
        textAlign(CENTER, BOTTOM);
        text(`(${distLabel})`, p.x, p.y-14);
    });
}
</script>
</body>
</html>
