<script>
document.addEventListener('DOMContentLoaded', () => {

    /*******************
     * INITIAL SETTINGS *
     *******************/
    const defaultSphereDiameter = 1.5;
    let sphereDiameter   = defaultSphereDiameter;
    let coneHeight       = 1.5;
    let coneBaseRadius   = 0.4;
    let currentPolyhedron = 'octahedron';

    /***************
     * THREE.JS SETUP
     ***************/
    const container = document.getElementById('canvas-container');
    const scene     = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    const camera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 100);
    camera.position.set(0, 0, 8);

    const renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    container.appendChild(renderer.domElement);
    onWindowResize(); // initial sizing

    // Orbit controls
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;

    // Lights
    scene.add(new THREE.AmbientLight(0x404040, 1.2));
    const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.9);
    dirLight1.position.set( 1, 1, 1 );
    scene.add(dirLight1);
    const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.6);
    dirLight2.position.set( -1, -1, -1 );
    scene.add(dirLight2);

    // Central sphere
    const sphereMaterial = new THREE.MeshPhongMaterial({color:0xdddddd, shininess:50});
    const sphereGeometry = new THREE.SphereGeometry(defaultSphereDiameter/2, 64, 64);
    const sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial);
    scene.add(sphereMesh);

    // Cone group and data store
    let conesGroup   = new THREE.Group();
    let conesData    = []; // {mesh, direction}
    scene.add(conesGroup);

    createCones(); // initial population

    /******************
     * EVENT HANDLERS *
     ******************/
    // Sphere diameter slider
    document.getElementById('slider-sphere-diameter').addEventListener('input', (e) => {
        sphereDiameter = parseFloat(e.target.value);
        const scaleFactor = sphereDiameter / defaultSphereDiameter;
        sphereMesh.scale.set(scaleFactor, scaleFactor, scaleFactor);
        updateConePositions(); // depends on sphere size
    });

    // Cone height slider
    document.getElementById('slider-cone-height').addEventListener('input', (e) => {
        coneHeight = parseFloat(e.target.value);
        rebuildConeGeometries(true); // true => height changed (needs position update)
    });

    // Cone base slider
    document.getElementById('slider-cone-base').addEventListener('input', (e) => {
        coneBaseRadius = parseFloat(e.target.value);
        rebuildConeGeometries(false); // base changed only
    });

    // Polyhedron buttons
    const polyBtns = document.querySelectorAll('#polyhedron-buttons button');
    polyBtns.forEach(btn => {
        btn.addEventListener('click', () => {
            if (btn.classList.contains('active')) return; // already selected
            polyBtns.forEach(b=>b.classList.remove('active'));
            btn.classList.add('active');
            currentPolyhedron = btn.id.replace('btn-',''); // cube, dodecahedron, etc.
            createCones();
        });
    });

    // Resize
    window.addEventListener('resize', onWindowResize);

    /********************
     * CORE FUNCTIONS   *
     ********************/
    // Create or recreate cones based on current settings
    function createCones(){
        // remove old group
        scene.remove(conesGroup);
        conesGroup.traverse(obj=>{
            if(obj.isMesh) obj.geometry.dispose();
        });
        conesGroup = new THREE.Group();
        conesData = [];

        const vertices = getPolyhedronVertices(currentPolyhedron);
        const coneMat  = new THREE.MeshPhongMaterial({color:0xffa500, shininess:30});
        const sphereRadius = sphereDiameter / 2;

        vertices.forEach(v=>{
            const direction = v.clone().normalize();
            const pos = direction.clone().multiplyScalar(sphereRadius + coneHeight/2);

            const geometry = new THREE.ConeGeometry(coneBaseRadius, coneHeight, 32);
            const cone = new THREE.Mesh(geometry, coneMat);

            cone.position.copy(pos);
            // Orient the cone so its Y-axis aligns with the direction vector
            cone.quaternion.setFromUnitVectors(new THREE.Vector3(0,1,0), direction);

            conesGroup.add(cone);
            conesData.push({mesh:cone, direction:direction});
        });

        scene.add(conesGroup);
    }

    // Rebuild cone geometries when height/base changes
    // if heightChanged == true, we need to update positions as well
    function rebuildConeGeometries(heightChanged){
        const sphereRadius = sphereDiameter / 2;
        conesData.forEach(item=>{
            const mesh = item.mesh;
            mesh.geometry.dispose();
            mesh.geometry = new THREE.ConeGeometry(coneBaseRadius, coneHeight, 32);
            if(heightChanged){
                const pos = item.direction.clone().multiplyScalar(sphereRadius + coneHeight/2);
                mesh.position.copy(pos);
            }
        });
    }

    // Only positions need update (sphere size changed)
    function updateConePositions(){
        const sphereRadius = sphereDiameter / 2;
        conesData.forEach(item=>{
            const pos = item.direction.clone().multiplyScalar(sphereRadius + coneHeight/2);
            item.mesh.position.copy(pos);
        });
    }

    // Return UNIQUE vertices of a platonic solid with radius 1
    function getPolyhedronVertices(name){
        let geom;
        switch(name){
            case 'cube':
                geom = new THREE.BoxGeometry(1,1,1); break;
            case 'dodecahedron':
                geom = new THREE.DodecahedronGeometry(1); break;
            case 'icosahedron':
                geom = new THREE.IcosahedronGeometry(1); break;
            case 'octahedron':
                geom = new THREE.OctahedronGeometry(1); break;
            case 'tetrahedron':
            default:
                geom = new THREE.TetrahedronGeometry(1); break;
        }

        const posAttr = geom.getAttribute('position');
        const vertices = [];
        const hashSet  = new Set();

        for(let i=0; i < posAttr.count; i++){
            const v = new THREE.Vector3().fromBufferAttribute(posAttr, i);
            // hash by rounded coordinates to eliminate duplicates
            const hash = `${v.x.toFixed(5)},${v.y.toFixed(5)},${v.z.toFixed(5)}`;
            if(!hashSet.has(hash)){
                hashSet.add(hash);
                vertices.push(v);
            }
        }
        geom.dispose();
        return vertices;
    }

    /****************
     * RENDER LOOP  *
     ****************/
    function animate(){
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
    }
    animate();

    // Adjust renderer/camera on window resize
    function onWindowResize(){
        const w = container.clientWidth;
        const h = container.clientHeight;
        camera.aspect = w / h;
        camera.updateProjectionMatrix();
        renderer.setSize(w, h);
    }
});
</script>
</body>
</html>
