      </div>
    </div>

    <div id="display-controls" style="margin-top:20px;">
      <input type="checkbox" id="checkbox-snapshots" checked>
      <label for="checkbox-snapshots">show sun snapshots</label><br>
      <input type="checkbox" id="checkbox-scales" checked>
      <label for="checkbox-scales">show scales</label>
    </div>
  </div>

  <!-- RIGHT COLUMN ------------------------------------------------------>
  <div id="visualization-area">
    <!-- p5.js will replace this canvas; keeping for sizing -->
    <canvas id="analemma-canvas"></canvas>
  </div>
</div>

<script>
/* ---------------------------------------------------------------------
   Pre-defined planetary data
------------------------------------------------------------------------ */
const planetData = {
  "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
  "Mars":  { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
};

/* ---------------------------------------------------------------------
   DOM ELEMENT REFERENCES
------------------------------------------------------------------------ */
const selectPlanet     = document.getElementById('select-planet');
const checkboxManual   = document.getElementById('checkbox-manual');
const sliderTilt       = document.getElementById('slider-tilt');
const sliderEquinox    = document.getElementById('slider-equinox');
const sliderEcc        = document.getElementById('slider-eccentricity');
const checkboxShots    = document.getElementById('checkbox-snapshots');
const checkboxScales   = document.getElementById('checkbox-scales');

/* ---------------------------------------------------------------------
   UTILITY  –  enable / disable sliders together
------------------------------------------------------------------------ */
function setSlidersEnabled(enabled){
  [sliderTilt, sliderEquinox, sliderEcc].forEach(el=>{
    el.disabled = !enabled;
  });
}

/* ---------------------------------------------------------------------
   EVENT HANDLING  –  Control interactions
------------------------------------------------------------------------ */

// Planet dropdown
selectPlanet.addEventListener('change', () => {
  if(selectPlanet.value === 'manual'){
    checkboxManual.checked = true;
    setSlidersEnabled(true);
  }else{
    const data = planetData[selectPlanet.value];
    checkboxManual.checked = false;
    setSlidersEnabled(false);

    sliderTilt.value    = data.tilt;
    sliderEcc.value     = data.eccentricity;
    sliderEquinox.value = data.equinox;
  }
  redrawSketch();
});

// Manual checkbox
checkboxManual.addEventListener('change', () => {
  setSlidersEnabled(checkboxManual.checked);
  if(checkboxManual.checked){
    selectPlanet.value = 'manual';
  }
  redrawSketch();
});

// Sliders
[sliderTilt, sliderEquinox, sliderEcc].forEach(slider=>{
  slider.addEventListener('input', redrawSketch);
});

// Display checkboxes
[checkboxShots, checkboxScales].forEach(cb=>{
  cb.addEventListener('change', redrawSketch);
});

/* ---------------------------------------------------------------------
   p5.js sketch  (instance mode to avoid globals)
------------------------------------------------------------------------ */
new p5((p) => {

  let C = 200; // scaling factor (pixels per radian)
  let points = []; // cached analemma points

  /* ---------------------------  setup  ------------------------------ */
  p.setup = function(){
    const area = document.getElementById('visualization-area');
    const canvas = p.createCanvas(area.clientWidth, area.clientHeight);
    canvas.parent(area);
    p.noLoop(); // we'll manually redraw
    calculateAnalemma();
    p.redraw();
  };

  /* ---------------------------  window resized  --------------------- */
  p.windowResized = function(){
    const area = document.getElementById('visualization-area');
    p.resizeCanvas(area.clientWidth, area.clientHeight);
    p.redraw();
  };

  /* ---------------------------  main draw  -------------------------- */
  p.draw = function(){
    p.background(255);

    // move origin to center
    p.translate(p.width/2, p.height/2 + 40); // slight downward shift so orbit fits on top
    p.strokeWeight(1);

    // Draw scales (axes)
    if(checkboxScales.checked){
      drawAxes();
    }

    // Orbit ellipse on top (before translate Y shift)
    p.push();
    p.translate(0,-(p.height/2)-20); // place near top
    drawOrbitEllipse();
    p.pop();

    // Draw analemma curve
    p.noFill();
    p.stroke('orange');
    p.strokeWeight(2);
    p.beginShape();
    points.forEach(pt => p.vertex(pt.x, -pt.y)); // invert y for screen coords
    p.endShape();

    // Sun snapshots
    if(checkboxShots.checked){
      p.noStroke();
      p.fill('orange');
      for(let i=0;i<points.length;i+=20){
        p.circle(points[i].x, -points[i].y, 8);
      }
    }
  };

  /* ---------------------------  helpers  ---------------------------- */
  function calculateAnalemma(){
    points = [];
    const tiltDeg   = parseFloat(sliderTilt.value);
    const ecc       = parseFloat(sliderEcc.value);
    const equinoxDeg= parseFloat(sliderEquinox.value);

    const tilt        = p.radians(tiltDeg);
    const equinoxAngle= p.radians(equinoxDeg);

    const days = 365;
    for(let d=0; d<days; d++){
      const M = 2*Math.PI*d/days;      // mean anomaly

      // Solve Kepler's equation for E
      let E = M; // initial guess
      for(let k=0;k<5;k++){
        E = E - (E - ecc*Math.sin(E) - M)/(1 - ecc*Math.cos(E));
      }

      // True anomaly
      const v = 2*Math.atan2(Math.sqrt(1+ecc)*Math.sin(E/2), Math.sqrt(1-ecc)*Math.cos(E/2));

      // Ecliptic longitude
      const lambda = v + equinoxAngle;

      // Declination
      const y = Math.asin(Math.sin(tilt)*Math.sin(lambda));

      // Right ascension
      const alpha = Math.atan2(Math.cos(tilt)*Math.sin(lambda), Math.cos(lambda));

      // Equation of Time
      let EoT = M - alpha;
      // Wrap into [-π, π]
      EoT = ((EoT + Math.PI) % (2*Math.PI)) - Math.PI;

      // Store pixel coordinates
      points.push({
        x: EoT * C,
        y: y * C
      });
    }
  }

  function drawAxes(){
    p.stroke(0);
    p.line(-p.width/2,0,p.width/2,0);    // X axis
    p.line(0, -p.height/2, 0, p.height/2); // Y axis

    const tick=50;
    p.textSize(12);
    p.textAlign(p.CENTER,p.CENTER);

    for(let x=-p.width/2; x<=p.width/2; x+=tick){
      p.line(x,-4,x,4);
      if(x!==0) p.text((x/C).toFixed(1),x,16);
    }
    for(let y=-p.height/2; y<=p.height/2; y+=tick){
      p.line(-4,y,4,y);
      if(y!==0) p.text((-y/C).toFixed(1),-20,y);
    }
  }

  function drawOrbitEllipse(){
    const ecc = parseFloat(sliderEcc.value);
    const a = 60; // semi-major axis in pixels
    const b = a * Math.sqrt(1 - ecc*ecc); // semi-minor axis
    p.noFill();
    p.stroke('#8B4513');
    p.ellipse(0,0, a*2, b*2);
  }

  /* ---------------------------  external trigger -------------------- */
  window.redrawSketch = function(){
    calculateAnalemma();
    p.redraw();
  };

}); // end p5 instance
</script>
</body>
</html>
