<script>
/* ========= GLOBAL STATE ========= */
const VERTEX_LABELS = ['A','B','C','D','E','F','G'];
let graph={vertices:[],weights:[]}; // current graph
let optimalPath=[], optimalWeight=0;

let currentAlgorithm='nearest-neighbor';
let nnState={
    startVertex:null, currentVertex:null,
    visited:new Set(),
    edges:[], totalWeight:0, finished:false
};
let clState={
    edges:[], totalWeight:0, sortedEdges:[], edgeIndex:0,
    degrees:[], uf:[], finished:false
};

/* ========= UTILITY ========= */
function enable(element,flag){element.disabled=!flag;}
function $(id){return document.getElementById(id);}

function resetButtons(){
    document.querySelectorAll('.vertex-btn').forEach(b=>{enable(b,true);});
}
function setStartingVertexButtonsEnabled(flag){
    document.querySelectorAll('.vertex-btn').forEach(b=>{enable(b,flag);});
}
function clearTable(){
    $('table-title').textContent='';
    $('table-edge-list').innerHTML='';
    $('text-total-weight').textContent='';
}
function addEdgeRow(num,edgeStr,w){
    if(!$('table-title').textContent) $('table-title').textContent='Edges chosen:';
    const tr=document.createElement('tr');
    tr.innerHTML=`<td>${num}</td><td>${edgeStr}</td><td>${w}</td>`;
    $('table-edge-list').appendChild(tr);
}

/* ========= UNION FIND (for Cheapest Link) ========= */
function ufInit(n){return Array.from({length:n},(_,i)=>i);}
function ufFind(uf,i){return uf[i]==i?i:uf[i]=ufFind(uf,uf[i]);}
function ufUnion(uf,i,j){uf[ufFind(uf,i)]=ufFind(uf,j);}

/* ========= GRAPH LOADING ========= */
function loadFixedExample(){
    graph.vertices=[
        {label:'A',x:200,y:60},
        {label:'B',x:80,y:120},
        {label:'C',x:60,y:240},
        {label:'D',x:150,y:340},
        {label:'E',x:250,y:340},
        {label:'F',x:340,y:240},
        {label:'G',x:320,y:120}
    ];
    graph.weights=[
        [0,11,16,44,30,44,23],
        [11,0,12,12,10,30,25],
        [16,12,0,43,32,36,44],
        [44,12,43,0,41,30,35],
        [30,10,32,41,0,31,20],
        [44,30,36,30,31,0,18],
        [23,25,44,35,20,18,0]
    ];
    $('text-example-type-algo').textContent='fixed example';
    $('text-example-type-optimal').textContent='fixed example';
    computeOptimal(); // uses brute force
    redrawOptimalCanvas();
}

function loadRandomExample(){
    // place vertices on circle
    const cx=200, cy=200, r=140;
    graph.vertices=[];
    for(let i=0;i<7;i++){
        const angle=-Math.PI/2 + i*(2*Math.PI/7);
        graph.vertices.push({
            label:VERTEX_LABELS[i],
            x:cx+r*Math.cos(angle),
            y:cy+r*Math.sin(angle)
        });
    }
    // weights
    graph.weights=Array.from({length:7},()=>Array(7).fill(0));
    for(let i=0;i<7;i++){
        for(let j=i+1;j<7;j++){
            const w=10+Math.floor(Math.random()*41); // 10-50
            graph.weights[i][j]=graph.weights[j][i]=w;
        }
    }
    $('text-example-type-algo').textContent='random example';
    $('text-example-type-optimal').textContent='random example';
    computeOptimal();
    redrawOptimalCanvas();
}

/* ========= OPTIMAL PATH (Brute Force) ========= */
function computeOptimal(){
    const n=graph.vertices.length;
    let bestW=Infinity, bestPath=null;
    const perm=VERTEX_LABELS.slice(1); // fix first vertex as 0 to reduce permutations
    function permute(arr,l){
        if(l==arr.length){
            const path=[0,...arr.map(lbl=>VERTEX_LABELS.indexOf(lbl))];
            let w=0;
            for(let i=0;i<n;i++){
                const a=path[i], b=path[(i+1)%n];
                w+=graph.weights[a][b];
            }
            if(w<bestW){bestW=w;bestPath=path;}
            return;
        }
        for(let i=l;i<arr.length;i++){
            [arr[l],arr[i]]=[arr[i],arr[l]];
            permute(arr,l+1);
            [arr[l],arr[i]]=[arr[i],arr[l]];
        }
    }
    permute(perm,0);
    optimalPath=bestPath;
    optimalWeight=bestW;
    $('text-optimal-weight').textContent='weight = '+optimalWeight;
}

/* ========= ALGORITHM RESET ========= */
function fullReset(){
    clearTable();
    // NN state
    nnState={startVertex:null,currentVertex:null,visited:new Set(),edges:[],totalWeight:0,finished:false};
    // CL state
    clState={
        edges:[],totalWeight:0,sortedEdges:[],edgeIndex:0,degrees:Array(7).fill(0),
        uf:ufInit(7),finished:false
    };
    if(currentAlgorithm==='nearest-neighbor'){
        $('title-algo').textContent='Select a starting vertex';
        setStartingVertexButtonsEnabled(true);
    }else{
        $('title-algo').textContent='The cheapest link algorithm';
        $('starting-vertex-selector').style.display='none';
        setStartingVertexButtonsEnabled(false);
        enable($('btn-next-step'),true); // can step immediately
    }
    $('checkbox-optimal').checked=false;
    $('optimal-result-container').style.display='none';
    redrawAlgoCanvas();
}

/* ========= EVENT HANDLERS ========= */
$('algo-cheapest-link').addEventListener('change',()=>{
    if($('algo-cheapest-link').checked){
        currentAlgorithm='cheapest-link';
        $('starting-vertex-selector').style.display='none';
        fullReset();
    }
});
$('algo-nearest-neighbor').addEventListener('change',()=>{
    if($('algo-nearest-neighbor').checked){
        currentAlgorithm='nearest-neighbor';
        $('starting-vertex-selector').style.display='block';
        fullReset();
    }
});
$('btn-start-over').addEventListener('click',fullReset);

$('btn-fixed-example').addEventListener('click',()=>{
    loadFixedExample();fullReset();
});
$('btn-random-example').addEventListener('click',()=>{
    loadRandomExample();fullReset();
});

document.querySelectorAll('.vertex-btn').forEach(btn=>{
    btn.addEventListener('click',()=>{
        const label=btn.dataset.vertex;
        const idx=VERTEX_LABELS.indexOf(label);
        nnState.startVertex=idx;
        nnState.currentVertex=idx;
        nnState.visited.add(idx);
        $('title-algo').textContent=`The nearest neighbor algorithm starting at vertex ${label}`;
        setStartingVertexButtonsEnabled(false);
        enable($('btn-next-step'),true);
    });
});

$('btn-next-step').addEventListener('click',()=>{
    if(currentAlgorithm==='nearest-neighbor'){
        stepNearestNeighbor();
    }else{
        stepCheapestLink();
    }
    redrawAlgoCanvas();
});

$('checkbox-optimal').addEventListener('change',()=>{
    $('optimal-result-container').style.display= $('checkbox-optimal').checked?'block':'none';
    if($('checkbox-optimal').checked) redrawOptimalCanvas();
});

/* ========= NEAREST NEIGHBOR STEP ========= */
function stepNearestNeighbor(){
    if(nnState.finished) return;
    const n=graph.vertices.length;
    let from=nnState.currentVertex;
    let to=null,minW=Infinity;
    if(nnState.edges.length===n-1){
        // last step: return to start
        to=nnState.startVertex;
        minW=graph.weights[from][to];
    }else{
        for(let i=0;i<n;i++){
            if(!nnState.visited.has(i) && graph.weights[from][i]<minW){
                minW=graph.weights[from][i]; to=i;
            }
        }
    }
    nnState.edges.push([from,to]);
    nnState.totalWeight+=minW;
    addEdgeRow(nnState.edges.length,
               `${VERTEX_LABELS[from]}→${VERTEX_LABELS[to]}`,minW);
    nnState.currentVertex=to;
    nnState.visited.add(to);
    if(nnState.edges.length===n){
        nnState.finished=true;
        $('text-total-weight').textContent='total weight = '+nnState.totalWeight;
        enable($('btn-next-step'),false);
    }
}

/* ========= CHEAPEST LINK PREP ========= */
function prepareCheapestLink(){
    const n=graph.vertices.length;
    const edges=[];
    for(let i=0;i<n;i++){
        for(let j=i+1;j<n;j++){
            edges.push({u:i,v:j,w:graph.weights[i][j]});
        }
    }
    edges.sort((a,b)=>a.w-b.w);
    clState.sortedEdges=edges;
    clState.degrees=Array(n).fill(0);
    clState.uf=ufInit(n);
}
/* step */
function stepCheapestLink(){
    if(clState.finished) return;
    if(clState.sortedEdges.length===0) prepareCheapestLink();
    const n=graph.vertices.length;
    while(clState.edgeIndex<clState.sortedEdges.length){
        const e=clState.sortedEdges[clState.edgeIndex++];
        const u=e.u, v=e.v;
        const du=clState.degrees[u], dv=clState.degrees[v];
        const sameSet=ufFind(clState.uf,u)===ufFind(clState.uf,v);
        const selectedCount=clState.edges.length;
        const acceptable= (du<2 && dv<2) &&
            (!sameSet || selectedCount===n-1);
        if(acceptable){
            // accept
            clState.edges.push([u,v]);
            clState.totalWeight+=e.w;
            addEdgeRow(clState.edges.length,
                `${VERTEX_LABELS[u]}–${VERTEX_LABELS[v]}`,e.w);
            clState.degrees[u]++; clState.degrees[v]++;
            ufUnion(clState.uf,u,v);
            break;
        }
    }
    if(clState.edges.length===n){
        clState.finished=true;
        $('text-total-weight').textContent='total weight = '+clState.totalWeight;
        enable($('btn-next-step'),false);
    }
}

/* ========= CANVAS DRAWING ========= */
let algoSketch, optimalSketch;

function createAlgoSketch(){
    algoSketch=new p5(s=>{
        s.setup=()=>{s.createCanvas(400,400).parent('canvas-algo-wrapper');s.noLoop();}
        s.draw=()=>{drawGraph(s,'algo');}
    });
}
function createOptimalSketch(){
    optimalSketch=new p5(s=>{
        s.setup=()=>{s.createCanvas(400,400).parent('canvas-optimal-wrapper');s.noLoop();}
        s.draw=()=>{drawGraph(s,'optimal');}
    });
}

function redrawAlgoCanvas(){algoSketch.redraw();}
function redrawOptimalCanvas(){if(optimalSketch)optimalSketch.redraw();}

function drawGraph(pg,mode){
    pg.clear();
    pg.background(255);
    pg.textAlign(pg.CENTER,pg.CENTER);
    pg.strokeWeight(1);
    pg.fill(0);
    // edges (default gray)
    pg.stroke(180);
    const n=graph.vertices.length;
    for(let i=0;i<n;i++){
        for(let j=i+1;j<n;j++){
            const v1=graph.vertices[i], v2=graph.vertices[j];
            pg.line(v1.x,v1.y,v2.x,v2.y);
            // weight label
            const mx=(v1.x+v2.x)/2, my=(v1.y+v2.y)/2;
            pg.noStroke();
            pg.fill(255);
            pg.rect(mx-12,my-8,24,14);
            pg.fill(0);
            pg.text(graph.weights[i][j],mx,my);
            pg.stroke(180);
        }
    }
    // highlight edges
    let edgeList=[];
    if(mode==='algo'){
        edgeList=(currentAlgorithm==='nearest-neighbor')?nnState.edges:clState.edges;
        pg.stroke(255,140,0);pg.strokeWeight(3);
    }else{ // optimal
        edgeList=[];
        for(let i=0;i<optimalPath.length;i++){
            let a=optimalPath[i],b=optimalPath[(i+1)%optimalPath.length];
            edgeList.push([a,b]);
        }
        pg.stroke(30,90,220);pg.strokeWeight(3);
    }
    edgeList.forEach(([u,v])=>{
        const v1=graph.vertices[u], v2=graph.vertices[v];
        pg.line(v1.x,v1.y,v2.x,v2.y);
    });

    // vertices
    for(let i=0;i<n;i++){
        const v=graph.vertices[i];
        pg.fill(255);pg.stroke(0);pg.strokeWeight(1.5);
        pg.ellipse(v.x,v.y,26,26);
        pg.fill(0);pg.noStroke();
        pg.text(v.label,v.x,v.y);
    }
}

/* ========= INITIAL STARTUP ========= */
createAlgoSketch();
createOptimalSketch();
loadFixedExample(); // default fixed graph
fullReset();
</script>
</body>
</html>
