    }
  </style>
</head>
<body>
  <div id="main-container">
    <!-- ---------- Control Panel ---------- -->
    <div id="control-panel">
      <h2>Controls</h2>

      <!-- Dot Product Sliders -->
      <div class="slider-group">
        <label for="slider-au">A.u <span id="value-au"></span></label>
        <input id="slider-au" type="range" min="-2" max="2" step="0.01" value="0.8">
      </div>
      <div class="slider-group">
        <label for="slider-bu">B.u <span id="value-bu"></span></label>
        <input id="slider-bu" type="range" min="-2" max="2" step="0.01" value="-0.5">
      </div>
      <div class="slider-group">
        <label for="slider-av">A.v <span id="value-av"></span></label>
        <input id="slider-av" type="range" min="-2" max="2" step="0.01" value="1.2">
      </div>
      <div class="slider-group">
        <label for="slider-bv">B.v <span id="value-bv"></span></label>
        <input id="slider-bv" type="range" min="-2" max="2" step="0.01" value="1.0">
      </div>
      <div class="slider-group">
        <label for="slider-aw">A.w <span id="value-aw"></span></label>
        <input id="slider-aw" type="range" min="-2" max="2" step="0.01" value="1.1">
      </div>
      <div class="slider-group">
        <label for="slider-bw">B.w <span id="value-bw"></span></label>
        <input id="slider-bw" type="range" min="-2" max="2" step="0.01" value="-0.2">
      </div>

      <!-- Scale Sliders -->
      <div class="slider-group">
        <label for="slider-x-scale">x scale <span id="value-x-scale"></span></label>
        <input id="slider-x-scale" type="range" min="20" max="200" step="1" value="80">
      </div>
      <div class="slider-group">
        <label for="slider-y-scale">y scale <span id="value-y-scale"></span></label>
        <input id="slider-y-scale" type="range" min="20" max="200" step="1" value="80">
      </div>
    </div>

    <!-- ---------- Canvas Container ---------- -->
    <div id="canvas-container"></div>
  </div>

  <script>
    /* ---------- GLOBAL STATE ---------- */
    const dotSliders = ['au','bu','av','bv','aw','bw'];
    const scaleSliders = ['x-scale','y-scale'];

    let currentValues = {
      au:0, bu:0, av:0, bv:0, aw:0, bw:0,
      xScale:80, yScale:80
    };

    // reconstructed vectors (null when invalid)
    let uVec=null, vVec=null, wVec=null, AVec=null, BVec=null;

    /* ---------- UTILITY FUNCTIONS ---------- */
    function readSliders(){
      // dot products
      dotSliders.forEach(id=>{
        const v=parseFloat(document.getElementById(`slider-${id}`).value);
        currentValues[id]=v;
        document.getElementById(`value-${id}`).textContent=v.toFixed(2);
      });
      // scales
      currentValues.xScale=parseFloat(document.getElementById('slider-x-scale').value);
      currentValues.yScale=parseFloat(document.getElementById('slider-y-scale').value);
      document.getElementById('value-x-scale').textContent=currentValues.xScale;
      document.getElementById('value-y-scale').textContent=currentValues.yScale;
    }

    // matrix helpers – everything is plain JS arrays
    const dot   = (a,b)=>a[0]*b[0]+a[1]*b[1];
    const scale = (v,s)=>[v[0]*s,v[1]*s];
    const add   = (a,b)=>[a[0]+b[0],a[1]+b[1]];

    function recalc(){
      readSliders();

      // Build measurement matrix M (3x2)
      const M=[
        [currentValues.au, currentValues.bu],
        [currentValues.av, currentValues.bv],
        [currentValues.aw, currentValues.bw]
      ];

      /* ---------- SVD via numeric.js ---------- */
      let svd;
      try{
        svd=numeric.svd(M);
      }catch(e){
        invalidate(); return;
      }
      const U=svd.U;            // 3x3
      const S=svd.S;            // [s1,s2] (length 2)
      const V=svd.V;            // 2x2

      // Truncate U to 3x2 (first 2 columns)
      const S_hat=[
        [U[0][0],U[0][1]],
        [U[1][0],U[1][1]],
        [U[2][0],U[2][1]]
      ];

      /* ---------- Solve for symmetric L ---------- */
      const K=[];
      for(let i=0;i<3;i++){
        const r=S_hat[i];
        K.push([r[0]*r[0], r[1]*r[1], 2*r[0]*r[1]]);
      }
      const Kmat=math.matrix(K);
      const rhs=math.matrix([1,1,1]);

      let lvec;
      try{
        lvec=math.lusolve(Kmat,rhs); // 3x1 matrix
      }catch(e){
        invalidate();return;
      }
      lvec=lvec.valueOf().map(x=>x[0]); // [l11,l22,l12]

      const l11=lvec[0], l22=lvec[1], l12=lvec[2];

      // Positive–definiteness check
      if(l11<=0 || l22<=0 || (l11*l22 - l12*l12)<=0){
        invalidate();return;
      }

      const L=[[l11,l12],[l12,l22]];

      /* ---------- Cholesky ---------- */
      let Q;
      try{
        Q=math.cholesky(math.matrix(L)).valueOf(); // lower-triangular 2x2
      }catch(e){
        invalidate();return;
      }

      /* ---------- Motion matrix S = S_hat * Q ---------- */
      const S_mat=numeric.dot(S_hat,Q); // 3x2

      uVec=S_mat[0]; // rows
      vVec=S_mat[1];
      wVec=S_mat[2];

      /* ---------- Shape matrix P = inv(Q) * D * V^T ---------- */
      const D=[[S[0],0],[0,S[1]]];                       // 2x2
      const QtInv=numeric.inv(Q);                        // 2x2
      const Vt=numeric.transpose(V);                    // 2x2
      const temp=numeric.dot(QtInv,D);                  // 2x2
      const P=numeric.dot(temp,Vt);                     // 2x2

      // Columns
      AVec=[P[0][0],P[1][0]];
      BVec=[P[0][1],P[1][1]];
    }

    function invalidate(){
      uVec=vVec=wVec=AVec=BVec=null;
    }

    /* ---------- Event listeners ---------- */
    function attachSliderHandlers(){
      [...dotSliders.map(id=>`slider-${id}`),'slider-x-scale','slider-y-scale']
      .forEach(id=>{
        const el=document.getElementById(id);
        el.addEventListener('input',()=>{
          recalc();
          redraw(); // p5.js redraw
        });
      });
    }

    /* ---------- p5.js SKETCH ---------- */
    let canvasW=700, canvasH=700;

    function setup(){
      const cvs=createCanvas(canvasW,canvasH);
      cvs.parent('canvas-container');
      angleMode(RADIANS);
      textSize(14);
      noLoop();
      attachSliderHandlers();
      recalc();
    }

    function draw(){
      background('#F0F0F0');

      // Center origin
      translate(width/2,height/2);
      scale(currentValues.xScale, -currentValues.yScale);  // flip Y

      strokeWeight(1/currentValues.xScale); // constant pixel thickness roughly

      if(!uVec){
        // invalid state
        resetMatrix(); // undo custom scale for correct text orientation
        translate(width/2,height/2);
        fill('#ff0000');
        noStroke();
        textAlign(CENTER,CENTER);
        text('Invalid / non-positive-definite L matrix',0,0);
        return;
      }

      drawAxes();
      drawPointsAndProjections();
    }

    function drawAxes(){
      const axes=[
        {v:uVec, col:'#0000CD', label:'u'},
        {v:vVec, col:'#6495ED', label:'v'},
        {v:wVec, col:'#00008B', label:'w'}
      ];

      axes.forEach(({v,col,label})=>{
        stroke(col);
        strokeWeight(3/currentValues.xScale);
        line(-v[0]*5,-v[1]*5,v[0]*5,v[1]*5);  // long line

        // label
        noStroke();
        fill(col);
        const labPos=scale(v,5.2);
        text(label,labPos[0],labPos[1]);
      });
    }

    function drawPointsAndProjections(){
      // Draw main points and origin
      const mainPoints=[
        {p:AVec,col:'#FF0000',label:'A'},
        {p:BVec,col:'#FFA500',label:'B'},
        {p:[0,0],col:'#FF0000',label:'O'}
      ];

      mainPoints.forEach(({p,col,label})=>{
        fill(col);
        noStroke();
        ellipse(p[0],p[1],0.15,0.15); // circles have radius ~0.075 units
        text(label,p[0]+0.1,p[1]+0.1);
      });

      // Green projection points + gray lines
      const axes=[{v:uVec,id:'u'},{v:vVec,id:'v'},{v:wVec,id:'w'}];
      [AVec,BVec].forEach((P,idx)=>{
        axes.forEach(({v,id})=>{
          // projection of P onto v
          const vNorm=math.norm(v);
          if(vNorm===0) return;
          const n=[v[0]/vNorm,v[1]/vNorm];
          const proj=scale(n, dot(P,n));

          stroke('#808080');
          strokeWeight(1/currentValues.xScale);
          line(P[0],P[1],proj[0],proj[1]);

          noStroke();
          fill('#00CC00');
          ellipse(proj[0],proj[1],0.08,0.08);
        });
      });
    }

    /* ---------- INITIAL KICK ---------- */
    window.addEventListener('DOMContentLoaded',()=>{
      // initialise displayed numeric values once DOM is ready
      readSliders();
    });
  </script>
</body>
</html>
