<script>
/*----------------------------------------------------
 * GLOBAL STATE
 *---------------------------------------------------*/
let currentType = '2A2';
let currentDimension = '2D';
let t1 = 0, t2 = 0;

/* Three.js essentials */
let scene, camera, renderer, controls3D;
const visContainer = document.getElementById('visualization-container');

initThree();
initControls();
updateVisualization();

/*----------------------------------------------------
 * INITIALIZE THREE.JS
 *---------------------------------------------------*/
function initThree(){
    scene = new THREE.Scene();
    renderer = new THREE.WebGLRenderer({antialias:true,alpha:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(visContainer.clientWidth, visContainer.clientHeight);
    visContainer.appendChild(renderer.domElement);

    addLights();
    window.addEventListener('resize', onWindowResize);
    animate();
}

function addLights(){
    const amb = new THREE.AmbientLight(0xffffff,0.5);
    scene.add(amb);
    const dir = new THREE.DirectionalLight(0xffffff,0.8);
    dir.position.set(5,10,7);
    scene.add(dir);
}

function setupCamera(){
    /* Dispose existing controls if any */
    if(controls3D){
        controls3D.dispose();
        controls3D = null;
    }
    const width = visContainer.clientWidth;
    const height = visContainer.clientHeight;
    if(currentDimension==='2D'){
        const frustum = 4; // adjust zoom
        camera = new THREE.OrthographicCamera(
            width/-frustum, width/frustum,
            height/frustum, height/-frustum,
            -100, 100);
        camera.position.set(0,0,10);
        camera.lookAt(0,0,0);
    }else{
        camera = new THREE.PerspectiveCamera(45, width/height, 0.1, 1000);
        camera.position.set(5,5,7);
        controls3D = new THREE.OrbitControls(camera, renderer.domElement);
        controls3D.enableDamping = true;
        controls3D.dampingFactor = 0.05;
    }
}

function onWindowResize(){
    const width = visContainer.clientWidth;
    const height = visContainer.clientHeight;
    if(currentDimension==='2D'){
        const frustum = 4;
        camera.left = width/-frustum;
        camera.right = width/frustum;
        camera.top = height/frustum;
        camera.bottom = height/-frustum;
        camera.updateProjectionMatrix();
    }else{
        camera.aspect = width/height;
        camera.updateProjectionMatrix();
    }
    renderer.setSize(width, height);
}

/*----------------------------------------------------
 * ANIMATION LOOP
 *---------------------------------------------------*/
function animate(){
    requestAnimationFrame(animate);
    if(controls3D) controls3D.update();
    renderer.render(scene,camera);
}

/*----------------------------------------------------
 * UI CONTROLS (Buttons + Time Pad)
 *---------------------------------------------------*/
function initControls(){
    // --- Buttons behaviour
    const allButtons = document.querySelectorAll('#control-panel button');
    allButtons.forEach(btn=>{
        btn.addEventListener('click',()=>{
            // deselect all
            allButtons.forEach(b=>b.classList.remove('active'));
            btn.classList.add('active');
            // set state
            currentType = btn.id.replace('btn-','');
            currentDimension = currentType.startsWith('2') ? '2D' : '3D';
            // rebuild
            updateVisualization();
        });
    });
    // default active button
    document.getElementById('btn-2A2').classList.add('active');

    // --- Time Pad
    const pad = document.getElementById('time-pad');
    const handle = document.getElementById('time-handle');
    const padRect = ()=>pad.getBoundingClientRect();
    const handleRadius = 9; // half of width/height
    function setHandlePos(px,py){
        // constrain inside pad
        const rect = padRect();
        px = Math.max(0, Math.min(rect.width, px));
        py = Math.max(0, Math.min(rect.height, py));
        handle.style.left = px + 'px';
        handle.style.top = py + 'px';
        // map to [-1,1]
        t1 = (px/rect.width)*2 -1;
        t2 = -((py/rect.height)*2 -1); // flip y so top is +1
        updateVisualization(true); // live update
    }
    function eventPos(e){
        if(e.touches && e.touches.length) e = e.touches[0];
        return {x:e.clientX,y:e.clientY};
    }
    let dragging=false,offsetX=0,offsetY=0;
    handle.addEventListener('mousedown',startDrag);
    handle.addEventListener('touchstart',startDrag,{passive:false});
    function startDrag(e){
        e.preventDefault();
        dragging=true;
        const rect=handle.getBoundingClientRect();
        const pos=eventPos(e);
        offsetX=pos.x - rect.left;
        offsetY=pos.y - rect.top;
        document.addEventListener('mousemove',onDrag);
        document.addEventListener('mouseup',stopDrag);
        document.addEventListener('touchmove',onDrag,{passive:false});
        document.addEventListener('touchend',stopDrag);
    }
    function onDrag(e){
        if(!dragging) return;
        e.preventDefault();
        const pos=eventPos(e);
        const rectPad=padRect();
        const px = pos.x - rectPad.left - offsetX + handleRadius;
        const py = pos.y - rectPad.top - offsetY + handleRadius;
        setHandlePos(px,py);
    }
    function stopDrag(){
        dragging=false;
        document.removeEventListener('mousemove',onDrag);
        document.removeEventListener('mouseup',stopDrag);
        document.removeEventListener('touchmove',onDrag);
        document.removeEventListener('touchend',stopDrag);
    }
    // clicking inside pad jumps handle
    pad.addEventListener('mousedown', e=>{
        if(e.target===handle) return;
        const rectPad=padRect();
        setHandlePos(e.clientX-rectPad.left, e.clientY-rectPad.top);
    });
    pad.addEventListener('touchstart', e=>{
        if(e.target===handle) return;
        const touch=eventPos(e);
        const rectPad=padRect();
        setHandlePos(touch.x-rectPad.left, touch.y-rectPad.top);
    },{passive:true});
}

/*----------------------------------------------------
 * VISUALIZATION LOGIC
 *---------------------------------------------------*/
let currentObject=null;
function updateVisualization(live=false){
    if(!live){ // full rebuild (button change) or on first call
        // clear scene
        scene.clear();
        addLights();
        setupCamera();
    }else{
        // if live update, we just update geometry positions
        if(currentObject){
            if(currentDimension==='2D'){
                update2DGeometry(currentObject.geometry);
            }else{
                update3DGeometry(currentObject.geometry);
            }
            return;
        }
    }

    // create new visualization
    if(currentDimension==='2D'){
        const lineGeo = new THREE.BufferGeometry();
        update2DGeometry(lineGeo);
        const lineMat = new THREE.LineBasicMaterial({color:0x4a69bd});
        currentObject = new THREE.Line(lineGeo,lineMat);
        scene.add(currentObject);
    }else{
        const func3D = parametric3DFunction(currentType);
        const geom = new THREE.ParametricGeometry(func3D,50,50);
        const mat = new THREE.MeshPhongMaterial({color:0xffc34d,side:THREE.DoubleSide,shininess:60});
        currentObject = new THREE.Mesh(geom,mat);
        scene.add(currentObject);
        // optional wireframe for certain types
        if(currentType==='3A3' || currentType==='3C3p' || currentType==='3C3m'){
            const wf = new THREE.LineSegments(new THREE.WireframeGeometry(geom),
                new THREE.LineBasicMaterial({color:0x000000}));
            currentObject.add(wf);
        }
    }
}

/*-------------  Update geometries for live t1/t2 -------------*/
function update2DGeometry(geom){
    const Uminmax = {
        '2A1':[-2,2],
        '2A2':[-1.5,1.5],
        '2B2':[-2,2]
    };
    const range = Uminmax[currentType] || [-2,2];
    const steps = 200;
    const vertices = [];
    const T1 = t1*1.5, T2 = t2*1.5;
    for(let i=0;i<=steps;i++){
        const u = range[0] + (range[1]-range[0])*(i/steps);
        let x=0,y=0;
        switch(currentType){
            case '2A1':
                x = u;
                y = u*u;
                break;
            case '2A2':
                x = 2*u*u*u;
                y = 3*u*u;
                break;
            case '2B2':
                x = u*u;
                y = Math.pow(u,4);
                break;
        }
        vertices.push(x+T1, y+T2, 0);
    }
    const arr = new Float32Array(vertices);
    if(geom.getAttribute('position')){
        geom.getAttribute('position').array.set(arr);
        geom.getAttribute('position').needsUpdate = true;
    }else{
        geom.setAttribute('position', new THREE.BufferAttribute(arr,3));
    }
    geom.computeBoundingSphere();
}

function update3DGeometry(geom){
    // just recreate vertices with new t1,t2 for simplicity
    const func = parametric3DFunction(currentType);
    const tmp = new THREE.ParametricGeometry(func,50,50);
    geom.copy(tmp);
    geom.attributes.position.needsUpdate=true;
    geom.computeVertexNormals();
}

/*-------------  Parametric Functions -------------*/
function parametric3DFunction(type){
    const T1 = t1*1.5, T2 = t2*1.5;
    switch(type){
        case '3A1p':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, 0.3*(U*U+V*V)+T1*U+T2*V);
            };
        case '3A1m':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, -0.3*(U*U+V*V)+T1*U+T2*V);
            };
        case '3A2':
            return (u,v,target)=>{
                const U=map(u,-1.5,1.5);
                const V=map(v,-2,2);
                target.set(2*Math.pow(U,3)+T1, 3*U*U+T2, V);
            };
        case '3A3':
            return (u,v,target)=>{
                const U=map(u,-1.5,1.5);
                const V=map(v,-4,4);
                target.set(3*Math.pow(U,4)+U*U*V, -4*Math.pow(U,3)-2*U*V+T1*2, V+T2*2);
            };
        case '3B2p':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, Math.pow(U*U+T1,2)+V*V+T2);
            };
        case '3B2m':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, -(Math.pow(U,4)+T1*2*U*U+V*V-T2));
            };
        case '3B3':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, Math.pow(U,3)+T1*U+V*V+T2);
            };
        case '3C3p':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, Math.pow(U,3)-3*U*V*V + T1*2*(U*U+V*V)+T2*4*U);
            };
        case '3C3m':
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U, V, Math.pow(U,3)+Math.pow(V,3)+T1*4*U+T2*4*V);
            };
        default:
            // fallback simple plane
            return (u,v,target)=>{
                const U=map(u,-2,2), V=map(v,-2,2);
                target.set(U,V,0);
            };
    }
}

function map(value,a,b){
    return a + (b-a)*value;
}
</script>
</body>
</html>
