            <input type="range" id="slider-ua" min="5000" max="30000" step="100" value="15000">
            <input type="number" id="input-ua" min="5000" max="30000" step="100" value="15000">
        </div>

        <div class="slider-block">
            <label for="slider-dh1">\( \Delta H_1 \)</label>
            <input type="range" id="slider-dh1" min="500" max="5000" step="50" value="1000">
            <input type="number" id="input-dh1" min="500" max="5000" step="50" value="1000">
        </div>

        <div class="slider-block">
            <label for="slider-dh2">\( \Delta H_2 \)</label>
            <input type="range" id="slider-dh2" min="500" max="5000" step="50" value="3000">
