        </div>
    </div>

    <!-- ---------------- PLOT AREA ---------------- -->
    <div id="plot-area">
        <div id="legend"></div>
        <!-- canvas created by p5 -->
    </div>
</div>

<script>
/* -------------------------------------------------
   GLOBAL STATE & CONSTANTS
---------------------------------------------------*/
let params = {
    plotType: 'semibatch',
    feedConfig: 'feed A to B',
    feedRate: 10,
    UA: 15000,
    dH1: 1000,
    dH2: 3000
};

const SIM = {
    t0: 0,
    tf: 2.0,
    dt: 0.01
};

const CONST = {
    V: 100,                  // L
    Tinit: 330,              // K
    T_cool: 330,             // K
    R: 8.314,                // J/mol/K
    Cp_total: 150000,        // J/K
    k1_0: 3e4,               // L²/(mol²·h)
    E1: 30000,               // J/mol
    k2_0: 1e5,               // L/(mol·h)
    E2: 40000                // J/mol
};

// Result arrays to be drawn
let plotData = {
    t: [],
    Na: [],
    Nb: [],
    Nd: [],
    Nu: [],
    Sel: [] // only for selectivity
};

/* -------------------------------------------------
   CONTROL PANEL UTILITIES
---------------------------------------------------*/

function linkSliderNumber(sliderId, numberId, paramKey){
    const slider = document.getElementById(sliderId);
    const number = document.getElementById(numberId);

    function syncFromSlider(){
        number.value = slider.value;
        params[paramKey] = Number(slider.value);
        updateSimulation();
    }
    function syncFromNumber(){
        slider.value = number.value;
        params[paramKey] = Number(number.value);
        updateSimulation();
    }
    slider.addEventListener('input', syncFromSlider);
    number.addEventListener('change', syncFromNumber);
}

/* -------------------------------------------------
   FEED CYCLER
---------------------------------------------------*/
function initControls(){
    // plot type
    document.getElementById('select-plot')
        .addEventListener('change', (e)=>{
            params.plotType = e.target.value;
            updateSimulation();
        });

    // feed cycler buttons
    const feedLabel = document.getElementById('cycler-feed-label');
    const feedOptions = ['feed A to B','feed B to A'];

    function cycle(delta){
        let idx = feedOptions.indexOf(params.feedConfig);
        idx = (idx + delta + feedOptions.length) % feedOptions.length;
        params.feedConfig = feedOptions[idx];
        feedLabel.textContent = params.feedConfig;
        updateSimulation();
    }

    document.getElementById('btn-feed-up').addEventListener('click', ()=>cycle(+1));
    document.getElementById('btn-feed-down').addEventListener('click', ()=>cycle(-1));

    // sliders
    linkSliderNumber('slider-feed-rate','input-feed-rate','feedRate');
    linkSliderNumber('slider-ua','input-ua','UA');
    linkSliderNumber('slider-dh1','input-dh1','dH1');
    linkSliderNumber('slider-dh2','input-dh2','dH2');
}

/* -------------------------------------------------
   NUMERICAL ODE SOLVER (RK4)
---------------------------------------------------*/
function rk4Step(y, t, dt, derivs){
    const k1 = derivs(y, t);
    const k2 = derivs(addVec(y, mulVec(k1, dt/2)), t + dt/2);
    const k3 = derivs(addVec(y, mulVec(k2, dt/2)), t + dt/2);
    const k4 = derivs(addVec(y, mulVec(k3, dt)),   t + dt);

    const increment = mulVec(addVec( addVec(k1, mulVec(k2,2)), addVec(mulVec(k3,2), k4)), dt/6);
    return addVec(y, increment);
}

function addVec(a,b){
    return a.map((v,i)=>v+b[i]);
}
function mulVec(a,scalar){
    return a.map(v=>v*scalar);
}

/* -------------------------------------------------
   ODE SYSTEM DEFINITIONS
---------------------------------------------------*/
function makeDerivs(caseType){
    // Precompute convenience values
    const feedRate = params.feedRate;          // mol/h
    const t_feed   = 100 / feedRate;           // h

    return function(y, t){
        let [Na,Nb,Nd,Nu,T] = y;
        const V = CONST.V;
        const Ca = Na / V;
        const Cb = Nb / V;

        const k1 = CONST.k1_0 * Math.exp(-CONST.E1/ (CONST.R*T));
        const k2 = CONST.k2_0 * Math.exp(-CONST.E2/ (CONST.R*T));
        const r1 = k1 * Ca * Cb*Cb;            // mol/L/h
        const r2 = k2 * Ca * Cb;               // mol/L/h

        let dNa = 0, dNb = 0;

        if(caseType === 'batch'){
            dNa = -(r1 + r2) * V;
            dNb = dNa; // same consumption
        }else if(caseType === 'semi_AtoB'){
            const FA0 = (t < t_feed) ? feedRate : 0;
            dNa = FA0 - (r1 + r2) * V;
            dNb = -(r1 + r2) * V;
        }else if(caseType === 'semi_BtoA'){
            const FB0 = (t < t_feed) ? feedRate : 0;
            dNa = -(r1 + r2) * V;
            dNb = FB0 - (r1 + r2) * V;
        }

        const dNd =  r1 * V;
        const dNu =  r2 * V;

        const dT  = ( params.UA * (CONST.T_cool - T)
                    + (-params.dH1 * r1 - params.dH2 * r2) * V )
                    / CONST.Cp_total;

        return [dNa,dNb,dNd,dNu,dT];
    };
}

/* -------------------------------------------------
   PERFORM SIMULATIONS ACCORDING TO CURRENT CONTROLS
---------------------------------------------------*/
function runBatch(){
    const init = [100,100,0,0, CONST.Tinit];
    const derivs = makeDerivs('batch');
    return integrate(init, derivs);
}

function runSemibatch(){
    let init, derivs;
    if(params.feedConfig === 'feed A to B'){
        init   = [0,100,0,0, CONST.Tinit];
        derivs = makeDerivs('semi_AtoB');
    }else{
        init   = [100,0,0,0, CONST.Tinit];
        derivs = makeDerivs('semi_BtoA');
    }
    return integrate(init, derivs);
}

function integrate(initVec, derivs){
    const nSteps = Math.floor((SIM.tf - SIM.t0) / SIM.dt);
    let y = initVec.slice();
    let t = SIM.t0;

    const arrT = [t];
    const arrNa=[y[0]], arrNb=[y[1]], arrNd=[y[2]], arrNu=[y[3]];

    for(let i=0;i<nSteps;i++){
        y = rk4Step(y, t, SIM.dt, derivs);
        t += SIM.dt;

        arrT.push(t);
        arrNa.push(y[0]);
        arrNb.push(y[1]);
        arrNd.push(y[2]);
        arrNu.push(y[3]);
    }
    return {t:arrT,Na:arrNa,Nb:arrNb,Nd:arrNd,Nu:arrNu};
}

function updateSimulation(){
    if(params.plotType === 'batch'){
        plotData = runBatch();
        plotData.Sel = []; // not used
    }else if(params.plotType === 'semibatch'){
        plotData = runSemibatch();
        plotData.Sel = [];
    }else{ // selectivity
        const batchData = runBatch();
        const semiData  = runSemibatch();

        const SelRatio = [];
        for(let i=1;i<batchData.t.length;i++){ // start at 1 to avoid t=0 0/0
            const Sd = batchData.Nd[i] / (batchData.Nu[i]===0?1e-6:batchData.Nu[i]);
            const Ss = semiData.Nd[i] / (semiData.Nu[i]===0?1e-6:semiData.Nu[i]);
            SelRatio.push(Ss / Sd);
        }
        plotData = {
            t: batchData.t.slice(1),
            Na:[],Nb:[],Nd:[],Nu:[],
            Sel: SelRatio
        };
    }
    updateLegend();
    redraw();               // p5.js redraw
}

/* -------------------------------------------------
   LEGEND HANDLING WITH MATHJAX
---------------------------------------------------*/
function updateLegend(){
    const legendDiv = document.getElementById('legend');
    if(params.plotType === 'selectivity'){
        legendDiv.innerHTML = `
            <span class="line-preview" style="border-top-color:#800080;"></span>
            semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)
        `;
    }else{
        legendDiv.innerHTML = `
            <span class="line-preview" style="border-top-color:#2ca02c; border-top-style:dashed;"></span> \\(N_a\\)&nbsp;
            <span class="line-preview" style="border-top-color:#000000; border-top-style:dashed;"></span> \\(N_b\\)&nbsp;
            <span class="line-preview" style="border-top-color:#1f77b4;"></span> \\(N_d\\)&nbsp;
            <span class="line-preview" style="border-top-color:#d62728;"></span> \\(N_u\\)
        `;
    }
    if(window.MathJax && MathJax.typesetPromise){
        MathJax.typesetPromise([legendDiv]);
    }
}

/* -------------------------------------------------
   p5.js SKETCH
---------------------------------------------------*/
let canvasW = 800, canvasH = 500;
let margins = {left:50, right:20, top:40, bottom:50};

function setup(){
    const canvas = createCanvas(canvasW, canvasH);
    canvas.parent('plot-area');
    noLoop();
    updateSimulation();     // initial run
}

function draw(){
    background('#F0F0F0');

    let plotWidth  = width  - margins.left - margins.right;
    let plotHeight = height - margins.top  - margins.bottom;

    // Determine y-range
    let yMin=0, yMax=100;
    if(params.plotType === 'selectivity'){
        yMin = Math.min(...plotData.Sel);
        yMax = Math.max(...plotData.Sel);
        if(!isFinite(yMin)) yMin=0;
        if(!isFinite(yMax)) yMax=1;
        const pad = 0.1*(yMax-yMin||1);
        yMin -= pad; yMax += pad;
    }

    // helper mapping
    function mapX(t){
        return margins.left + (t - SIM.t0) / (SIM.tf - SIM.t0) * plotWidth;
    }
    function mapY(y){
        return margins.top + (yMax - y)/(yMax - yMin) * plotHeight;
    }

    stroke(0); fill(0); textSize(12);
    // axes
    line(margins.left, margins.top, margins.left, height - margins.bottom); // y-axis
    line(margins.left, height - margins.bottom, width - margins.right, height - margins.bottom); // x-axis

    // gridlines and ticks
    stroke('#cccccc'); strokeWeight(1); drawingContext.setLineDash([2,2]);
    // x grid
    for(let xt=0; xt<=SIM.tf+1e-6; xt+=0.5){
        const x = mapX(xt);
        line(x, margins.top, x, height - margins.bottom);
    }
    // y grid
    const yTickStep = (params.plotType==='selectivity') ? ( (yMax-yMin)/5 ) : 20;
    for(let yv=yMin; yv<=yMax+1e-6; yv+=yTickStep){
        const y = mapY(yv);
        line(margins.left, y, width - margins.right, y);
    }
    drawingContext.setLineDash([]);

    // axes labels
    fill(0); noStroke();
    textAlign(CENTER);
    text('time (h)', margins.left + plotWidth/2, height - margins.bottom + 35);

    push();
    translate(margins.left-40, margins.top + plotHeight/2);
    rotate(-Math.PI/2);
    text(params.plotType==='selectivity' ? 'selectivity ratio' : 'moles (mol)', 0,0);
    pop();

    // ------------- DATA CURVES --------------
    if(params.plotType === 'selectivity'){
        drawCurve(plotData.t, plotData.Sel, '#800080', false);
    }else{
        drawCurve(plotData.t, plotData.Na, '#2ca02c', true);   // dashed
        drawCurve(plotData.t, plotData.Nb, '#000000', true);   // dashed
        drawCurve(plotData.t, plotData.Nd, '#1f77b4', false);  // solid
        drawCurve(plotData.t, plotData.Nu, '#d62728', false);  // solid
    }
}

function drawCurve(arrT, arrY, colorStr, dashed){
    stroke(colorStr);
    strokeWeight(2);
    if(dashed) drawingContext.setLineDash([8,6]);
    else drawingContext.setLineDash([]);

    noFill();
    beginShape();
    for(let i=0;i<arrT.length;i++){
        vertex(
            map(arrT[i], SIM.t0, SIM.tf, margins.left, width - margins.right),
            map(arrY[i], ...currentYRange(), margins.top, height - margins.bottom)
        );
    }
    endShape();
    drawingContext.setLineDash([]);
}

function currentYRange(){
    if(params.plotType === 'selectivity'){
        let yMin = Math.min(...plotData.Sel);
        let yMax = Math.max(...plotData.Sel);
        const pad = 0.1*(yMax-yMin||1);
        return [yMin-pad, yMax+pad];
    }else{
        return [0,100];
    }
}

/* -------------------------------------------------
   INITIALIZATION
---------------------------------------------------*/
window.addEventListener('load', ()=>{
    initControls();
});
</script>
</body>
</html>
