        background:#ff5722;
        color:#fff;
        cursor:pointer;
    }
</style>
</head>
<body>

<div id="main-container">

    <!-- -------------  CONTROL PANEL ------------- -->
    <div id="control-panel">

        <!-- Tree type -->
        <h4>Tree type</h4>
        <div id="tree-type-buttons">
            <button id="btn-rt"  class="active">Random Tree</button>
            <button id="btn-rrt">RRT</button>
            <button id="btn-rrt-star">RRT*</button>
        </div>

        <!-- Obstacle type -->
        <h4>obstacle type</h4>
        <select id="select-obstacle">
            <option value="narrow-passage" selected>narrow passage</option>
            <option value="empty">empty</option>
            <option value="random-rects">random rects</option>
        </select>

        <!-- Add nodes -->
        <h4>number of nodes to add:</h4>
        <div id="add-nodes-buttons">
            <button id="btn-add-1">1</button>
            <button id="btn-add-10">10</button>
            <button id="btn-add-100">100</button>
            <button id="btn-add-200">200</button>
            <button id="btn-add-500">500</button>
        </div>

        <!-- Exploration bias -->
        <h4>exploration bias</h4>
        <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
        <span id="bias-value-label">0</span>

