        <h4>goal radius</h4>
        <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
        <span id="radius-value-label">1</span>

    </div> <!-- control-panel -->

    <!-- -------------  CANVAS ------------- -->
    <div id="canvas-container">
        <p id="status-text">1 node, goal not yet reached</p>
        <button id="btn-reset">+</button>
    </div>

</div> <!-- main-container -->

<!-- -------------  JAVASCRIPT ------------- -->
<script>
/* ---------------- GLOBAL STATE ---------------- */
const CANVAS_W = 500, CANVAS_H = 500;
let treeType = 'rt';               // 'rt' | 'rrt' | 'rrt-star'
let obstacleType = 'narrow-passage';
let explorationBias = 0;
let goalRadius = 1;

const startPos = {x:50,  y:250};
let goalPos   = {x:450, y:450};

let nodes = [];          // Array of Node instances
let goalReached = false;
let goalNode   = null;   // when found
let obstacles  = [];     // array of rectangles {x,y,w,h}
let draggingGoal = false;

const STEP_SIZE = 20;    // max step when growing tree (for RRT/RRT*)
const REWIRE_RADIUS = 40; // for RRT*

/* ------------------- NODE CLASS ------------------- */
class Node{
    constructor(x,y,parent){
        this.x = x;
        this.y = y;
        this.parent = parent;          // another Node or null
        this.cost   = parent ? parent.cost + dist(x,y,parent.x,parent.y) : 0;
    }
}

/* ---------------  p5.js SKETCH --------------- */
let sketch = (p)=>{

    p.setup = ()=>{
        let c = p.createCanvas(CANVAS_W,CANVAS_H);
        c.parent('canvas-container');
        p.noLoop();      // draw only when we ask for it
        initSimulation();
    };

    p.draw = ()=>{ drawScene(p); }

    p.mousePressed = ()=>{
        // check if mouse over goal
        if( p.dist(p.mouseX, p.mouseY, goalPos.x, goalPos.y) <= goalRadius ){
            draggingGoal = true;
        }
    };
    p.mouseDragged = ()=>{
        if(draggingGoal){
            goalPos.x = p.constrain(p.mouseX, 0, CANVAS_W);
            goalPos.y = p.constrain(p.mouseY, 0, CANVAS_H);
            fullResetAfterGoalMove();
        }
    };
    p.mouseReleased = ()=>{ draggingGoal = false; };
};
new p5(sketch);


/* ------------------- DRAWING ------------------- */
function drawScene(p){
    p = p || window;  // in case called without arg

    // BACKGROUND
    p.background(255);

    // Obstacles
    p.stroke('blue');
    p.strokeWeight(8);
    p.noFill();
    obstacles.forEach(rect=>{
        p.rect(rect.x, rect.y, rect.w, rect.h);
    });

    // Goal region
    p.noStroke();
    p.fill(255,255,0,100);
    p.circle(goalPos.x, goalPos.y, goalRadius*2);
    // crosshair
    p.stroke(0); p.strokeWeight(1);
    p.line(goalPos.x-6, goalPos.y, goalPos.x+6, goalPos.y);
    p.line(goalPos.x, goalPos.y-6, goalPos.x, goalPos.y+6);

    // Start node (green if tree grown, red otherwise)
    if(nodes.length>1){ p.fill('green'); } else{ p.fill('red'); }
    p.noStroke();
    p.circle(startPos.x, startPos.y, 6);

    // TREE EDGES (RRT*)
    if(treeType==='rrt-star'){
        p.stroke(0); p.strokeWeight(0.5);
        nodes.forEach(n=>{
            if(n.parent){
                p.line(n.x, n.y, n.parent.x, n.parent.y);
            }
        });
    }

    // TREE NODES
    p.noStroke();
    p.fill('red');
    nodes.forEach(n=>{ p.circle(n.x, n.y, 4); });

    // FINAL PATH
    if(goalReached){
        p.stroke('green'); p.strokeWeight(3);
        let current = goalNode;
        while(current && current.parent){
            p.line(current.x, current.y, current.parent.x, current.parent.y);
            current = current.parent;
        }
    }
}

/* -------------------  INITIALIZATION ------------------- */
function initSimulation(){
    nodes = [new Node(startPos.x,startPos.y,null)];
    goalReached = false;
    goalNode = null;
    buildObstacles();
    updateStatusText();
    enableBiasSlider();
    requestRedraw();
}

function fullResetAfterGoalMove(){
    initSimulation();
}

/* -------------------  OBSTACLES ------------------- */
function buildObstacles(){
    obstacles = [];
    if(obstacleType === 'empty'){ return; }

    if(obstacleType === 'narrow-passage'){
        // Outer border (left, right, top, bottom)
        obstacles.push({x:0,y:0,w:CANVAS_W,h:0}); // just border lines by stroke alone
        // Two vertical walls with gaps
        let wallW = 8;
        // left wall
        obstacles.push({x:200, y:0,   w:wallW, h:190});
        obstacles.push({x:200, y:260, w:wallW, h:CANVAS_H-260});
        // right wall
        obstacles.push({x:300, y:60,  w:wallW, h:180});
        obstacles.push({x:300, y:270, w:wallW, h:CANVAS_H-270});
    }
    else if(obstacleType === 'random-rects'){
        for(let i=0;i<6;i++){
            let w = 50 + Math.random()*70;
            let h = 50 + Math.random()*70;
            let x = Math.random()*(CANVAS_W-w-20)+10;
            let y = Math.random()*(CANVAS_H-h-20)+10;
            obstacles.push({x,y,w,h});
        }
    }
}

/* ---------------  COLLISION CHECKS --------------- */
function pointInObstacle(x,y){
    for(let r of obstacles){
        if(x > r.x && x < r.x + r.w && y > r.y && y < r.y + r.h){
            return true;
        }
    }
    return false;
}
function segmentHitsObstacle(x1,y1,x2,y2){
    // simple discretized check
    let steps = Math.max(Math.abs(x2-x1), Math.abs(y2-y1));
    for(let i=0;i<=steps;i+=4){
        let t = i/steps;
        let x = x1 + (x2-x1)*t;
        let y = y1 + (y2-y1)*t;
        if(pointInObstacle(x,y)) return true;
    }
    return false;
}

/* ---------------  ADD NODES --------------- */
function addNodes(count){
    for(let i=0;i<count;i++){
        let newNode;
        if(treeType==='rt'){
            newNode = randomTreeStep();
        }else if(treeType==='rrt'){
            newNode = rrtStep();
        }else{
            newNode = rrtStarStep();
        }
        if(newNode){
            nodes.push(newNode);
            // goal check
            if(!goalReached && dist(newNode.x,newNode.y,goalPos.x,goalPos.y) <= goalRadius){
                goalReached = true;
                goalNode = newNode;
            }
            else if(goalReached && treeType==='rrt-star'){
                // see if new node offers shorter path
                if(dist(newNode.x,newNode.y,goalPos.x,goalPos.y) <= goalRadius){
                    if(newNode.cost < goalNode.cost){
                        goalNode = newNode;
                    }
                }
            }
        }
    }
    updateStatusText();
    requestRedraw();
}

function randomTreeStep(){
    // purely random point insertion, ignore obstacles
    let tries=0;
    while(tries<100){
        let x = Math.random()*CANVAS_W;
        let y = Math.random()*CANVAS_H;
        if(!pointInObstacle(x,y)){
            return new Node(x,y,null); // No parent/edges for RT
        }
        tries++;
    }
    return null;
}

function rrtStep(){
    let sample;
    if(Math.random() < explorationBias){   // sample goal
        sample = {x:goalPos.x, y:goalPos.y};
    }else{
        sample = {x:Math.random()*CANVAS_W, y:Math.random()*CANVAS_H};
    }
    let nearest = nearestNode(sample.x, sample.y);
    let dir = Math.atan2(sample.y - nearest.y, sample.x - nearest.x);
    let newX = nearest.x + STEP_SIZE*Math.cos(dir);
    let newY = nearest.y + STEP_SIZE*Math.sin(dir);
    newX = Math.max(0, Math.min(CANVAS_W, newX));
    newY = Math.max(0, Math.min(CANVAS_H, newY));

    if(pointInObstacle(newX,newY) || segmentHitsObstacle(nearest.x,nearest.y,newX,newY)){
        return null;
    }
    return new Node(newX,newY, nearest);
}

function rrtStarStep(){
    let sample;
    if(Math.random() < explorationBias){
        sample = {x:goalPos.x, y:goalPos.y};
    }else{
        sample = {x:Math.random()*CANVAS_W, y:Math.random()*CANVAS_H};
    }
    // find nearest
    let nearest = nearestNode(sample.x, sample.y);
    let dir = Math.atan2(sample.y - nearest.y, sample.x - nearest.x);
    let newX = nearest.x + STEP_SIZE*Math.cos(dir);
    let newY = nearest.y + STEP_SIZE*Math.sin(dir);
    newX = Math.max(0, Math.min(CANVAS_W, newX));
    newY = Math.max(0, Math.min(CANVAS_H, newY));

    if(pointInObstacle(newX,newY) || segmentHitsObstacle(nearest.x,nearest.y,newX,newY)){
        return null;
    }
    let newNode = new Node(newX,newY, nearest);

    // search nearby for better parent
    let nearNodes = nodes.filter(n => dist(n.x,n.y,newX,newY) < REWIRE_RADIUS);
    let bestParent = nearest;
    let bestCost = newNode.cost;
    for(let n of nearNodes){
        if(!segmentHitsObstacle(n.x,n.y,newX,newY)){
            let cost = n.cost + dist(n.x,n.y,newX,newY);
            if(cost < bestCost){
                bestCost = cost;
                bestParent = n;
            }
        }
    }
    newNode.parent = bestParent;
    newNode.cost = bestCost;

    // rewire others through new node
    for(let n of nearNodes){
        if(n!==bestParent && !segmentHitsObstacle(n.x,n.y,newX,newY)){
            let newCost = newNode.cost + dist(n.x,n.y,newX,newY);
            if(newCost < n.cost){
                n.parent = newNode;
                n.cost = newCost;
            }
        }
    }
    return newNode;
}

function nearestNode(x,y){
    let best = nodes[0];
    let bestDist = dist(x,y,best.x,best.y);
    for(let n of nodes){
        let d = dist(x,y,n.x,n.y);
        if(d < bestDist){
            bestDist = d;
            best = n;
        }
    }
    return best;
}

/* ---------------  STATUS TEXT --------------- */
function updateStatusText(){
    const p = document.getElementById('status-text');
    if(goalReached){
        p.textContent = `${nodes.length} nodes, path length ${goalNode.cost.toFixed(2)}`;
    }else{
        p.textContent = `${nodes.length} nodes, goal not yet reached`;
    }
}

/* ---------------  UI HANDLERS --------------- */
function setActiveTreeButton(btnId){
    document.querySelectorAll('#tree-type-buttons button').forEach(b=>b.classList.remove('active'));
    document.getElementById(btnId).classList.add('active');
}

function enableBiasSlider(){
    const slider = document.getElementById('slider-bias');
    slider.disabled = !(treeType==='rrt' || treeType==='rrt-star');
    slider.style.opacity = slider.disabled ? 0.4 : 1;
}

/* ---------------  EVENT LISTENERS --------------- */
// Tree type buttons
document.getElementById('btn-rt').addEventListener('click',()=>{
    treeType='rt';
    setActiveTreeButton('btn-rt');
    initSimulation();
});
document.getElementById('btn-rrt').addEventListener('click',()=>{
    treeType='rrt';
    setActiveTreeButton('btn-rrt');
    initSimulation();
});
document.getElementById('btn-rrt-star').addEventListener('click',()=>{
    treeType='rrt-star';
    setActiveTreeButton('btn-rrt-star');
    initSimulation();
});

// Obstacle select
document.getElementById('select-obstacle').addEventListener('change',e=>{
    obstacleType = e.target.value;
    initSimulation();
});

// Add nodes buttons
['1','10','100','200','500'].forEach(val=>{
    document.getElementById('btn-add-'+val).addEventListener('click',()=>addNodes(parseInt(val)));
});

// Bias slider
document.getElementById('slider-bias').addEventListener('input',e=>{
    explorationBias = parseFloat(e.target.value);
    document.getElementById('bias-value-label').textContent = explorationBias;
});

// Radius slider
document.getElementById('slider-radius').addEventListener('input',e=>{
    goalRadius = parseFloat(e.target.value);
    document.getElementById('radius-value-label').textContent = goalRadius;
    // after change, maybe goal reached now
    if(!goalReached){
        nodes.forEach(n=>{
            if(dist(n.x,n.y,goalPos.x,goalPos.y) <= goalRadius){
                goalReached = true;
                goalNode = n;
            }
        });
    }
    updateStatusText();
    requestRedraw();
});

// Reset button
document.getElementById('btn-reset').addEventListener('click',()=>{
    goalPos = {x:450, y:450};
    initSimulation();
});

/* ---------------  UTILITIES --------------- */
function requestRedraw(){
    window.redraw();  // p5 global
}
</script>
</body>
</html>
