    <div class="control-group">
        <label for="slider-v">\(v\)</label>
        <input id="slider-v" type="range" min="0" max="2.0" step="0.01" value="0">
        <input id="input-v" type="number" min="0" max="2.0" step="0.01" value="0">
    </div>

    <!-- lambda -->
    <div class="control-group">
        <label for="slider-lambda">\(\\lambda\)</label>
        <input id="slider-lambda" type="range" min="0" max="2.0" step="0.01" value="1.1">
        <input id="input-lambda" type="number" min="0" max="2.0" step="0.01" value="1.1">
    </div>

</div>

<!-- ============  VISUALIZATION  ============ -->
<div id="visualization-container">
    <canvas id="three-canvas"></canvas>
</div>


<!-- ============  SCRIPT  ============ -->
<script>
(function(){
    /* ----------  THREE.JS INIT ---------- */
    const canvas = document.getElementById('three-canvas');
    const renderer = new THREE.WebGLRenderer({canvas:canvas, antialias:true});
    renderer.setPixelRatio(window.devicePixelRatio);
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    const camera = new THREE.PerspectiveCamera(60, 1, 0.1, 100);
    camera.position.set(4,4,4);
    camera.lookAt(0,1,0);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.target.set(0,1,0);
    controls.update();

    function resize(){
        const w = canvas.clientWidth;
        const h = canvas.clientHeight;
        renderer.setSize(w,h,false);
        camera.aspect = w/h;
        camera.updateProjectionMatrix();
    }
    window.addEventListener('resize', resize);
    resize();

    /* ----------  LIGHTS ---------- */
    scene.add(new THREE.AmbientLight(0x666666));
    const dirLight = new THREE.DirectionalLight(0xffffff,1);
    dirLight.position.set(5,5,5);
    scene.add(dirLight);

    /* ----------  AXES ---------- */
    scene.add(new THREE.AxesHelper(4));

    /* ----------  STATIC GEOMETRY (POTENTIAL SURFACE & CYLINDER) ---------- */
    // Potential surface
    const surfFunc = function(u,v,target){
        const rho = 0.4 + u*2.1;
        const theta = v*2*Math.PI;
        const x = rho*Math.cos(theta);
        const y = rho*rho + 1/(rho*rho); // vertical axis
        const z = -rho*Math.sin(theta);
        target.set(x,y,z);
    };
    const surfGeom = new THREE.ParametricBufferGeometry(surfFunc, 60, 120);
    const surfMat = new THREE.MeshStandardMaterial({color:0xE69943, metalness:0.4, roughness:0.5, side:THREE.DoubleSide});
    const surfaceMesh = new THREE.Mesh(surfGeom, surfMat);
    scene.add(surfaceMesh);

    // Central cylinder
    const cylGeom = new THREE.CylinderBufferGeometry(0.4,0.4,2.5,32);
    const cylMat = new THREE.MeshStandardMaterial({color:0xcccccc, metalness:0.1, roughness:0.9});
    const cylMesh = new THREE.Mesh(cylGeom, cylMat);
    cylMesh.position.y = 1.25; // raise half-height
    scene.add(cylMesh);

    /* ----------  PATH HOLDER ---------- */
    let pathMesh = null; // will hold the ion path mesh

    /* ----------  NUMERICAL SOLVER ---------- */
    function integrateRK4(t, rho0, zeta0, e0, nu, lambda){
        const ds = 0.05;
        const steps = Math.floor(t/ds);
        let s = 0;

        // State vector Y = [rho, drho/ds, phi]
        let Y = [rho0, 0, 0];

        const points = [];
        for(let i=0;i<=steps;i++){
            // Save point
            const rho_i = Y[0];
            const phi_i = Y[2];

            const u_rho = rho_i*rho_i + 1/(rho_i*rho_i);
            const sqrt_arg = e0 - u_rho;
            const z_i = Math.sqrt(Math.max(0,sqrt_arg)) * Math.sin(Math.sqrt(2)*nu*s + zeta0);
            const x_i = rho_i * Math.cos(phi_i);
            const y_i = rho_i * Math.sin(phi_i);

            // Map to THREE coords (x, z_calc, -y) to fit orientation
            points.push(new THREE.Vector3(x_i, z_i, -y_i));

            // Advance one RK4 step unless last
            if(i === steps) break;

            const derivs = function(state){
                const rho = state[0];
                const drho = state[1];

                const d_rho = drho;
                const d2_rho = -(2*rho - 2/Math.pow(rho,3)) - 4*Math.pow(lambda,2)*rho + 4*e0/Math.pow(rho,3);
                const d_phi = 2*(e0/Math.pow(rho,2) - lambda);
                return [d_rho, d2_rho, d_phi];
            };

            const k1 = derivs(Y);
            const Yk2 = Y.map((v,idx)=>v + k1[idx]*ds/2);
            const k2 = derivs(Yk2);
            const Yk3 = Y.map((v,idx)=>v + k2[idx]*ds/2);
            const k3 = derivs(Yk3);
            const Yk4 = Y.map((v,idx)=>v + k3[idx]*ds);
            const k4 = derivs(Yk4);

            Y = Y.map( (v,idx) => v + ds*(k1[idx] + 2*k2[idx] + 2*k3[idx] + k4[idx])/6 );
            s += ds;
        }
        return points;
    }

    /* ----------  CONTROLS -> VISUALIZATION LINK ---------- */
    const paramIDs = ['t','rho0','zeta0','e0','v','lambda'];
    paramIDs.forEach(id=>{
        const slider = document.getElementById('slider-'+id);
        const input  = document.getElementById('input-'+id);

        // sync slider -> number
        slider.addEventListener('input', ()=>{
            input.value = slider.value;
            updateVisualization();
        });
        // sync number -> slider
        input.addEventListener('input', ()=>{
            slider.value = input.value;
            updateVisualization();
        });
    });

    /* ----------  UPDATE VISUALIZATION ---------- */
    function updateVisualization(){
        // Gather parameters
        const t       = parseFloat(document.getElementById('input-t').value);
        const rho0    = parseFloat(document.getElementById('input-rho0').value);
        const zeta0   = parseFloat(document.getElementById('input-zeta0').value);
        const e0      = parseFloat(document.getElementById('input-e0').value);
        const nu      = parseFloat(document.getElementById('input-v').value);
        const lambda  = parseFloat(document.getElementById('input-lambda').value);

        // Compute new path
        const curvePoints = integrateRK4(t, rho0, zeta0, e0, nu, lambda);

        // Remove old path
        if(pathMesh){
            scene.remove(pathMesh);
            pathMesh.geometry.dispose();
            pathMesh.material.dispose();
            pathMesh = null;
        }

        // Create new mesh
        const curve = new THREE.CatmullRomCurve3(curvePoints);
        const tubeGeom = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
        const tubeMat = new THREE.MeshBasicMaterial({color:0x61AEEF});
        pathMesh = new THREE.Mesh(tubeGeom, tubeMat);
        scene.add(pathMesh);
    }

    /* ----------  INITIALIZE ---------- */
    // Wait until MathJax has finished typesetting labels, then render
    if(window.MathJax){
        MathJax.startup.promise.then(()=>{
            updateVisualization();
        });
    }else{
        updateVisualization();
    }

    /* ----------  ANIMATION LOOP ---------- */
    (function animate(){
        requestAnimationFrame(animate);
        renderer.render(scene,camera);
    })();

})();
</script>
</body>
</html>
