            gap:20px;
            margin-top:20px;
        }
        .density-plot{
            width:45%;
            min-width:350px;
            height:400px;
        }
    </style>
</head>
<body>
<div id="app-container">

    <!-- ─── Control panel ───────────────────────────────────────────── -->
    <div id="control-panel">
        <label for="slider-r">R (Å)</label>
        <input id="slider-r" type="range" min="0.001" max="4.0" step="0.001" value="0.001">
        <output id="slider-r-value">0.001</output>
    </div>

    <!-- ─── Potential energy plot ──────────────────────────────────── -->
    <div id="potential-energy-plot-container"></div>

    <!-- ─── Density plots ──────────────────────────────────────────── -->
    <div id="density-plots-container">
        <div id="bonding-plot-container"  class="density-plot"></div>
        <div id="antibonding-plot-container" class="density-plot"></div>
    </div>

</div>

<script>
/* ============================================================
   Utility / math helpers
   ============================================================ */
const ANGSTROM_TO_BOHR = 1 / 0.529177;   // ≈ 1.889726124565062
const GRID_SIZE   = 60;
const GRID_MIN    = -3;
const GRID_MAX    =  3;

/* ---------- generate x–y grid once ---------- */
const xGrid = Array.from({length: GRID_SIZE},
                (_,i)=> GRID_MIN + (GRID_MAX-GRID_MIN)*i/(GRID_SIZE-1));
const yGrid = [...xGrid];

/* ============================================================
   Potential–energy curves  (static)
   ============================================================ */
function calcOverlap(R){
    return (1 + R + R*R/3) * Math.exp(-R);
}
function calcCoulomb(R){
    return (1/R) - (1 + 1/R) * Math.exp(-2*R);
}
function calcExchange(R){
    return (1 + R) * Math.exp(-R);
}
function generatePotentialEnergyData(){
    const R_au  = [];
    const R_ang = [];
    const E_b   = [];
    const E_a   = [];
    for(let R=0.2; R<=15.0; R+=0.1){
        const S = calcOverlap(R);
        const J = calcCoulomb(R);
        const K = calcExchange(R);
        const E_bonding    = (J + K) / (1 + S);
        const E_antibonding= (J - K) / (1 - S);

        R_au.push(R);
        R_ang.push(R*0.529177);
        E_b.push(E_bonding);
        E_a.push(E_antibonding);
    }
    return {R_ang, E_b, E_a};
}
function plotPotentialEnergy(){
    const {R_ang, E_b, E_a} = generatePotentialEnergyData();

    const traces = [
        { x:R_ang, y:E_b, mode:'lines', name:'bonding PE',    line:{color:'blue'} },
        { x:R_ang, y:E_a, mode:'lines', name:'antibonding PE',line:{color:'red'}  }
    ];
    const layout = {
        title:'Potential Energy',
        xaxis:{title:'R (Å)', range:[0,8]},
        yaxis:{title:'Energy (Hartrees)', range:[-0.6,0.4]},
        legend:{orientation:'h',x:0.25,y:1.15},
        margin:{t:60,l:50,r:20,b:50},
    };
    Plotly.newPlot('potential-energy-plot-container', traces, layout, {responsive:true});
}

/* ============================================================
   Electron density surfaces (dynamic)
   ============================================================ */
let bondingInit     = false;
let antibondingInit = false;

function generateDensitySurfaces(R_angstrom){
    const R = R_angstrom * ANGSTROM_TO_BOHR;      // convert to Bohr
    const halfR = R/2;

    /* normalization constants */
    const S = calcOverlap(R);
    const N_b = 1/Math.sqrt(2*(1+S));
    const N_a = 1/Math.sqrt(2*(1-S));

    const zBonding    = [];
    const zAntibonding= [];

    for(let i=0;i<GRID_SIZE;i++){
        const rowBond = [];
        const rowAnti = [];
        const x = xGrid[i];

        for(let j=0;j<GRID_SIZE;j++){
            const y = yGrid[j];

            const distA = Math.hypot(x + halfR, y);
            const distB = Math.hypot(x - halfR, y);

            const phiA = Math.exp(-distA);
            const phiB = Math.exp(-distB);

            const psi_b = N_b * (phiA + phiB);
            const psi_a = N_a * (phiA - phiB);

            rowBond.push(psi_b*psi_b);
            rowAnti.push(psi_a*psi_a);
        }
        zBonding.push(rowBond);
        zAntibonding.push(rowAnti);
    }
    return {zBonding, zAntibonding};
}

function updateDensityPlots(R_angstrom){
    const {zBonding, zAntibonding} = generateDensitySurfaces(R_angstrom);

    /* -----  common layout  ----- */
    const layout = {
        autosize:true,
        margin:{l:0,r:0,t:0,b:0},
        scene:{
            xaxis:{visible:false, range:[GRID_MIN,GRID_MAX]},
            yaxis:{visible:false, range:[GRID_MIN,GRID_MAX]},
            zaxis:{visible:false, range:[0,0.5]},
            camera:{eye:{x:1.25,y:1.25,z:1.25}}
        }
    };

    /* ----- bonding ----- */
    const bondingSurface = {
        type:'surface',
        x:xGrid,
        y:yGrid,
        z:zBonding,
        colorscale:'Viridis',
        showscale:false
    };
    if(!bondingInit){
        Plotly.newPlot('bonding-plot-container',[bondingSurface],layout,{responsive:true});
        bondingInit=true;
    }else{
        Plotly.react('bonding-plot-container',[bondingSurface],layout,{responsive:true});
    }

    /* ----- antibonding ----- */
    const antibondingSurface = {
        type:'surface',
        x:xGrid,
        y:yGrid,
        z:zAntibonding,
        colorscale:'Viridis',
        showscale:false
    };
    if(!antibondingInit){
        Plotly.newPlot('antibonding-plot-container',[antibondingSurface],layout,{responsive:true});
        antibondingInit=true;
    }else{
        Plotly.react('antibonding-plot-container',[antibondingSurface],layout,{responsive:true});
    }
}

/* ============================================================
   DOM ready: initialise everything
   ============================================================ */
document.addEventListener('DOMContentLoaded',()=>{
    const slider = document.getElementById('slider-r');
    const output = document.getElementById('slider-r-value');

    /* plot static potential–energy curves */
    plotPotentialEnergy();

    /* initial densities (default slider value) */
    output.value = parseFloat(slider.value).toFixed(3);
    updateDensityPlots(parseFloat(slider.value));

    /* slider interaction */
    slider.addEventListener('input', e=>{
        const val = parseFloat(e.target.value);
        output.value = val.toFixed(3);
        updateDensityPlots(val);
    });
});
</script>
</body>
</html>
