      background:#3B82F6;
      color:#fff;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <!-- ========= CONTROL PANEL ========= -->
    <div id="control-panel">
      <h4>time</h4>
      <input type="range" id="slider-time" min="0" max="672" step="1" value="0">
      <div>
        <button id="btn-play-pause">▶</button>
        <button id="btn-step-back">«</button>
        <button id="btn-reset">⥀</button>
        <button id="btn-step-forward">»</button>
      </div>
      <p id="display-time">0 d 0 h</p>
      <hr>
      <h4>Moon's rotational period related to the current value</h4>
      <div id="rotation-selector">
        <button id="btn-rot-half">half</button>
        <button id="btn-rot-normal" class="active">normal</button>
        <button id="btn-rot-double">double</button>
        <button id="btn-rot-none">no rotation</button>
      </div>
      <hr>
      <div>
        <input type="checkbox" id="check-day-night">
        <label for="check-day-night">day/night</label>
      </div>
      <div>
        <input type="checkbox" id="check-near-side" checked>
        <label for="check-near-side">near side of the Moon</label>
      </div>
      <div>
        <input type="checkbox" id="check-earth-moon-line" checked>
        <label for="check-earth-moon-line">Earth-Moon line</label>
      </div>
      <div>
        <input type="checkbox" id="check-moon-only">
        <label for="check-moon-only">the Moon only</label>
      </div>
    </div>

    <!-- ========= VISUALIZATION ========= -->
    <div id="visualization-area">
      <canvas id="main-canvas"></canvas>
    </div>
  </div>

<script>
/* =====================================================
   GLOBAL STATE
===================================================== */
let timeHours = 0;            // simulation time (hours)
const TIME_MAX = 672;         // 28 days in hours
let playing = false;          // animation state
const SPEED_HR_PER_SEC = 4;   // simulation speed while playing
let rotMode = 'normal';       // Moon rotation mode

/* ======== DOM SHORTCUTS ======== */
const sliderTime   = document.getElementById('slider-time');
const btnPlayPause = document.getElementById('btn-play-pause');
const btnStepBack  = document.getElementById('btn-step-back');
const btnReset     = document.getElementById('btn-reset');
const btnStepFwd   = document.getElementById('btn-step-forward');
const displayTime  = document.getElementById('display-time');
const rotButtons   = {
  half:  document.getElementById('btn-rot-half'),
  normal:document.getElementById('btn-rot-normal'),
  double:document.getElementById('btn-rot-double'),
  none:  document.getElementById('btn-rot-none')
};

/* ======== CANVAS CONSTANTS ======== */
const EARTH_R  = 80;
const MOON_R   = 25;
const ORBIT_R  = 250;

/* =====================================================
   p5.js SKETCH
===================================================== */
let canvas;

function setup(){
  const container = document.getElementById('visualization-area');
  canvas = createCanvas(container.clientWidth, container.clientHeight);
  canvas.parent('visualization-area');
  noLoop();                 // start paused
}

function windowResized(){
  const container = document.getElementById('visualization-area');
  resizeCanvas(container.clientWidth, container.clientHeight);
  redraw();
}

function draw(){
  background(0);

  // ------- Progress time if playing -------
  if(playing){
    timeHours += (deltaTime/1000)*SPEED_HR_PER_SEC;
    if(timeHours > TIME_MAX) timeHours -= TIME_MAX;
    updateTimeUI();
  }

  push();
  translate(width/2, height/2);
  renderSystem();
  pop();
}

/* =====================================================
   RENDERING ROUTINES
===================================================== */
function renderSystem(){
  const earthRot  = (timeHours / 24) * TWO_PI;
  const moonOrbit = (timeHours / TIME_MAX) * TWO_PI;
  let moonRot;

  switch(rotMode){
    case 'half':   moonRot = moonOrbit/2; break;
    case 'double': moonRot = moonOrbit*2; break;
    case 'none':   moonRot = 0;           break;
    default:       moonRot = moonOrbit;   // normal
  }

  const moonOnly     = document.getElementById('check-moon-only').checked;
  const showLine     = document.getElementById('check-earth-moon-line').checked && !moonOnly;
  const showDayNight = document.getElementById('check-day-night').checked;

  // ---------------- Earth ----------------
  if(!moonOnly){
    drawEarth(earthRot, showDayNight);
  }

  // ------------ Compute Moon position ------------
  let mx = ORBIT_R * Math.cos(moonOrbit - HALF_PI);
  let my = ORBIT_R * Math.sin(moonOrbit - HALF_PI);
  if(moonOnly){ mx = 0; my = 0; }

  // ------------ Earth–Moon Line ------------
  if(showLine){
    push();
    stroke(255);
    strokeWeight(1);
    drawingContext.setLineDash([6,6]);
    line(0,0,mx,my);
    drawingContext.setLineDash([]);
    pop();
  }

  // ---------------- Moon ----------------
  push();
  translate(mx,my);
  drawMoon(moonRot, showDayNight);
  pop();
}

/* ---------- Helper: Earth ---------- */
function drawEarth(rotationAngle, showDayNight){
  push();
  noStroke();
  // Main body
  fill('#3B82F6');
  circle(0,0,EARTH_R*2);

  // Simple landmass
  fill('#22C55E');
  beginShape();
  vertex(-EARTH_R*0.2, -EARTH_R*0.1);
  vertex(-EARTH_R*0.1, -EARTH_R*0.4);
  vertex(EARTH_R*0.1, -EARTH_R*0.3);
  vertex(EARTH_R*0.3, -EARTH_R*0.05);
  vertex(EARTH_R*0.25, EARTH_R*0.2);
  vertex(0, EARTH_R*0.1);
  endShape(CLOSE);

  // Day/Night terminator (static relative to Sun)
  if(showDayNight){
    fill(0,128);
    arc(0,0,EARTH_R*2,EARTH_R*2, HALF_PI, 3*HALF_PI, PIE);
  }

  // Rotating stick figure
  push();
  rotate(rotationAngle);
  translate(0,-EARTH_R);
  drawStickFigure('#EF4444');
  pop();

  pop();
}

/* ---------- Helper: Moon ---------- */
function drawMoon(rotationAngle, showDayNight){
  push();
  noStroke();
  fill('#808080');
  circle(0,0,MOON_R*2);

  const showNearSide = document.getElementById('check-near-side').checked;

  if(showNearSide){
    push();
    rotate(rotationAngle);
    // Green arc facing Earth (downward in default orientation)
    fill('#22C55E');
    arc(0,0,MOON_R*2,MOON_R*2,-HALF_PI,HALF_PI, PIE);

    // Magenta stick figure on near side
    push();
    translate(0,MOON_R);
    rotate(PI);                // make figure stand outward
    drawStickFigure('#D946EF');
    pop();

    pop();
  }

  // Day/Night on Moon
  if(showDayNight){
    fill(0,128);
    arc(0,0,MOON_R*2,MOON_R*2, HALF_PI, 3*HALF_PI, PIE);
  }
  pop();
}

/* ---------- Helper: Stick Figure ---------- */
function drawStickFigure(colorHex){
  stroke(colorHex);
  strokeWeight(2);
  noFill();
  // Head
  circle(0,-12,10);
  // Body
  line(0,-7,0,6);
  // Arms
  line(0,-4,-5,-9);
  line(0,-4,5,-9);
  // Legs
  line(0,6,-5,16);
  line(0,6,5,16);
}

/* =====================================================
   UI EVENT HANDLERS
===================================================== */
function updateTimeUI(){
  // keep value in range 0–672
  timeHours = (timeHours % TIME_MAX + TIME_MAX) % TIME_MAX;
  sliderTime.value = Math.floor(timeHours);
  const days  = Math.floor(timeHours/24);
  const hours = Math.floor(timeHours % 24);
  displayTime.textContent = `${days} d ${hours} h`;
}

sliderTime.addEventListener('input', e=>{
  timeHours = Number(e.target.value);
  if(!playing) redraw();
  updateTimeUI();
});

/* --- Play / Pause --- */
btnPlayPause.addEventListener('click', ()=>{
  playing = !playing;
  btnPlayPause.textContent = playing ? '❚❚' : '▶';
  if(playing){ loop(); }
  else{ noLoop(); redraw(); }
});

/* --- Step Back --- */
btnStepBack.addEventListener('click', ()=>{
  timeHours = Math.max(0, timeHours-1);
  updateTimeUI();
  redraw();
});

/* --- Step Forward --- */
btnStepFwd.addEventListener('click', ()=>{
  timeHours = Math.min(TIME_MAX, timeHours+1);
  updateTimeUI();
  redraw();
});

/* --- Reset --- */
btnReset.addEventListener('click', ()=>{
  timeHours = 0;
  playing = false;
  btnPlayPause.textContent = '▶';
  noLoop();
  updateTimeUI();
  redraw();
});

/* --- Rotation Selector --- */
function setRotationMode(modeStr){
  rotMode = modeStr;
  // Visual active class
  Object.keys(rotButtons).forEach(m=>{
    rotButtons[m].classList.toggle('active', m===modeStr);
  });
  if(!playing) redraw();
}

rotButtons.half  .addEventListener('click', ()=> setRotationMode('half'));
rotButtons.normal.addEventListener('click', ()=> setRotationMode('normal'));
rotButtons.double.addEventListener('click', ()=> setRotationMode('double'));
rotButtons.none  .addEventListener('click', ()=> setRotationMode('none'));

/* --- Checkbox Redraws --- */
['check-day-night','check-near-side','check-earth-moon-line','check-moon-only']
  .forEach(id=>{
    document.getElementById(id).addEventListener('change', ()=>{ if(!playing) redraw(); });
  });

/* ======== Initial UI Sync ======== */
updateTimeUI();
</script>
</body>
</html>
