<script>
/* ---------- GLOBAL DATA STORAGE ---------- */
let allLines = []; // each element: {x, y, K, subband, branch}
let maxIntensity = 1;

/* ---------- CONSTANTS ---------- */
const v0 = 1000;               // cm-1
const B_u = 0.98;
const A_u = 1.95;
const B_l = 1.0;
const A_l = 2.0;
const zeta = 0.2;             // not used in simple model
const T = 300;
const k_B = 0.695;
const J_max = 60;

/* ---------- INITIALISE CONTROLS ---------- */
const lowerSlider = document.getElementById('slider-x-lower');
const upperSlider = document.getElementById('slider-x-upper');
const lowerSpan   = document.getElementById('span-x-lower-value');
const upperSpan   = document.getElementById('span-x-upper-value');
const DEFAULT_LOWER = 902;
const DEFAULT_UPPER = 1091;
[lowerSlider.min, lowerSlider.max, lowerSlider.step, lowerSlider.value] = [900,1100,1,DEFAULT_LOWER];
[upperSlider.min, upperSlider.max, upperSlider.step, upperSlider.value] = [900,1100,1,DEFAULT_UPPER];
lowerSpan.textContent = DEFAULT_LOWER + '.';
upperSpan.textContent = DEFAULT_UPPER + '.';

/* ---------- UTILITY FUNCTIONS ---------- */
function energy(B,A,J,K){
    return B*J*(J+1)+(A-B)*K*K;
}
function generateData(){
    const lines=[];
    let maxI=0;
    for(let K=0; K<=5; K++){
        for(const sub of ['positive','negative']){
            if(K===0 && sub==='negative') continue; // no negative for K=0
            const dK = sub==='positive'?+1:-1;
            const K_l = K;
            const K_u = K_l + dK;
            for(let J_l=0; J_l<=J_max; J_l++){
                const F_l = energy(B_l,A_l,J_l,K_l);
                const intensityBase = (2*J_l+1)*Math.exp(-F_l/(k_B*T));
                for(const br of ['P','Q','R']){
                    let J_u;
                    if(br==='P'){J_u = J_l-1;}
                    else if(br==='Q'){J_u = J_l;}
                    else {J_u = J_l+1;} // R
                    if(J_u<0 || J_u>J_max) continue;

                    const F_u = energy(B_u,A_u,J_u,K_u);
                    const wNum = v0 + (F_u - F_l);
                    const intensity = intensityBase; // simple model
                    maxI = Math.max(maxI,intensity);
                    lines.push({x:wNum, y:intensity, K, subband:sub, branch:br});
                }
            }
        }
    }
    // normalise intensities to max 1
    lines.forEach(l=> l.y = l.y / maxI);
    maxIntensity = 1;
    return lines;
}

function byId(id){return document.getElementById(id);}

/* enable/disable helpers */
function setGroupEnabled(el,enabled){
    if(enabled){
        el.classList.remove('disabled');
    }else{
        el.classList.add('disabled');
    }
}
function setGroupVisible(el,visible){
    el.style.display = visible ? 'flex' : 'none';
}

/* ---------- PLOT RENDERING ---------- */
function getSelections(){
    const view = document.querySelector('input[name="view-selector"]:checked').value;  // full / sub
    const K = Number(document.querySelector('input[name="k-selector"]:checked').value);
    const subbandType = document.querySelector('input[name="subband-type"]:checked').value; // full/positive/negative
    const branch = document.querySelector('input[name="branch-selector"]:checked').value; // all/P/Q/R
    return {view,K,subbandType,branch};
}

function buildTraces(sel){
    if(sel.view==='full'){
        const xs = allLines.map(l=>l.x);
        const ys = allLines.map(l=>l.y);
        return [{
            type:'bar',
            x:xs,
            y:ys,
            width:0.3,
            marker:{color:'black'}
        }];
    }
    // sub-band view
    const filterK = l=> l.K===sel.K;
    const filterBranch = l=> sel.branch==='all' || l.branch===sel.branch;
    const traces=[];
    if(sel.subbandType==='full'){
        const posLines = allLines.filter(l=>filterK(l)&&l.subband==='positive');
        const negLines = allLines.filter(l=>filterK(l)&&l.subband==='negative');
        traces.push({
            type:'bar',
            x:posLines.map(l=>l.x),
            y:posLines.map(l=>l.y),
            width:0.3,
            marker:{color:'red'}
        });
        traces.push({
            type:'bar',
            x:negLines.map(l=>l.x),
            y:negLines.map(l=>l.y),
            width:0.3,
            marker:{color:'blue'}
        });
    }else{
        const lines = allLines.filter(l=> filterK(l)&& l.subband===sel.subbandType && filterBranch(l));
        const color = sel.subbandType==='positive'?'red':'blue';
        traces.push({
            type:'bar',
            x:lines.map(l=>l.x),
            y:lines.map(l=>l.y),
            width:0.3,
            marker:{color}
        });
    }
    return traces;
}

function makeTitle(sel){
    if(sel.view==='full') return 'Rovibronic Spectrum';
    let txt = `K = ${sel.K} sub-band`;
    if(sel.subbandType==='full') return txt;
    txt += `<br>"${sel.subbandType}" sub-band`;
    if(sel.branch!=='all') txt += ` – ${sel.branch} branch`;
    return txt;
}

function updatePlot(){
    const sel = getSelections();
    const traces = buildTraces(sel);
    const layout = {
        title:{text:makeTitle(sel)},
        xaxis:{title:'wavenumber (cm⁻¹)', range:[Number(lowerSlider.value),Number(upperSlider.value)]},
        yaxis:{title:'absorbance', range:[0,1.2]},
        showlegend:false,
        bargap:0
    };
    if(!window.initialPlotDone){
        Plotly.newPlot('plot-div',traces,layout,{responsive:true});
        window.initialPlotDone=true;
    }else{
        Plotly.react('plot-div',traces,layout,{responsive:true});
    }
}

/* ---------- UI LOGIC ---------- */
function handleViewChange(){
    const subView = byId('radio-view-sub').checked;
    setGroupEnabled(byId('k-selector-group'),subView);
    setGroupEnabled(byId('subband-type-group'),subView);
    if(!subView){
        setGroupVisible(byId('branch-selector-group'),false);
    }else{
        handleSubbandTypeChange(); // decide branch visibility
        handleKChange(); // ensure negative option availability
    }
    updatePlot();
}

function handleKChange(){
    const K = Number(document.querySelector('input[name="k-selector"]:checked').value);
    const negRadio = byId('radio-subband-negative');
    if(K===0){
        negRadio.disabled=true;
        if(negRadio.checked){
            byId('radio-subband-full').checked=true;
        }
    }else{
        negRadio.disabled=false;
    }
    updatePlot();
}

function handleSubbandTypeChange(){
    const type = document.querySelector('input[name="subband-type"]:checked').value;
    const branchGroup = byId('branch-selector-group');
    if(type==='positive' || type==='negative'){
        setGroupVisible(branchGroup,true);
        setGroupEnabled(branchGroup,true);
    }else{
        setGroupVisible(branchGroup,false);
    }
    updatePlot();
}

function handleBranchChange(){
    updatePlot();
}

function handleZoomChange(e){
    let low = Number(lowerSlider.value);
    let high = Number(upperSlider.value);
    // maintain 1 cm-1 gap
    if(low>=high){
        if(e.target===lowerSlider){
            high = low+1;
            upperSlider.value = high;
        }else{
            low = high-1;
            lowerSlider.value = low;
        }
    }
    lowerSpan.textContent = low + '.';
    upperSpan.textContent = high + '.';
    Plotly.relayout('plot-div',{'xaxis.range':[low,high]});
}

/* ---------- INITIALISATION ---------- */
window.addEventListener('DOMContentLoaded',()=>{
    allLines = generateData();
    /* add listeners */
    document.querySelectorAll('input[name="view-selector"]').forEach(el=>el.addEventListener('change',handleViewChange));
    document.querySelectorAll('input[name="k-selector"]').forEach(el=>el.addEventListener('change',handleKChange));
    document.querySelectorAll('input[name="subband-type"]').forEach(el=>el.addEventListener('change',handleSubbandTypeChange));
    document.querySelectorAll('input[name="branch-selector"]').forEach(el=>el.addEventListener('change',handleBranchChange));
    lowerSlider.addEventListener('input',handleZoomChange);
    upperSlider.addEventListener('input',handleZoomChange);

    // first plot
    updatePlot();
});
</script>
</body>
</html>
