<script type="module">
import * as THREE from 'three';
import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
import { ParametricGeometry } from 'three/addons/geometries/ParametricGeometry.js';

/* --------------------------------------------------------------------
   Global variables & initial state
-------------------------------------------------------------------- */
let currentWaveType = 'P';
const container = document.getElementById('canvas-container');

const scene    = new THREE.Scene();
scene.background = new THREE.Color('#f0f0f0');

const camera   = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 100);
camera.position.set(4,4,4);

const renderer = new THREE.WebGLRenderer({antialias:true});
renderer.setSize(container.clientWidth, container.clientHeight);
container.appendChild(renderer.domElement);

const controls = new OrbitControls(camera, renderer.domElement);
controls.enableDamping = true;

/* --------------------------------------------------------------------
   Lights
-------------------------------------------------------------------- */
scene.add( new THREE.AmbientLight(0xffffff, .5) );
const dir1 = new THREE.DirectionalLight(0xffffff, .8);
dir1.position.set(5,5,5);
scene.add(dir1);
const dir2 = new THREE.DirectionalLight(0xffffff, .8);
dir2.position.set(-5,-3,4);
scene.add(dir2);

/* --------------------------------------------------------------------
   Radiation pattern group (will receive rotations)
-------------------------------------------------------------------- */
const patternGroup = new THREE.Object3D();
scene.add(patternGroup);

/* --------------------------------------------------------------------
   Bounding box (initially invisible)
-------------------------------------------------------------------- */
let boxHelper = new THREE.BoxHelper(patternGroup, 0x000000);
boxHelper.visible = false;
scene.add(boxHelper);

/* --------------------------------------------------------------------
   Compass overlay
-------------------------------------------------------------------- */
const compassGroup = new THREE.Object3D();
scene.add(compassGroup);

(function buildCompass(){
    const radius = 1.6;
    // Circle
    const steps = 128;
    const pts = [];
    for(let i=0;i<=steps;i++){
        const a = i/steps * Math.PI*2;
        pts.push( new THREE.Vector3( radius*Math.cos(a), radius*Math.sin(a), 0 ) );
    }
    const circleGeom = new THREE.BufferGeometry().setFromPoints(pts);
    const circleMat  = new THREE.LineDashedMaterial({color:0x333333,dashSize:0.05,gapSize:0.05});
    const circleLine = new THREE.Line(circleGeom, circleMat);
    circleLine.computeLineDistances();
    compassGroup.add(circleLine);

    // Cardinal labels
    const cardinal = [
        {txt:'N', x:0, y: radius+0.15},
        {txt:'S', x:0, y:-radius-0.15},
        {txt:'E', x: radius+0.15, y:0},
        {txt:'W', x:-radius-0.15, y:0}
    ];
    cardinal.forEach(c=>{
        const spr = createTextSprite(c.txt, '#000', 64);
        spr.position.set(c.x, c.y, 0);
        compassGroup.add(spr);
    });
})();

/* --------------------------------------------------------------------
   Up arrow
-------------------------------------------------------------------- */
const arrow = new THREE.ArrowHelper(new THREE.Vector3(0,0,1).normalize(), new THREE.Vector3(0,0,0), 2, 0x000000, 0.08, 0.05);
scene.add(arrow);
const upLabel = createTextSprite('U', '#000', 64);
upLabel.position.set(0,0,2.12);
scene.add(upLabel);

/* --------------------------------------------------------------------
   Functions: create pattern geometry, orientation etc.
-------------------------------------------------------------------- */
// amplitude functions
function radiusP(theta,phi){      return Math.abs( Math.sin(2*theta) * Math.sin(phi) ); }
function radiusSV(theta,phi){     return Math.abs( Math.cos(2*theta) * Math.sin(phi) ); }
function radiusSH(theta,phi){     return Math.abs( Math.cos(theta) * Math.cos(phi) );   }

function getParametricGeometry(waveType, segments=120){
    let rFunc;
    switch(waveType){
        case 'P':  rFunc=radiusP; break;
        case 'SV': rFunc=radiusSV; break;
        case 'SH': rFunc=radiusSH; break;
    }
    const func = (u,v,target)=>{
        const theta = u * Math.PI;          // 0 -> π
        const phi   = v * 2*Math.PI;        // 0 -> 2π
        const r = rFunc(theta,phi) + 0.02;  // avoid r=0 for visibility
        const x = r * Math.sin(theta) * Math.cos(phi);
        const y = r * Math.sin(theta) * Math.sin(phi);
        const z = r * Math.cos(theta);
        target.set(x,y,z);
    };
    return new ParametricGeometry(func, segments, segments);
}

let patternMesh;
generateNewPattern('P');    // initial

function generateNewPattern(waveType){
    // remove old mesh
    if(patternMesh){
        patternGroup.remove(patternMesh);
        patternMesh.geometry.dispose();
    }
    const geom = getParametricGeometry(waveType, 120);
    const mat  = new THREE.MeshStandardMaterial({color:'#D2B48C', side:THREE.DoubleSide});
    patternMesh = new THREE.Mesh(geom, mat);
    patternGroup.add(patternMesh);
    updatePatternOrientation();       // apply existing sliders to new mesh
    boxHelper.update();               // make sure bounding box fits new geometry
}

/* --------------------------------------------------------------------
   Orientation application
-------------------------------------------------------------------- */
function updatePatternOrientation(){
    const strike = THREE.MathUtils.degToRad(+sliderStrike.value);
    const dip    = THREE.MathUtils.degToRad(+sliderDip.value);
    const rake   = THREE.MathUtils.degToRad(+sliderRake.value);

    // Reset rotations
    patternGroup.rotation.set(0,0,0);

    // Apply rotations: strike (Z world), dip (local X), rake (local Y)
    patternGroup.rotateZ( strike );
    patternGroup.rotateX( dip );
    patternGroup.rotateY( rake );

    boxHelper.update();
}

/* --------------------------------------------------------------------
   GUI elements references
-------------------------------------------------------------------- */
const sliderStrike = document.getElementById('slider-strike');
const sliderDip    = document.getElementById('slider-dip');
const sliderRake   = document.getElementById('slider-rake');
const strikeVal    = document.getElementById('strike-value');
const dipVal       = document.getElementById('dip-value');
const rakeVal      = document.getElementById('rake-value');

const btnP  = document.getElementById('btn-p');
const btnSV = document.getElementById('btn-sv');
const btnSH = document.getElementById('btn-sh');

const checkboxBox = document.getElementById('checkbox-box');

/* --------------------------------------------------------------------
   Event listeners
-------------------------------------------------------------------- */
[sliderStrike, sliderDip, sliderRake].forEach(sl=>{
    sl.addEventListener('input', ()=>{
        strikeVal.textContent = sliderStrike.value;
        dipVal.textContent    = sliderDip.value;
        rakeVal.textContent   = sliderRake.value;
        updatePatternOrientation();
    });
});

[btnP,btnSV,btnSH].forEach(btn=>{
    btn.addEventListener('click', ()=>{
        document.querySelector('.button-container button.active')?.classList.remove('active');
        btn.classList.add('active');
        currentWaveType = btn.textContent;   // 'P','SV','SH'
        generateNewPattern(currentWaveType);
    });
});

checkboxBox.addEventListener('change', ()=>{
    boxHelper.visible = checkboxBox.checked;
});

/* --------------------------------------------------------------------
   Text sprite helper
-------------------------------------------------------------------- */
function createTextSprite(message, color='#000', size=128){
    const canvas = document.createElement('canvas');
    canvas.width = canvas.height = size;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = 'rgba(0,0,0,0)';
    ctx.fillRect(0,0,size,size);
    ctx.fillStyle = color;
    ctx.font = `${size*0.6}px Arial`;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(message, size/2, size/2);
    const texture = new THREE.CanvasTexture(canvas);
    texture.minFilter = THREE.LinearFilter;
    const material = new THREE.SpriteMaterial({map:texture, transparent:true});
    const sprite = new THREE.Sprite(material);
    sprite.scale.set(0.3,0.3,0.3);
    return sprite;
}

/* --------------------------------------------------------------------
   Responsive resize
-------------------------------------------------------------------- */
window.addEventListener('resize', ()=>{
    camera.aspect = container.clientWidth / container.clientHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(container.clientWidth, container.clientHeight);
});

/* --------------------------------------------------------------------
   Render loop
-------------------------------------------------------------------- */
function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
}
animate();

/* --------------------------------------------------------------------
   MathJax typeset (render LaTeX)
-------------------------------------------------------------------- */
window.MathJax?.typeset();
</script>
</body>
