            <span id="span-terms-value">10</span>
        </div>
        <!-- Euler transformations -->
        <div>
            <label>repeated Euler transformations:</label>
            <input type="radio" name="euler-transformations" id="radio-euler-0" value="0" checked>
            <label for="radio-euler-0">0</label>
            <input type="radio" name="euler-transformations" id="radio-euler-1" value="1">
            <label for="radio-euler-1">1</label>
            <input type="radio" name="euler-transformations" id="radio-euler-2" value="2">
            <label for="radio-euler-2">2</label>
            <input type="radio" name="euler-transformations" id="radio-euler-3" value="3">
            <label for="radio-euler-3">3</label>
        </div>
        <!-- Series select -->
        <div>
            <label>limit of infinite sequence:</label>
            <input type="radio" name="series-select" id="radio-series-pi" value="pi" checked>
            <label for="radio-series-pi">π</label>
            <input type="radio" name="series-select" id="radio-series-ln2" value="ln2">
            <label for="radio-series-ln2">ln(2)</label>
            <input type="radio" name="series-select" id="radio-series-sqrt2" value="sqrt2">
            <label for="radio-series-sqrt2">√2</label>
