<!-- CDN for p5.js library -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
/*******************************************************************
 * Part 1 –– p5.js  (visual cryptography & rendering)
 *******************************************************************/
let secretDim = 64;           // size of secret binary image
let shareDim  = secretDim*2;  // 2× for 2×2 sub-pixels
let shareG1, shareG2;         // off-screen graphics for the two shares
let offsets   = {x1:0,y1:0,x2:0,y2:0};   // positions controlled by joysticks

function setup(){
    const visArea = document.getElementById("visualization-area");
    let canvas = createCanvas(400,400);
    canvas.parent(visArea);

    generateShares();
    initJoystickState();      // read initial handle positions
}

function draw(){
    background(255);

    const cx = width/2  - shareDim/2;
    const cy = height/2 - shareDim/2;

    // draw shares with current offsets
    image(shareG1, cx + offsets.x1, cy + offsets.y1);
    image(shareG2, cx + offsets.x2, cy + offsets.y2);
}

/* ---- generate the secret smiley + the two noisy shares ---- */
function generateShares(){
    // draw smiley onto small graphics
    let secretG = createGraphics(secretDim, secretDim);
    secretG.background(255);
    secretG.noStroke();

    // face
    secretG.fill(0);
    secretG.ellipse(secretDim/2, secretDim/2, secretDim*0.9);

    // eyes (white)
    secretG.fill(255);
    secretG.ellipse(secretDim*0.35, secretDim*0.35, secretDim*0.15);
    secretG.ellipse(secretDim*0.65, secretDim*0.35, secretDim*0.15);

    // mouth (white arc)
    secretG.noFill();
    secretG.stroke(255);
    secretG.strokeWeight(secretDim*0.05);
    secretG.arc(secretDim/2, secretDim*0.6, secretDim*0.5, secretDim*0.3, 0, PI);

    // prepare share graphics
    shareG1 = createGraphics(shareDim, shareDim);
    shareG2 = createGraphics(shareDim, shareDim);
    shareG1.clear();
    shareG2.clear();

    secretG.loadPixels();

    const patternA = [[1,0],[0,1]];   // diagonal TL->BR
    const patternB = [[0,1],[1,0]];   // diagonal TR->BL
    const blackCol = color(0,0,0,255);
    const transCol = color(0,0,0,0);

    function applyPattern(g, pat, px, py){
        for(let dy=0; dy<2; dy++){
            for(let dx=0; dx<2; dx++){
                const col = pat[dy][dx] ? blackCol : transCol;
                g.set(px+dx, py+dy, col);
            }
        }
    }

    for(let y=0; y<secretDim; y++){
        for(let x=0; x<secretDim; x++){
            const idx = 4*(y*secretDim + x);
            const pixelVal = secretG.pixels[idx];       // red channel suffices
            const isBlack = pixelVal < 128 ? 1 : 0;

            const useA = Math.random() < 0.5;
            const pat1 = useA ? patternA : patternB;
            const pat2 = isBlack ? (useA ? patternB : patternA) : pat1;  // complement if secret pixel is black

            const sx = x*2;
            const sy = y*2;
            applyPattern(shareG1, pat1, sx, sy);
            applyPattern(shareG2, pat2, sx, sy);
        }
    }
    shareG1.updatePixels();
    shareG2.updatePixels();
}


/*******************************************************************
 * Part 2 –– Joystick logic (vanilla JS)
 *******************************************************************/
const joystickData = [
    {containerId:"joystick-share1", handleId:"joystick-handle1", offXKey:"x1", offYKey:"y1", defaultPos:{xRatio:0.75, yRatio:0.25}}, // share 1
    {containerId:"joystick-share2", handleId:"joystick-handle2", offXKey:"x2", offYKey:"y2", defaultPos:{xRatio:0.25, yRatio:0.75}}  // share 2
];

let active = null; // {handleDom, containerDom, dataObj}

joystickData.forEach(setupJoystick);

function setupJoystick(data){
    const container = document.getElementById(data.containerId);
    const handle    = document.getElementById(data.handleId);

    /* mouse / touch event helpers */
    handle.addEventListener('mousedown',startDrag);
    handle.addEventListener('touchstart',startDrag,{passive:false});

    function startDrag(e){
        e.preventDefault();
        active = {handle, container, data};
        moveHandle(e);                                  // update immediately
        document.addEventListener('mousemove',moveHandle);
        document.addEventListener('mouseup',endDrag);
        document.addEventListener('touchmove',moveHandle,{passive:false});
        document.addEventListener('touchend',endDrag);
    }
}

/* drag movement handler */
function moveHandle(e){
    if(!active) return;

    const {handle, container, data} = active;
    const rect   = container.getBoundingClientRect();
    const radius = handle.offsetWidth/2;

    const clientX = (e.touches ? e.touches[0].clientX : e.clientX);
    const clientY = (e.touches ? e.touches[0].clientY : e.clientY);

    // position inside container (center of handle)
    let x = clientX - rect.left;
    let y = clientY - rect.top;

    // clamp so the center stays inside container
    x = Math.max(radius, Math.min(rect.width  - radius, x));
    y = Math.max(radius, Math.min(rect.height - radius, y));

    // place the handle (center-referenced) *********************
    handle.style.left = x + "px";
    handle.style.top  = y + "px";

    // convert to ratio [0,1]
    const ratioX = x / rect.width;
    const ratioY = y / rect.height;

    // map ratio ➜ offset in pixels on canvas
    const maxOffset = shareDim/2;
    offsets[data.offXKey] = (ratioX - 0.5) * 2 * maxOffset;
    offsets[data.offYKey] = (ratioY - 0.5) * 2 * maxOffset;
}

function endDrag(){
    active = null;
    document.removeEventListener('mousemove',moveHandle);
    document.removeEventListener('mouseup',endDrag);
    document.removeEventListener('touchmove',moveHandle);
    document.removeEventListener('touchend',endDrag);
}

/* --------- helper to initialise offsets according to initial handle % ---------- */
function initJoystickState(){
    joystickData.forEach(data=>{
        const container = document.getElementById(data.containerId);
        const handle    = document.getElementById(data.handleId);
        const rect      = container.getBoundingClientRect();
        const radius    = handle.offsetWidth/2;

        // read initial % from inline style (top/left like "75%")
        const leftPerc  = parseFloat(handle.style.left)  || data.defaultPos.xRatio*100;
        const topPerc   = parseFloat(handle.style.top)   || data.defaultPos.yRatio*100;

        // we stored the percentage, convert to px (center)
        const cx = rect.width  * (leftPerc/100);
        const cy = rect.height * (topPerc /100);

        // update to exact px so later computation consistent
        handle.style.left = cx + "px";
        handle.style.top  = cy + "px";

        const ratioX = cx / rect.width;
        const ratioY = cy / rect.height;
        const maxOffset = shareDim/2;
        offsets[data.offXKey] = (ratioX - 0.5) * 2 * maxOffset;
        offsets[data.offYKey] = (ratioY - 0.5) * 2 * maxOffset;
    });
}

/*******************************************************************
 * Part 3 –– Reset button
 *******************************************************************/
document.getElementById("btn-reset").addEventListener("click", resetAll);

function resetAll(){
    joystickData.forEach(data=>{
        const container = document.getElementById(data.containerId);
        const handle    = document.getElementById(data.handleId);
        const rect      = container.getBoundingClientRect();
        const radius    = handle.offsetWidth/2;

        const cx = rect.width  * data.defaultPos.xRatio;
        const cy = rect.height * data.defaultPos.yRatio;

        handle.style.left = cx + "px";
        handle.style.top  = cy + "px";

        const ratioX = cx / rect.width;
        const ratioY = cy / rect.height;
        const maxOffset = shareDim/2;
        offsets[data.offXKey] = (ratioX - 0.5) * 2 * maxOffset;
        offsets[data.offYKey] = (ratioY - 0.5) * 2 * maxOffset;
    });
}
</script>
</body>
</html>
