<title>Circle Inversion Demo</title>
<!-- p5.js CDN -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<style>
    body{
        font-family: Arial, Helvetica, sans-serif;
        background:#f4f4f4;
        margin:0;
        padding:20px;
        display:flex;
        justify-content:center;
    }
    #main-container{
        width:650px;
    }
    /* ---------- Control panel ---------- */
    #control-panel{
        display:flex;
        justify-content:space-between;
        margin-bottom:10px;
        background:#ffffff;
        padding:10px;
        border:1px solid #ccc;
        border-radius:4px;
    }
    #control-panel > div{
        display:flex;
        flex-direction:column;
        align-items:flex-start;
        font-size:14px;
    }
    #control-panel label{
        margin-right:4px;
    }
    #control-panel .radio-group,
    #control-panel .checkbox-group{
        display:flex;
        flex-wrap:wrap;
        gap:4px 10px;
        margin-top:4px;
    }

    /* ---------- Canvas ---------- */
    #canvas-container{
        border:1px solid #999;
        width:550px;
        height:550px;
        margin:0 auto;
    }
    /* remove default number spinner for radio inputs on some browsers */
