<body>
<div id="main-container">
    <!-- ---------------- Control Panel ---------------- -->
    <div id="control-panel">
        <!-- Rings control -->
        <div id="rings-control-group">
            <label>rings</label>
            <div class="radio-group" id="rings-radios">
                <input type="radio" name="rings" value="1" id="rings-1" checked>
                <label for="rings-1">1</label>
                <input type="radio" name="rings" value="2" id="rings-2">
                <label for="rings-2">2</label>
                <input type="radio" name="rings" value="3" id="rings-3">
                <label for="rings-3">3</label>
                <input type="radio" name="rings" value="4" id="rings-4">
                <label for="rings-4">4</label>
                <input type="radio" name="rings" value="5" id="rings-5">
                <label for="rings-5">5</label>
                <input type="radio" name="rings" value="6" id="rings-6">
                <label for="rings-6">6</label>
                <input type="radio" name="rings" value="7" id="rings-7">
                <label for="rings-7">7</label>
            </div>
        </div>

        <!-- Circles per ring control -->
        <div id="circles-per-ring-control-group">
            <label>circles per ring</label>
            <div class="radio-group" id="cpr-radios">
                <input type="radio" name="cpr" value="4" id="cpr-4">
                <label for="cpr-4">4</label>
                <input type="radio" name="cpr" value="6" id="cpr-6">
                <label for="cpr-6">6</label>
                <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                <label for="cpr-8">8</label>
                <input type="radio" name="cpr" value="10" id="cpr-10">
                <label for="cpr-10">10</label>
                <input type="radio" name="cpr" value="12" id="cpr-12">
                <label for="cpr-12">12</label>
                <input type="radio" name="cpr" value="14" id="cpr-14">
                <label for="cpr-14">14</label>
            </div>
        </div>

        <!-- Inverse control -->
        <div id="inverse-control-group">
            <label for="checkbox-inverse">inverse</label>
            <div class="checkbox-group">
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
    </div>

    <!-- ---------------- Canvas Container ---------------- -->
    <div id="canvas-container">
        <!-- p5.js will inject the actual canvas element here -->
    </div>
</div>

<script>
/* -------------------------  State variables ------------------------- */
let rings = 1;                     // current number of rings
let cpr   = 8;                     // circles per ring
let inverseMode = false;           // checkbox state

// Locator state (relative to canvas center)
let locators = [new p5.Vector(-50,0), new p5.Vector(50,0)];
let draggedLocator = -1;           // -1 = none, 0 or 1 otherwise

/* -------------------------  DOM <-> state sync ---------------------- */
function readControls(){
    rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
    cpr   = parseInt(document.querySelector('input[name="cpr"]:checked').value);
    inverseMode = document.getElementById('checkbox-inverse').checked;
}

/* Add listeners to all controls to trigger redraw */
document.querySelectorAll('input[name="rings"]').forEach(el=>{
    el.addEventListener('change', ()=>{readControls(); redraw();});
});
document.querySelectorAll('input[name="cpr"]').forEach(el=>{
    el.addEventListener('change', ()=>{readControls(); redraw();});
});
document.getElementById('checkbox-inverse').addEventListener('change', ()=>{
    readControls();
    draggedLocator = -1;   // reset dragging when toggling mode
    redraw();
});

/* ------------------------------ p5.js ------------------------------- */
let sketch = (p)=>{

    /* -------------------- helper: compute circle set -------------------- */
    function computeCircles(N, M, canvasW){
        /*
            Returns an array of objects:
            {center: p5.Vector, radius: number}
        */
        let circles = [];
        let R_max = canvasW/2 * 0.9;
        let R_c = R_max / (1 + Math.sin(Math.PI/N));
        let r   = R_c * Math.sin(Math.PI/N);
        let k   = (1 - Math.sin(Math.PI/N)) / (1 + Math.sin(Math.PI/N));

        for(let i=0;i<M;i++){
            let centerRad = R_c * Math.pow(k, i);
            let thisR     = r   * Math.pow(k, i);
            for(let j=0;j<N;j++){
                let angle = j * p.TWO_PI / N;
                let cx = centerRad * Math.cos(angle);
                let cy = centerRad * Math.sin(angle);
                circles.push({center: p.createVector(cx,cy), radius:thisR, ring:i});
            }
        }
        return circles;
    }

    /* --------------- helper: draw infinite line (clipped) --------------- */
    function drawInfiniteLine(pt, dir){
        // Draws a long line through 'pt' along direction 'dir'
        let big = 1000;
        let p1 = p5.Vector.add(pt, p5.Vector.mult(dir, big));
        let p2 = p5.Vector.add(pt, p5.Vector.mult(dir, -big));
        p.line(p1.x, p1.y, p2.x, p2.y);
    }

    /* --------------------------- p5 setup -------------------------- */
    p.setup = function(){
        let cnv = p.createCanvas(550,550);
        cnv.parent('canvas-container');
        p.angleMode(p.RADIANS);
        p.noLoop();
    };

    /* ---------------------------- p5 draw -------------------------- */
    p.draw = function(){
        readControls();
        p.background('#C1D4E6');

        // Center coordinate system
        p.push();
        p.translate(p.width/2, p.height/2);

        // Calculate base circles once
        let baseCircles = computeCircles(cpr, rings, p.width);

        if(!inverseMode){
            // ---------- Normal mode ----------
            p.stroke(0);
            p.fill('#8C929D');
            p.strokeWeight(1);

            for(let c of baseCircles){
                p.circle(c.center.x, c.center.y, c.radius*2);
                if(c.ring===0){
                    // guide line for outermost ring
                    p.stroke('#A0A0A0');
                    p.line(0,0,c.center.x,c.center.y);
                    p.stroke(0);
                }
            }
        }else{
            // ---------- Inverse mode ----------
            let C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            let R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;

            /* -- Draw inversion circle & locators -- */
            // locator line
            p.stroke('#FF0000');
            p.strokeWeight(1);
            p.line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            // inversion circle
            p.fill('rgba(255,0,0,0.3)');
            p.stroke(0);
            p.circle(C_inv.x, C_inv.y, R_inv*2);

            // locators
            p.noStroke();
            p.fill('#FF0000');
            for(let idx=0; idx<2; idx++){
                p.circle(locators[idx].x, locators[idx].y, 8);
            }

            /* -- Draw inverted shapes -- */
            p.stroke(0);
            p.fill('rgba(220,100,220,0.5)');
            for(let c of baseCircles){
                let d = p5.Vector.dist(C_inv, c.center);
                // near-tangency threshold
                if(Math.abs(d - c.radius) < 1e-3){
                    // becomes a line
                    let n = p5.Vector.sub(c.center, C_inv).normalize(); // normal vector
                    let s = (R_inv*R_inv) / (2*d); // distance from C_inv to line along n
                    let P = p5.Vector.add(C_inv, p5.Vector.mult(n, s));
                    let dir = p.createVector(-n.y, n.x); // perpendicular direction
                    drawInfiniteLine(P, dir);
                }else{
                    // becomes circle
                    let factor = (R_inv*R_inv) / (d*d - c.radius*c.radius);
                    let C_new = p5.Vector.add(C_inv, p5.Vector.mult(p5.Vector.sub(c.center, C_inv), factor));
                    let r_new = Math.abs(factor) * c.radius;
                    p.circle(C_new.x, C_new.y, r_new*2);
                }
            }
        }

        p.pop(); // restore origin
    };

    /* ------------------------- Interaction -------------------------- */
    p.mousePressed = function(){
        if(!inverseMode) return;
        // Check if mouse within any locator
        let mx = p.mouseX - p.width/2;
        let my = p.mouseY - p.height/2;
        for(let i=0;i<2;i++){
            if(p.dist(mx,my, locators[i].x, locators[i].y) < 6){
                draggedLocator = i;
                break;
            }
        }
    };

    p.mouseDragged = function(){
        if(!inverseMode) return;
        if(draggedLocator>-1){
            locators[draggedLocator].x = p.mouseX - p.width/2;
            locators[draggedLocator].y = p.mouseY - p.height/2;
            p.redraw(); // continuous update
        }
    };

    p.mouseReleased = function(){
        draggedLocator = -1;
    };

}; // end sketch

/* --------------------- Run the p5 instance --------------------- */
new p5(sketch);
</script>
</body>
</html>
