            display:none; /* hidden; p5 will create the real one */
        }

        /*  Formula overlay styles  */
        #formula-strong, #formula-weak {
            position: absolute;
            pointer-events: none; /* allow canvas interaction underneath */
            font-size: 1.1rem;
            white-space: nowrap;
        }

        #formula-strong {
            color: maroon;
        }

        #formula-weak {
            color: green;
        }
    </style>
</head>
<body>
<div id="main">
    <!-- CONTROL PANEL ------------------------------------------------------>
    <div id="control-panel">
        <div class="slider-group">
            <label for="slider-logCa">\(C_a\) (log scale): <span id="value-logCa"></span></label>
            <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
        </div>

        <div class="slider-group">
            <label for="slider-pka">\(pK_a\): <span id="value-pka"></span></label>
            <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
        </div>
    </div>

    <!-- VISUALISATION AREA ------------------------------------------------->
    <div id="viz-container">
        <!-- Placeholder canvas (real canvas created by p5) -->
        <canvas id="plot-canvas"></canvas>

        <!-- Formula overlays -->
        <div id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
        <div id="formula-weak">\( \color{green}{pH = \frac{1}{2}(pK_a - \log C_a)} \)</div>
    </div>
</div>

<script>
    /* --------------------------------------------------------------------
       DOM references & global state
    -------------------------------------------------------------------- */
    const sliderLogCa = document.getElementById('slider-logCa');
    const sliderPka   = document.getElementById('slider-pka');
    const valueLogCa  = document.getElementById('value-logCa');
    const valuePka    = document.getElementById('value-pka');

    // Formula overlay nodes
    const formulaStrong = document.getElementById('formula-strong');
    const formulaWeak   = document.getElementById('formula-weak');

    // Initial displayed numeric values
    valueLogCa.textContent = sliderLogCa.value;
    valuePka.textContent   = sliderPka.value;

    /* --------------------------------------------------------------------
       p5.js sketch (global mode)
    -------------------------------------------------------------------- */
    let padding = 60;             // inner padding for axes
    const xMin = -4, xMax = 7;    // pKa domain
    const yMin = 0,  yMax = 7;    // pH  domain
    let canvas;                   // will hold p5 canvas reference

    function setup() {
        // Create canvas sized to viz-container
        const bounds = document.getElementById('viz-container').getBoundingClientRect();
        canvas = createCanvas(bounds.width, bounds.height);
        canvas.parent('viz-container');
        canvas.canvas.id = 'plot-canvas-real';  // optional id for inspection

        noLoop();  // we'll redraw manually on interaction
        drawScene();   // first draw
    }

    function windowResized() {
        // Adjust canvas when window changes
        const bounds = document.getElementById('viz-container').getBoundingClientRect();
        resizeCanvas(bounds.width, bounds.height);
        drawScene();
    }

    /* --------------------------------------------------------------------
       Coordinate helpers
    -------------------------------------------------------------------- */
    function dataXtoPx(x) {
        return map(x, xMin, xMax, padding, width - padding);
    }

    function dataYtoPx(y) {
        return map(y, yMin, yMax, height - padding, padding); // inverted Y
    }

    /* --------------------------------------------------------------------
       Chemistry maths
    -------------------------------------------------------------------- */
    function strongPH(logCa) {
        return -logCa; // pH = - log10(Ca)
    }

    function weakPH(logCa, pKa) {
        return 0.5 * (pKa - logCa);
    }

    // Exact pH via quadratic (see plan)
    function exactPH(logCa, pKa) {
        const Ca = Math.pow(10, logCa);
        const Ka = Math.pow(10, -pKa);

        // Solve [H]^2 + Ka[H] - Ka*Ca = 0
        const a = 1;
        const b = Ka;
        const c = -Ka * Ca;

        const discriminant = b*b - 4*a*c;
        const H = (-b + Math.sqrt(discriminant)) / (2*a); // positive root
        return -Math.log10(H);
    }

    /* --------------------------------------------------------------------
       Drawing routine
    -------------------------------------------------------------------- */
    function drawScene() {
        clear();
        background(255);

        // Read current slider values
        const logCa = parseFloat(sliderLogCa.value);
        const pKaCurrent = parseFloat(sliderPka.value);

        /* ----------- Draw axes ------------- */
        stroke(60);
        strokeWeight(1);
        // x-axis
        line(dataXtoPx(xMin), dataYtoPx(0), dataXtoPx(xMax), dataYtoPx(0));
        // y-axis
        line(dataXtoPx(0), dataYtoPx(yMin), dataXtoPx(0), dataYtoPx(yMax));

        // Tick marks
        textSize(12);
        textAlign(CENTER, TOP);
        for (let x = xMin; x <= xMax; x += 1) {
            const px = dataXtoPx(x);
            line(px, dataYtoPx(0) - 5, px, dataYtoPx(0) + 5);
            text(x, px, dataYtoPx(0) + 8);
        }

        textAlign(RIGHT, CENTER);
        for (let y = yMin; y <= yMax; y += 1) {
            const py = dataYtoPx(y);
            line(dataXtoPx(0) - 5, py, dataXtoPx(0) + 5, py);
            text(y, dataXtoPx(0) - 8, py);
        }

        /* ----------- Strong acid horizontal line (maroon) ----------- */
        const pHstrong = strongPH(logCa);
        stroke('maroon');
        strokeWeight(2);
        line(dataXtoPx(xMin), dataYtoPx(pHstrong),
             dataXtoPx(xMax), dataYtoPx(pHstrong));

        /* ----------- Weak acid approximation line (green) ----------- */
        stroke('green');
        const pHweakStart = weakPH(logCa, xMin);
        const pHweakEnd   = weakPH(logCa, xMax);
        line(dataXtoPx(xMin), dataYtoPx(pHweakStart),
             dataXtoPx(xMax), dataYtoPx(pHweakEnd));

        /* ----------- Exact pH curve (blue) ----------- */
        stroke('blue');
        strokeWeight(3);
        noFill();
        beginShape();
        for (let x = xMin; x <= xMax; x += 0.05) {
            const pH = exactPH(logCa, x);
            vertex(dataXtoPx(x), dataYtoPx(pH));
        }
        endShape();

        /* ----------- Indicator point (orange) ----------- */
        const pHint = exactPH(logCa, pKaCurrent);
        const px = dataXtoPx(pKaCurrent);
        const py = dataYtoPx(pHint);
        fill('orange');
        stroke(80);
        strokeWeight(1);
        circle(px, py, 10);

        /* ----------- Position overlay formulas ----------- */
        // Strong formula: place near left side of red line
        let sx = dataXtoPx(xMin + 0.5);
        let sy = dataYtoPx(pHstrong);
        formulaStrong.style.left = `${sx}px`;
        formulaStrong.style.top  = `${sy - 18}px`;

        // Weak formula: place near right side of green line
        let wx = dataXtoPx(xMax - 1);
        let wy = dataYtoPx(weakPH(logCa, xMax - 1));
        formulaWeak.style.left = `${wx - 10}px`;
        formulaWeak.style.top  = `${wy - 18}px`;
    }

    /* --------------------------------------------------------------------
       Slider interactivity
    -------------------------------------------------------------------- */
    function onLogCaChange() {
        valueLogCa.textContent = sliderLogCa.value;
        drawScene();
    }

    function onPkaChange() {
        valuePka.textContent = sliderPka.value;
        drawScene(); // only point changes, but simplest is full redraw
    }

    sliderLogCa.addEventListener('input', onLogCaChange);
    sliderPka  .addEventListener('input', onPkaChange);

    /* --------------------------------------------------------------------
       Ensure MathJax renders the static formulas and labels
    -------------------------------------------------------------------- */
    MathJax.typesetPromise();
</script>
</body>
</html>
