const joystickStart   = document.getElementById('joystick-start');
const joystickEnd     = document.getElementById('joystick-end');
const handleStart     = document.getElementById('handle-start');
const handleEnd       = document.getElementById('handle-end');

/* ============== JOYSTICK IMPLEMENTATION ============== */
function setHandlePosition(handle, norm){
    const rect = handle.parentElement.getBoundingClientRect();
    const x = norm.x * rect.width;
    const y = (1 - norm.y) * rect.height; // invert y: top is 1, bottom is 0 in UI
    handle.style.left = `${x}px`;
    handle.style.top  = `${y}px`;
}
['start','end'].forEach(id=>{
    const area = id==='start'?joystickStart:joystickEnd;
    const handle = id==='start'?handleStart:handleEnd;
    let dragging=false;

    handle.addEventListener('mousedown',e=>{dragging=true;e.preventDefault();});
    document.addEventListener('mouseup',()=>{ if(dragging){ dragging=false; triggerComputation(); }});
    area.addEventListener('mousemove',e=>{
        if(!dragging) return;
        const rect = area.getBoundingClientRect();
        let x = (e.clientX - rect.left) / rect.width;
        let y = (e.clientY - rect.top ) / rect.height;
        x = Math.min(1,Math.max(0,x));
        y = Math.min(1,Math.max(0,y));
        if(id==='start'){ startNorm.x = x; startNorm.y = 1 - y; }
        else            { endNorm.x   = x; endNorm.y   = 1 - y; }
        setHandlePosition(handle, id==='start'?startNorm:endNorm);
    });
});
/* initialize handle positions */
window.addEventListener('load',()=>{
    setHandlePosition(handleStart,startNorm);
    setHandlePosition(handleEnd,endNorm);
});

/* ============== PRIORITY QUEUE ============== */
class PriorityQueue{
    constructor(){this.data=[];}
    push(element,priority){
        this.data.push({element,priority});
        this.data.sort((a,b)=>a.priority-b.priority);
    }
    pop(){return this.data.shift().element;}
    isEmpty(){return this.data.length===0;}
}

/* ============== GRID & UTILS ============== */
function normToGrid(norm){ // convert normalized [0,1] to grid integer cell
    return {
        r: Math.max(0, Math.min(ROWS-1, Math.floor(norm.y * ROWS))),
        c: Math.max(0, Math.min(COLS-1, Math.floor(norm.x * COLS)))
    };
}
function key(r,c){return `${r},${c}`;}
function parseKey(k){const [r,c]=k.split(',').map(Number);return {r,c};}

/* obstacles presets return Set of key strings */
function getObstacles(value){
    const obs=new Set();
    switch(value){
        case '1-square':
            obs.add(key(Math.floor(ROWS/2), Math.floor(COLS/2)));
            break;
        case '2-squares':
            obs.add(key(Math.floor(ROWS*0.3), Math.floor(COLS*0.3)));
            obs.add(key(Math.floor(ROWS*0.7), Math.floor(COLS*0.7)));
            break;
        case 'l-shaped-wall':
            for(let r=5;r<20;r++) obs.add(key(r,5));
            for(let c=5;c<20;c++) obs.add(key(5,c));
            break;
    }
    return obs;
}

/* ============== ALGORITHMS ============== */
function heuristic(a,b){ // Manhattan distance
    return Math.abs(a.r-b.r)+Math.abs(a.c-b.c);
}
function neighbours(node){
    const dirs=[
        {dr:-1,dc:0,cost:CARD_COST},{dr:1,dc:0,cost:CARD_COST},
        {dr:0,dc:-1,cost:CARD_COST},{dr:0,dc:1,cost:CARD_COST},
        {dr:-1,dc:-1,cost:DIAG_COST},{dr:-1,dc:1,cost:DIAG_COST},
        {dr:1,dc:-1,cost:DIAG_COST},{dr:1,dc:1,cost:DIAG_COST}
    ];
    const out=[];
    for(const d of dirs){
        const nr=node.r+d.dr,nc=node.c+d.dc;
        if(nr>=0&&nr<ROWS&&nc>=0&&nc<COLS) out.push({r:nr,c:nc,cost:d.cost});
    }
    return out;
}
function runAlgorithm(){
    history=[]; finalPath=[];
    const start=normToGrid(startNorm);
    const goal =normToGrid(endNorm);
    const obstacles = getObstacles(selectObstacles.value);
    // Edge case: ensure start/end not obstacle
    obstacles.delete(key(start.r,start.c));
    obstacles.delete(key(goal.r,goal.c));

    const openPQ=new PriorityQueue();
    openPQ.push(start,0);
    const gScore=new Map(); gScore.set(key(start.r,start.c),0);
    const cameFrom=new Map();
    const openSet = new Set([key(start.r,start.c)]);
    const closedSet=new Set();

    const useAStar=radioAStar.checked;

    while(!openPQ.isEmpty()){
        const current=openPQ.pop();
        openSet.delete(key(current.r,current.c));
        closedSet.add(key(current.r,current.c));

        // record frame
        history.push({
            open:new Set(openSet),
            closed:new Set(closedSet),
            current:key(current.r,current.c)
        });

        if(current.r===goal.r&&current.c===goal.c){
            // reconstruct path
            let curKey=key(goal.r,goal.c);
            finalPath.push(parseKey(curKey));
            while(cameFrom.has(curKey)){
                curKey=cameFrom.get(curKey);
                finalPath.push(parseKey(curKey));
            }
            finalPath.reverse(); // from start to goal
            // push final frame (duplicates last closed/open but holds path)
            history.push({
                open:new Set(openSet),
                closed:new Set(closedSet),
                current:key(current.r,current.c),
                path:[...finalPath] // deep copy
            });
            return;
        }
        for(const nb of neighbours(current)){
            const nbKey=key(nb.r,nb.c);
            if(obstacles.has(nbKey) || closedSet.has(nbKey)) continue;
            const tentativeG = gScore.get(key(current.r,current.c))+nb.cost;
            if(!gScore.has(nbKey) || tentativeG<gScore.get(nbKey)){
                cameFrom.set(nbKey,key(current.r,current.c));
                gScore.set(nbKey,tentativeG);
                const fScore = tentativeG + (useAStar?heuristic(nb,goal):0);
                if(!openSet.has(nbKey)){
                    openSet.add(nbKey);
                    openPQ.push({r:nb.r,c:nb.c},fScore);
                }
            }
        }
    }
    // No path found: still set slider etc.
}

/* ============== TRIGGER COMPUTATION & SLIDER HANDLING ============== */
function triggerComputation(){
    runAlgorithm();
    sliderStep.max = history.length;
    sliderStep.value = 1;
    labelStep.textContent = '+ 1';
    if(p5instance) p5instance.redraw();
}
/* listeners that trigger full computation */
[radioDijkstra,radioAStar,selectObstacles].forEach(el=>el.addEventListener('change',triggerComputation));

sliderStep.addEventListener('input',()=>{
    labelStep.textContent = '+ ' + sliderStep.value;
    if(p5instance) p5instance.redraw();
});

/* ============== P5 RENDERING ============== */
const sketch = (p)=>{
    p.setup = ()=>{
        const container=document.getElementById('canvas-container');
        gridSizePx = Math.min(container.clientWidth, container.clientHeight)-40;
        if(gridSizePx<300) gridSizePx=300;
        cellSize = gridSizePx / ROWS;
        p.createCanvas(gridSizePx,gridSizePx).parent(container);
        p.noLoop();
        triggerComputation(); // initial compute & first draw
    };
    p.redraw = ()=>{
        p.clear();
        const stepIdx = sliderStep.value-1;
        const frame = history[stepIdx] || {};
        const obstacles = getObstacles(selectObstacles.value);
        const start=normToGrid(startNorm);
        const goal =normToGrid(endNorm);

        // draw cells
        for(let r=0;r<ROWS;r++){
            for(let c=0;c<COLS;c++){
                let col = p.color(255); // empty white
                const k=key(r,c);
                if(obstacles.has(k)) col = p.color(0);
                if(frame.open && frame.open.has(k)) col = p.color('#BDE0FE');
                if(frame.closed && frame.closed.has(k)) col = p.color('#0000CD');
                p.fill(col);
                p.noStroke();
                p.rect(c*cellSize, r*cellSize, cellSize, cellSize);
            }
        }
        // draw start & goal
        p.fill('#34A853');
        p.rect(start.c*cellSize, start.r*cellSize, cellSize, cellSize);
        p.rect(goal.c*cellSize, goal.r*cellSize, cellSize, cellSize);

        // draw path if final frame
        if(frame.path){
            p.stroke('#ff0000');
            p.strokeWeight(3);
            p.noFill();
            p.beginShape();
            frame.path.forEach(pt=>{
                p.vertex(pt.c*cellSize+cellSize/2, pt.r*cellSize+cellSize/2);
            });
            p.endShape();
        }
        // grid overlay
        p.stroke(200);
        p.strokeWeight(1);
        for(let r=0;r<=ROWS;r++){
            p.line(0,r*cellSize,gridSizePx,r*cellSize);
        }
        for(let c=0;c<=COLS;c++){
            p.line(c*cellSize,0,c*cellSize,gridSizePx);
        }
    };
};
p5instance = new p5(sketch);
</script>
</body>
</html>
