</div>

<script>
/* ---------- GLOBAL STATE ---------- */
let Q = 2.0;                 // source charge
let q = -0.0845;             // test charge
const k = 1;                 // Coulomb constant (unitless here)

let testPos;                 // p5.Vector for locator position in meters
let dragging = false;

const scaleMetersToPixels = 40;  // 1 m = 40 px
const vectorScale = 1000;        // common multiplier for visualising E and F

/* ---------- DOM ELEMENTS ---------- */
let srcSlider, testSlider;
let srcLabel, testLabel;

function setup(){
    const canvas = createCanvas(600,600);
    canvas.parent('canvas-container');

    // initial locator position (meters)
    testPos = createVector(2,3);

    /* select sliders and labels */
    srcSlider  = select('#slider-source-charge');
    testSlider = select('#slider-test-charge');
    srcLabel   = select('#label-source-charge');
    testLabel  = select('#label-test-charge');

    srcSlider.input(()=>{ Q = parseFloat(srcSlider.value()); srcLabel.html(Q.toFixed(1)); });
    testSlider.input(()=>{ q = parseFloat(testSlider.value()); testLabel.html(q.toFixed(4)); });

    // text rendering settings
    textFont('Arial');
    textSize(12);
}

function draw(){
    background(255);

    translate(width/2, height/2);      // move origin to centre
    scale(1,-1);                       // invert y-axis to match math coords

    drawGrid();
    drawAxes();

    // draw source charge
    noStroke();
    fill(getChargeColor(Q));
    circle(0,0,16);

    // draw test charge locator
    fill(0);
    circle(testPos.x*scaleMetersToPixels, testPos.y*scaleMetersToPixels, 12);

    // physics calculations
    const rVec   = p5.Vector.sub(testPos, createVector(0,0));
    const r      = rVec.mag();
    let EVec     = createVector(0,0);
    let FVec     = createVector(0,0);

    if(r > 1e-6){
        EVec = rVec.copy().normalize().mult(k * Q / (r*r));   // N/C
        FVec = EVec.copy().mult(q);                           // N
    }

    // draw Electric Field arrow (red)
    push();
    strokeWeight(2);
    drawArrow(testPos.copy().mult(scaleMetersToPixels), EVec.copy().mult(vectorScale), color(255,0,0));
    pop();

    // draw Electric Force arrow (blue) only if q ≠ 0
    if(Math.abs(q) > 1e-8){
        push();
        strokeWeight(2);
        drawArrow(testPos.copy().mult(scaleMetersToPixels), FVec.copy().mult(vectorScale), color(0,102,255));
        pop();
    }

    // ---------- DOM updates ----------
    updateInfoDisplay(EVec, FVec, rVec);
}

/* ---------- MOUSE INTERACTION FOR DRAGGING LOCATOR ---------- */
function mousePressed(){
    // transform mouse pixel to logical meters
    const lx = (mouseX - width/2) / scaleMetersToPixels;
    const ly = (height/2 - mouseY) / scaleMetersToPixels;
    const d  = dist(lx, ly, testPos.x, testPos.y);
    if(d < 0.3){   // within 0.3 m radius
        dragging = true;
    }
}

function mouseDragged(){
    if(dragging){
        const lx = (mouseX - width/2) / scaleMetersToPixels;
        const ly = (height/2 - mouseY) / scaleMetersToPixels;
        testPos.set(lx, ly);
    }
}

function mouseReleased(){
    dragging = false;
}

/* ---------- DRAW UTILITY FUNCTIONS ---------- */
function drawArrow(baseVec, vec, myColor){
    push();
    stroke(myColor);
    fill(myColor);
    const base = createVector(baseVec.x, baseVec.y);
    const tip  = p5.Vector.add(base, vec);
    line(base.x, base.y, tip.x, tip.y);

    // arrowhead
    const arrowSize = 8;
    const dir = vec.copy().normalize();
    const left = p5.Vector.add(tip, p5.Vector.mult(dir.copy().rotate(PI*5/6), arrowSize));
    const right = p5.Vector.add(tip, p5.Vector.mult(dir.copy().rotate(-PI*5/6), arrowSize));
    triangle(tip.x, tip.y, left.x, left.y, right.x, right.y);
    pop();
}

function drawGrid(){
    strokeWeight(1);
    stroke(varCSS('--grid'));
    const maxUnits = 10;
    for(let x=-maxUnits; x<=maxUnits; x++){
        const px = x*scaleMetersToPixels;
        line(px, -height, px, height);
    }
    for(let y=-maxUnits; y<=maxUnits; y++){
        const py = y*scaleMetersToPixels;
        line(-width, py, width, py);
    }
}

function drawAxes(){
    stroke(0);
    strokeWeight(1.5);
    // x-axis
    line(-width/2, 0, width/2, 0);
    // y-axis
    line(0, -height/2, 0, height/2);

    // labels (switch back to screen coords)
    push();
    scale(1,-1);
    fill(0);
    noStroke();
    text('x', width/2 - 15, -4);
    text('y', 4, -height/2 + 15);
    pop();
}

function varCSS(variable){
    return getComputedStyle(document.documentElement).getPropertyValue(variable);
}

function getChargeColor(val){
    return val >= 0 ? color(0,102,255) : color(255,102,0);
}

/* ---------- INFO PANEL UPDATE ---------- */
function updateInfoDisplay(E, F, rVec){
    // magnitudes
    const Emag = E.mag();
    const Fmag = F.mag();

    document.getElementById('display-E-mag').innerHTML =
        `|E| = ${Emag.toFixed(3)} N/C`;
    document.getElementById('display-Ex').innerHTML =
        `E<sub>x</sub> component = ${E.x.toFixed(3)} N/C`;
    document.getElementById('display-Ey').innerHTML =
        `E<sub>y</sub> component = ${E.y.toFixed(3)} N/C`;
    document.getElementById('display-x-pos').innerHTML =
        `x position = ${testPos.x.toFixed(2)} m`;
    document.getElementById('display-y-pos').innerHTML =
        `y position = ${testPos.y.toFixed(2)} m`;
    document.getElementById('display-force').innerHTML =
        `Electric force = ${Fmag.toFixed(3)} N`;
}
</script>
</body>
</html>
