    <script>
        /* ------------------  DOM REFERENCES  ------------------ */
        const cbHertz     = document.getElementById('checkbox-hertz');
        const cbOverlap   = document.getElementById('checkbox-overlap');
        const cbGaussian  = document.getElementById('checkbox-gaussian');

        const btnLinLin   = document.getElementById('btn-linear-linear');
        const btnLogLin   = document.getElementById('btn-log-linear');
        const btnLogLog   = document.getElementById('btn-log-log');
        const scaleButtons = [btnLinLin, btnLogLin, btnLogLog];

        const sliderR     = document.getElementById('slider-r');
        const sliderK     = document.getElementById('slider-k');
        const spanR       = document.getElementById('span-r-value');
        const spanK       = document.getElementById('span-k-value');

        /* ------------------  UTILITY FUNCTIONS  ------------------ */
        function linspace(start, stop, num){
            const arr = [];
            const step = (stop - start)/(num-1);
            for(let i=0;i<num;i++) arr.push(start + i*step);
            return arr;
        }

        // ---------- Real-space functions ----------
        function f_Hertz(r){
            return r<=1 ? Math.pow(1 - r*r, 2.5) : 0;
        }
        function f_Overlap(r){
            return r<=1 ? Math.pow(1 - r, 2) : 0;
        }
        function f_Gaussian(r){
            return Math.exp(-Math.PI*r*r);
        }

        // ---------- Fourier transforms ----------
        function F_Hertz(k){
            if(k===0) return 4*Math.PI/ (1); // limit value computed numerically ~4π
            return 15*Math.PI*(((3 - k*k)*Math.sin(k) - 3*k*Math.cos(k)))/(Math.pow(k,5));
        }
        function F_Overlap(k){
            if(k===0) return 16*Math.PI/ (3); // limit value
            return 8*Math.PI*(2 - k*k - 2*Math.cos(k) - 2*k*Math.sin(k))/Math.pow(k,4);
        }
        function F_Gaussian(k){
            return (1/Math.sqrt(Math.PI))*Math.exp(-k*k/(4*Math.PI));
        }

        // remove non-positive values for log plotting
        function sanitizeForLog(arr){
            return arr.map(v => (v>0 ? v : null));
        }

        /* ------------------  PLOTTING  ------------------ */
        function init(){
            // initial empty plots
            const layoutLeft = {
                title:{text:'function', font:{color:'#aaa'}},
                xaxis:{title:'r', range:[0, Number(sliderR.value)]},
                yaxis:{title:'f(r)'},
                showlegend:false,
                margin:{l:60,r:10,t:30,b:50},
            };
            const layoutRight = {
                title:{text:'3D Fourier transform (real part)', font:{color:'#aaa'}},
                xaxis:{title:'k', range:[0, Number(sliderK.value)]},
                yaxis:{title:'F(k)'},
                showlegend:false,
                margin:{l:60,r:10,t:30,b:50},
            };
            Plotly.newPlot('plot-left', [], layoutLeft, {responsive:true});
            Plotly.newPlot('plot-right', [], layoutRight, {responsive:true});

            updatePlots();
        }

        function updatePlots(){
            // 1. States
            const showH = cbHertz.checked;
            const showO = cbOverlap.checked;
            const showG = cbGaussian.checked;

            let yScaleType = 'linear';
            if(btnLinLin.classList.contains('active')) yScaleType = 'linear';
            else yScaleType = 'log';

            const rMax = Number(sliderR.value);
            const kMax = Number(sliderK.value);

            spanR.textContent = rMax.toFixed(1);
            spanK.textContent = kMax.toFixed(2);

            /* ---- LEFT PLOT ---- */
            const rVals = linspace(0, rMax, 200);
            const tracesLeft = [];

            if(showH){
                tracesLeft.push({
                    x:rVals,
                    y:rVals.map(f_Hertz),
                    mode:'lines',
                    line:{color:'#ff0000'},
                    hovertemplate:`Hertz<br>\\(f(r)=(1-r^{2})^{5/2},\\; r\\le 1\\)<extra></extra>`
                });
            }
            if(showO){
                tracesLeft.push({
                    x:rVals,
                    y:rVals.map(f_Overlap),
                    mode:'lines',
                    line:{color:'#000000'},
                    hovertemplate:`overlap<br>\\(f(r)=(1-r)^{2},\\; r\\le 1\\)<extra></extra>`
                });
            }
            if(showG){
                tracesLeft.push({
                    x:rVals,
                    y:rVals.map(f_Gaussian),
                    mode:'lines',
                    line:{color:'#0000ff'},
                    hovertemplate:`Gaussian<br>\\(f(r)=e^{-\\pi r^{2}}\\)<extra></extra>`
                });
            }

            const layoutLeft = {
                xaxis:{title:'r', range:[0,rMax]},
                yaxis:{title:'f(r)'},
            };

            Plotly.react('plot-left', tracesLeft, layoutLeft);

            /* ---- RIGHT PLOT ---- */
            const kVals = linspace(0, kMax, 200);
            const tracesRight = [];

            if(showH){
                let y = kVals.map(F_Hertz);
                if(yScaleType==='log') y = sanitizeForLog(y);
                tracesRight.push({
                    x:kVals,
                    y:y,
                    mode:'lines',
                    line:{color:'#ff0000'},
                    hovertemplate:`Hertz<br>\\(F(k)=15\\pi\\,\\frac{(3-k^{2})\\sin k-3k\\cos k}{k^{5}}\\)<extra></extra>`
                });
            }
            if(showO){
                let y = kVals.map(F_Overlap);
                if(yScaleType==='log') y = sanitizeForLog(y);
                tracesRight.push({
                    x:kVals,
                    y:y,
                    mode:'lines',
                    line:{color:'#000000'},
                    hovertemplate:`overlap<br>\\(F(k)=8\\pi\\,\\frac{2-k^{2}-2\\cos k-2k\\sin k}{k^{4}}\\)<extra></extra>`
                });
            }
            if(showG){
                let y = kVals.map(F_Gaussian);
                if(yScaleType==='log') y = sanitizeForLog(y);
                tracesRight.push({
                    x:kVals,
                    y:y,
                    mode:'lines',
                    line:{color:'#0000ff'},
                    hovertemplate:`Gaussian<br>\\(F(k)=\\frac{1}{\\sqrt{\\pi}}\\,e^{-k^{2}/(4\\pi)}\\)<extra></extra>`
                });
            }

            const layoutRight = {
                xaxis:{title:'k', range:[0,kMax]},
                yaxis:{title:'F(k)', type:yScaleType},
            };
            Plotly.react('plot-right', tracesRight, layoutRight);

            // re-typeset MathJax for new hover labels
            if(window.MathJax){
                MathJax.typesetPromise();
            }
        }

        /* -------------------  EVENT LISTENERS  ------------------- */
        [cbHertz, cbOverlap, cbGaussian].forEach(cb => cb.addEventListener('change', updatePlots));

        scaleButtons.forEach(btn=>{
            btn.addEventListener('click', ()=>{
                scaleButtons.forEach(b=>b.classList.remove('active'));
                btn.classList.add('active');
                updatePlots();
            });
        });

        sliderR.addEventListener('input', updatePlots);
        sliderK.addEventListener('input', updatePlots);

        // init on load
        window.addEventListener('load', init);
    </script>
</body>
</html>
