    <!-- ----------------- VISUALIZATION PANEL --------------------------- -->
    <div id="vis-panel">
        <div id="climate-neutral-buttons">
            <button id="btn-neutral-now">now</button>
            <button id="btn-neutral-2030">2030</button>
            <button id="btn-neutral-2040" class="active">2040</button>
            <button id="btn-neutral-2050">2050</button>
            <button id="btn-neutral-never">never</button>
        </div>
        <div id="main-plot-container"></div>
    </div><!-- /vis-panel -->
</div><!-- /app-container -->

<script>
/* ---------------  GLOBAL STATE & HELPERS -------------------------- */
const YEARS = [];
for(let y=2020;y<=2050;y++) YEARS.push(y);

const state = {
    scale: 'individual',
    population: 250,            // k
    type: 'emissions',
    initial_emissions: 14,      // tons/person
    reduction_half_time: 20,    // years
    annual_budget: 1.5,         // tons/person
    start_year: 2020,
    cost_per_ton: 15,           // $
    fractional_comp_pct: 50,    // %
    constant_comp_pct: 25,      // %
    neutral_year: 2040,         // 2020/2030/2040/2050 or null
    // arrays to be filled
    real: [],
    compensated: [],
    effective: [],
    cumulative: [],
    annual_costs: [],
    total_costs: 0
};

/* ---------------  DOM SHORTCUTS ----------------------------------- */
function $(id){return document.getElementById(id);}

/* ---------------  EVENT REGISTRATION ------------------------------ */
document.querySelectorAll('#control-panel input, #control-panel input[type=range]').forEach(el=>{
    el.addEventListener('input', ()=>{syncStateFromDOM(); update();});
    el.addEventListener('change', ()=>{syncStateFromDOM(); update();});
});
document.querySelectorAll('#scale-switch input').forEach(el=>{
    el.addEventListener('change',handleScaleSwitch);
});
document.querySelectorAll('#type-switch input').forEach(el=>{
    el.addEventListener('change',handleTypeSwitch);
});
document.querySelectorAll('#climate-neutral-buttons button').forEach(btn=>{
    btn.addEventListener('click',handleNeutralButton);
});

/* ---------------  EVENT HANDLERS ---------------------------------- */
function handleScaleSwitch(){
    if($('scale-individual').checked){
        $('slider-population').disabled=true;
    }else{
        $('slider-population').disabled=false;
    }
}
function handleTypeSwitch(){
    // Nothing specific besides update for now
}
function handleNeutralButton(e){
    document.querySelectorAll('#climate-neutral-buttons button').forEach(b=>b.classList.remove('active'));
    e.target.classList.add('active');
    const id = e.target.id;
    if(id==='btn-neutral-now') state.neutral_year = 2020;
    else if(id==='btn-neutral-2030') state.neutral_year = 2030;
    else if(id==='btn-neutral-2040') state.neutral_year = 2040;
    else if(id==='btn-neutral-2050') state.neutral_year = 2050;
    else if(id==='btn-neutral-never') state.neutral_year = null;
    update();
}

/* ---------------  SYNC STATE FROM DOM ----------------------------- */
function syncStateFromDOM(){
    state.scale = $('scale-individual').checked ? 'individual':'population';
    state.population = parseFloat($('slider-population').value);
    state.type = $('type-emissions').checked ? 'emissions':'costs';
    state.initial_emissions = parseFloat($('slider-annual-emissions').value);
    state.reduction_half_time = parseFloat($('slider-reduction-time').value);
    state.annual_budget = parseFloat($('slider-annual-budget').value);
    state.start_year = parseInt($('slider-start-year').value);
    state.cost_per_ton = parseFloat($('slider-compensation-cost').value);
    state.fractional_comp_pct = parseFloat($('slider-fractional-compensation').value);
    state.constant_comp_pct = parseFloat($('slider-constant-compensation').value);
    // update spans
    $('value-population').textContent = state.population;
    $('value-annual-emissions').textContent = state.initial_emissions;
    $('value-reduction-time').textContent = state.reduction_half_time;
    $('value-annual-budget').textContent = state.annual_budget;
    $('value-start-year').textContent = state.start_year;
    $('value-compensation-cost').textContent = state.cost_per_ton;
    $('value-fractional-compensation').textContent = state.fractional_comp_pct;
    $('value-constant-compensation').textContent = state.constant_comp_pct;
}

/* ---------------  MAIN CALCULATION -------------------------------- */
function recalculate(){
    state.real = [];
    state.compensated = [];
    state.effective = [];
    state.cumulative = [];
    state.annual_costs = [];
    let cum = 0;
    YEARS.forEach((t,index)=>{
        let real;
        if(t < state.start_year) real = state.initial_emissions;
        else{
            real = state.initial_emissions * Math.pow(0.5,(t - state.start_year)/state.reduction_half_time);
        }
        const fractional = real * (state.fractional_comp_pct/100);
        const constant  = state.initial_emissions * (state.constant_comp_pct/100);
        const comp = fractional + constant;
        const eff = real - comp;
        cum += eff;
        const cost = comp * state.cost_per_ton;

        state.real.push(real);
        state.compensated.push(comp);
        state.effective.push(eff);
        state.cumulative.push(cum);
        state.annual_costs.push(cost);
    });
    state.total_costs = state.annual_costs.reduce((a,b)=>a+b,0);
}

/* ---------------  P5.js  ------------------------------------------ */
let sketch = function(p){
    let margin = {left:60,right:30,top:40,bottom:40};
    p.setup = function(){
        const container = $('main-plot-container');
        const c = p.createCanvas(container.clientWidth, 600);
        c.parent('main-plot-container');
        p.noLoop();
        p.textFont('Arial',12);
    };
    p.windowResized = function(){
        const container = $('main-plot-container');
        p.resizeCanvas(container.clientWidth,600);
        redrawAll();
    };
    function getYScale(values,plotHeight){
        const minVal = Math.min(0, ...values);
        const maxVal = Math.max(...values);
        const range = maxVal - minVal;
        return function(v){
            return margin.top + (maxVal - v) / range * plotHeight;
        };
    }
    p.draw = function(){
        p.clear();
        const plotHeight = p.height*0.65 - margin.top - margin.bottom;
        const circleAreaTop = p.height*0.7;
        const plotWidth = p.width - margin.left - margin.right;

        /* ------ coords helpers ------- */
        const xForYear = y=>margin.left + (y-2020)/30*plotWidth;
        const yScale = (state.type==='costs') 
            ? getYScale(state.annual_costs,plotHeight)
            : getYScale([...state.real,...state.effective],plotHeight);

        /* ------ axes and grid -------- */
        p.stroke(180);p.strokeWeight(1);
        // y-axis
        p.line(margin.left, margin.top, margin.left, margin.top+plotHeight);
        // x-axis
        p.line(margin.left, margin.top+plotHeight, margin.left+plotWidth, margin.top+plotHeight);
        // labels
        p.noStroke();p.fill(0);
        p.textAlign(p.CENTER,p.TOP);
        p.text('Year', margin.left+plotWidth/2, margin.top+plotHeight+8);
        p.push();
        p.translate(15, margin.top+plotHeight/2);
        p.rotate(-Math.PI/2);
        const yLabel = (state.type==='costs')
            ? 'annual personal compensation costs ($)'
            : (state.scale==='individual'
                ? 'annual CO₂ emissions per person (tons)'
                : 'annual city emissions of CO₂ (mil tons)');
        p.text(yLabel,0,0);
        p.pop();

        /* ------ PLOT --------------------------------------------------- */
        if(state.type==='emissions'){
            // compensated area (between real & effective)
            p.noStroke();
            p.fill(30,144,255,80);
            p.beginShape();
            YEARS.forEach((yr,i)=>{
                const x = xForYear(yr);
                const y = yScale(state.real[i]);
                p.vertex(x,y);
            });
            for(let i=YEARS.length-1;i>=0;i--){
                const x = xForYear(YEARS[i]);
                const y = yScale(state.effective[i]);
                p.vertex(x,y);
            }
            p.endShape(p.CLOSE);

            // real emissions line
            p.stroke('red');p.noFill();p.strokeWeight(2);
            p.beginShape();
            YEARS.forEach((yr,i)=>{
                p.vertex(xForYear(yr), yScale(state.real[i]));
            });
            p.endShape();

            // effective emissions line
            p.stroke(0);p.strokeWeight(2);
            p.beginShape();
            YEARS.forEach((yr,i)=>{
                p.vertex(xForYear(yr), yScale(state.effective[i]));
            });
            p.endShape();

            // compensated positive/negative fill under effective line
            p.noStroke();
            YEARS.forEach((yr,i)=>{
                const eff = state.effective[i];
                const x = xForYear(yr);
                const yEff = yScale(eff);
                const yZero = yScale(0);
                if(eff>0) p.fill(255,0,0,60);
                else p.fill(0,200,0,60);
                // bar segment
                const nextX = i<YEARS.length-1 ? xForYear(YEARS[i+1]) : x+ (plotWidth/30);
                p.rect(x,Math.min(yEff,yZero), nextX-x, Math.abs(yEff-yZero));
            });

            // neutral year marker
            if(state.neutral_year){
                const xNeutral = xForYear(state.neutral_year);
                p.stroke(0,0,0,120);
                p.drawingContext.setLineDash([6,6]);
                p.line(xNeutral, margin.top, xNeutral, margin.top+plotHeight);
                p.drawingContext.setLineDash([]);
                p.noStroke();p.fill(0);
                p.textAlign(p.CENTER,p.BOTTOM);
                p.text('climate neutral '+state.neutral_year, xNeutral, margin.top+plotHeight-4);

                // cumulative dashed line
                const cumVal = state.cumulative[state.neutral_year-2020];
                const yCum = yScale(cumVal);
                p.stroke(0,0,0,120);
                p.drawingContext.setLineDash([4,4]);
                p.line(margin.left, yCum, margin.left+plotWidth, yCum);
                p.drawingContext.setLineDash([]);
                p.noStroke();p.fill(0);
                p.textAlign(p.LEFT,p.BOTTOM);
                p.text(`CO₂ emissions until ${state.neutral_year} = ${cumVal.toFixed(1)} t`, margin.left+4, yCum-2);
            }

            // cumulative until 2050
            const cum2050 = state.cumulative[state.cumulative.length-1];
            const y2050 = yScale(cum2050);
            p.stroke(0,0,0,120);
            p.drawingContext.setLineDash([4,4]);
            p.line(margin.left, y2050, margin.left+plotWidth, y2050);
            p.drawingContext.setLineDash([]);
            p.noStroke();p.fill(0);
            p.textAlign(p.LEFT,p.BOTTOM);
            p.text(`CO₂ emissions until 2050 = ${cum2050.toFixed(1)} t`, margin.left+4, y2050-2);

        }else{
            /* -------- COSTS PLOT ------------------------------------ */
            p.noFill();p.stroke(0,120,255);p.strokeWeight(2);
            p.beginShape();
            YEARS.forEach((yr,i)=>{
                p.vertex(xForYear(yr), yScale(state.annual_costs[i]));
            });
            p.endShape();

            // fill under line
            p.noStroke();p.fill(30,144,255,60);
            p.beginShape();
            YEARS.forEach((yr,i)=>{
                p.vertex(xForYear(yr), yScale(state.annual_costs[i]));
            });
            p.vertex(xForYear(2050), yScale(0));
            p.vertex(xForYear(2020), yScale(0));
            p.endShape(p.CLOSE);

            // average annual cost dashed line
            const avgAnnual = state.total_costs/31;
            const yAvg = yScale(avgAnnual);
            p.stroke(0,0,0,120);
            p.drawingContext.setLineDash([4,4]);
            p.line(margin.left, yAvg, margin.left+plotWidth, yAvg);
            p.drawingContext.setLineDash([]);
            p.noStroke();p.fill(0);
            p.textAlign(p.LEFT,p.BOTTOM);
            p.text(`average annual cost = $${avgAnnual.toFixed(1)}/yr`, margin.left+4, yAvg-2);

            // total cost label
            p.noStroke();p.fill(0);
            p.textAlign(p.RIGHT,p.TOP);
            p.text(`total personal compensation costs (until 2050) = $${state.total_costs.toFixed(0)}`, margin.left+plotWidth, margin.top+4);
        }

        /* ----------- BUDGET CIRCLES ------------------------------------ */
        const circleYears = [2020,2030,2040,2050];
        const circlesY = circleAreaTop + (p.height-circleAreaTop)/2;
        const maxWidth = p.width - 2*margin.left;
        const spacing = maxWidth / (circleYears.length+1);
        // prepare radius scaling
        const values = circleYears.map(y=>{
            const idx = y-2020;
            const total_budget = 30*state.annual_budget;
            return total_budget - state.cumulative[idx];
        });
        const maxAbsVal = Math.max(...values.map(v=>Math.abs(v))) || 1;
        const k = 50/maxAbsVal; // radius proportional factor so largest ~50px

        circleYears.forEach((yr,i)=>{
            const val = values[i];
            const x = margin.left + spacing*(i+1);
            const radius = Math.sqrt(Math.abs(val))*k;
            if(val>=0){
                p.fill(0,200,0,150);
                p.stroke(0,100,0);
                p.strokeWeight(2);
            }else{
                p.noFill();
                p.stroke(255,0,0);
                p.strokeWeight(2);
                p.drawingContext.setLineDash([6,4]);
            }
            if(radius<5) radius=5;
            p.ellipse(x,circlesY,radius*2,radius*2);
            p.drawingContext.setLineDash([]);
            // text
            p.noStroke();p.fill(0);
            p.textAlign(p.CENTER,p.CENTER);
            p.text(val.toFixed(1)+' t', x, circlesY);
            p.textAlign(p.CENTER,p.TOP);
            p.text(yr, x, circlesY+radius/2+4);
        });
    };
};
const P5INSTANCE = new p5(sketch);

/* ---------------  UPDATE & INITIALIZE ----------------------------- */
function update(){
    recalculate();
    redrawAll();
}
function redrawAll(){
    P5INSTANCE.redraw();
}

/* initial setup */
handleScaleSwitch();
syncStateFromDOM();
update();
</script>
</body>
</html>
