<script>
/* =========================================================
   GLOBALS & THREE.JS SCENE SETUP
   ========================================================= */
let scene, camera, renderer, controls;
let currentSolid = null;   // {group, pivots}
const canvasContainer = document.getElementById('canvas-container');

initScene();
animate();

/* =========================================================
   UI ELEMENTS
   ========================================================= */
const sliderFold     = document.getElementById('slider-fold');
const btnResetView   = document.getElementById('btn-reset-view');
const solidButtons   = [
    document.getElementById('btn-solid-1'),
    document.getElementById('btn-solid-2'),
    document.getElementById('btn-solid-3')
];

/* =========================================================
   SOLID BUILDERS
   Each builder returns { group:THREE.Group, pivots:[{obj,axis,angle}], update(t)}
   ========================================================= */

/* ---------- helper to create square face (for cube / pyramid base) ---------- */
function createSquareFace(color){
    const geo = new THREE.PlaneGeometry(1,1);
    const mat = new THREE.MeshStandardMaterial({color, side:THREE.DoubleSide, flatShading:true});
    return new THREE.Mesh(geo,mat);
}

/* ---------- helper to create triangular face ---------- */
function createTriangleFace(color){
    const geo = new THREE.CircleGeometry(0.57735, 3);     // approx side length 1
    geo.rotateZ(Math.PI/2);                               // orient one edge flat
    const mat = new THREE.MeshStandardMaterial({color, side:THREE.DoubleSide, flatShading:true});
    return new THREE.Mesh(geo,mat);
}

/* ---------- Solid 1 : Cube (net of 6 squares) ------------------------------ */
function createSolid1(){
    const group = new THREE.Group();
    const pivots = [];

    // central square
    const central = createSquareFace(0xE47A55);
    group.add(central);

    // top face
    const topPivot = new THREE.Object3D();
    topPivot.position.set(0,0.5,0);
    central.add(topPivot);

    const topFace = createSquareFace(0xA4C6E3);
    topFace.position.set(0,0.5,0);
    topPivot.add(topFace);
    pivots.push({obj:topPivot, axis:'x', angle:-Math.PI/2});

    // bottom face
    const bottomPivot = new THREE.Object3D();
    bottomPivot.position.set(0,-0.5,0);
    central.add(bottomPivot);

    const bottomFace = createSquareFace(0xF3C76E);
    bottomFace.position.set(0,-0.5,0);
    bottomPivot.add(bottomFace);
    pivots.push({obj:bottomPivot, axis:'x', angle: Math.PI/2});

    // left face
    const leftPivot = new THREE.Object3D();
    leftPivot.position.set(-0.5,0,0);
    central.add(leftPivot);

    const leftFace = createSquareFace(0xF4B6CC);
    leftFace.position.set(-0.5,0,0);
    leftPivot.add(leftFace);
    pivots.push({obj:leftPivot, axis:'y', angle: Math.PI/2});

    // right face
    const rightPivot = new THREE.Object3D();
    rightPivot.position.set(0.5,0,0);
    central.add(rightPivot);

    const rightFace = createSquareFace(0x7FD08B);
    rightFace.position.set(0.5,0,0);
    rightPivot.add(rightFace);
    pivots.push({obj:rightPivot, axis:'y', angle:-Math.PI/2});

    // sixth face attached to bottom face
    const bottom2Pivot = new THREE.Object3D();
    bottom2Pivot.position.set(0,-0.5,0);      // relative to bottom face
    bottomFace.add(bottom2Pivot);

    const bottom2Face = createSquareFace(0xC59FF4);
    bottom2Face.position.set(0,-0.5,0);
    bottom2Pivot.add(bottom2Face);
    pivots.push({obj:bottom2Pivot, axis:'x', angle:Math.PI/2});

    // helper for updating
    const updater = (t)=>{
        pivots.forEach(p=>{
            p.obj.rotation[p.axis] = t * p.angle;
        });
    };

    return {group,pivots,update:updater};
}

/* ---------- Solid 2 : Tetrahedron (net of 4 triangles) -------------------- */
function createSolid2(){
    const group = new THREE.Group();
    const pivots = [];

    // central base triangle
    const base = createTriangleFace(0xF3C76E);
    group.add(base);

    // constants
    const h = Math.sqrt(3)/2;           // height of triangle with side 1
    const dihedral = Math.acos(1/3);    // ≈70.53°
    const foldAngle = Math.PI - dihedral; // ≈109.47°

    // pivot 1 (edge bottom)
    const p1 = new THREE.Object3D();
    p1.position.set(0,-h/3,0);          // rough approximation to bottom edge midpoint
    base.add(p1);

    const f1 = createTriangleFace(0xE47A55);
    f1.position.set(0,-h/3,0);
    p1.add(f1);
    pivots.push({obj:p1, axis:'x', angle:foldAngle});

    // pivot 2 (upper left edge)
    const p2 = new THREE.Object3D();
    p2.position.set(-0.5, h/6, 0);      // approximated midpoint
    base.add(p2);

    const f2 = createTriangleFace(0xA4C6E3);
    f2.position.set(-0.5, h/6, 0);
    p2.add(f2);
    pivots.push({obj:p2, axis:'y', angle:-foldAngle});

    // pivot 3 (upper right edge)
    const p3 = new THREE.Object3D();
    p3.position.set(0.5, h/6, 0);
    base.add(p3);

    const f3 = createTriangleFace(0x7FD08B);
    f3.position.set(0.5, h/6, 0);
    p3.add(f3);
    pivots.push({obj:p3, axis:'y', angle: foldAngle});

    const updater = (t)=>{
        pivots.forEach(p=>{
            p.obj.rotation[p.axis] = t * p.angle;
        });
    };

    return {group,pivots,update:updater};
}

/* ---------- Solid 3 : Square Pyramid (base + 4 triangles) ----------------- */
function createSolid3(){
    const group = new THREE.Group();
    const pivots = [];

    // base square
    const base = createSquareFace(0xC59FF4);
    group.add(base);

    // pyramid changing angle
    const tipAngle = Math.PI/2; // 90° for simple pyramid

    // four triangles around
    const directions = [
        {pos:[ 0, 0.5,0], axis:'x', angle:-tipAngle, color:0xE47A55},  // top edge
        {pos:[ 0,-0.5,0], axis:'x', angle: tipAngle, color:0xA4C6E3},  // bottom
        {pos:[-0.5,0,0], axis:'y', angle: tipAngle, color:0xF3C76E},   // left
        {pos:[ 0.5,0,0], axis:'y', angle:-tipAngle, color:0x7FD08B}    // right
    ];

    directions.forEach((d)=>{
        const pivot = new THREE.Object3D();
        pivot.position.set(...d.pos);
        base.add(pivot);

        const tri = createTriangleFace(d.color);
        tri.scale.set(1,1,1);              // same size basis
        tri.position.set(...d.pos);
        pivot.add(tri);

        pivots.push({obj:pivot, axis:d.axis, angle:d.angle});
    });

    const updater = (t)=>{
        pivots.forEach(p=>{
            p.obj.rotation[p.axis] = t * p.angle;
        });
    };

    return {group,pivots,update:updater};
}

/* Array of builder functions */
const solidFactories = [createSolid1, createSolid2, createSolid3];


/* =========================================================
   EVENT HANDLERS
   ========================================================= */
solidButtons.forEach((btn,idx)=>{
    btn.addEventListener('click',()=>{
        // update active UI
        document.querySelector('.solid-btn.active')?.classList.remove('active');
        btn.classList.add('active');

        loadSolid(idx);
    });
});

sliderFold.addEventListener('input', ()=>{
    updateFolding(parseFloat(sliderFold.value));
});

btnResetView.addEventListener('click', resetView);

/* =========================================================
   CORE LOGIC
   ========================================================= */
function loadSolid(index){
    // remove old
    if(currentSolid){
        scene.remove(currentSolid.group);
    }

    currentSolid = solidFactories[index]();
    scene.add(currentSolid.group);

    // apply current slider value to new solid
    updateFolding(parseFloat(sliderFold.value));

    resetView();
}

function updateFolding(t){
    if(currentSolid && typeof currentSolid.update==="function"){
        currentSolid.update(t);
    }
}

function resetView(){
    // default camera position
    camera.position.set(3,3,3);
    controls.target.set(0,0,0);
    controls.update();
}

/* =========================================================
   INITIALISATION FUNCTIONS
   ========================================================= */
function initScene(){
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    const w = canvasContainer.clientWidth;
    const h = canvasContainer.clientHeight;

    camera = new THREE.PerspectiveCamera(45, w/h, 0.1, 100);
    camera.position.set(3,3,3);

    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(w,h);
    canvasContainer.appendChild(renderer.domElement);

    // controls
    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.1;

    // lighting
    scene.add(new THREE.AmbientLight(0xffffff, 0.6));
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
    dirLight.position.set(5,5,5);
    scene.add(dirLight);

    // first solid
    loadSolid(0);

    window.addEventListener('resize', onWindowResize);
}

function onWindowResize(){
    const w = canvasContainer.clientWidth;
    const h = canvasContainer.clientHeight;
    camera.aspect = w/h;
    camera.updateProjectionMatrix();
    renderer.setSize(w,h);
}

function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene,camera);
}
</script>
</body>
</html>
