<script>
/* --------------------------------------------------
 *  UI STATE & EVENT HANDLERS
 * -------------------------------------------------- */
let alphaAC = 0.6;   // α₁₃
let alphaBC = 3.93;  // α₂₃

const sliderAC = document.getElementById('slider-ac');
const sliderBC = document.getElementById('slider-bc');
const valueAC  = document.getElementById('value-ac');
const valueBC  = document.getElementById('value-bc');
const btnReset = document.getElementById('btn-reset');

/* Update display + redraw helper */
function updateAC(val){
    alphaAC          = parseFloat(val);
    valueAC.textContent = alphaAC.toFixed(2);
    if(window.mysketch) window.mysketch.redraw();
}
function updateBC(val){
    alphaBC          = parseFloat(val);
    valueBC.textContent = alphaBC.toFixed(2);
    if(window.mysketch) window.mysketch.redraw();
}

sliderAC.addEventListener('input', e=>updateAC(e.target.value));
sliderBC.addEventListener('input', e=>updateBC(e.target.value));

btnReset.addEventListener('click', ()=>{
    sliderAC.value = 0.6;
    sliderBC.value = 3.93;
    updateAC(sliderAC.value);
    updateBC(sliderBC.value);
});

/* --------------------------------------------------
 *  VISUALIZATION – p5.js sketch
 * -------------------------------------------------- */
(function(){
    /* Remove the placeholder canvas to avoid duplicate IDs */
    const placeholder = document.getElementById('ternary-plot-canvas');
    placeholder.parentNode.removeChild(placeholder);

    const sketch = (p)=>{
        const CANVAS_W = 600;
        const CANVAS_H = 550;
        const PAD      = 60;
        const N        = 10;          // subdivision count

        /* Triangle vertices (Cartesian) */
        let Ax, Ay, Bx, By, Cx, Cy;

        /* Pre-compute gradient colors */
        const gradientStops = [
            {stop:0.00, col:'#2c7bb6'},
            {stop:0.25, col:'#abd9e9'},
            {stop:0.50, col:'#ffffbf'},
            {stop:0.75, col:'#fdae61'},
            {stop:1.00, col:'#d7191c'}
        ];

        p.setup = ()=>{
            /* Create canvas inside visualization container */
            const c = p.createCanvas(CANVAS_W, CANVAS_H);
            c.parent('visualization-container');
            c.id('ternary-plot-canvas');

            /* Vertex positions */
            Ax = CANVAS_W/2; Ay = PAD;               // vertex A (top)
            Bx = PAD;          By = CANVAS_H - PAD;  // vertex B (bottom-left)
            Cx = CANVAS_W-PAD; Cy = CANVAS_H - PAD;  // vertex C (bottom-right)

            p.noLoop();
            p.redraw();
        };

        /* Linear interpolation between two p5 colors for value t∈[0,1] */
        function interpColor(c1, c2, t){
            return p.lerpColor(c1, c2, t);
        }

        /* Map normalized scalar v∈[0,1] ➜ gradient color */
        function scalarToColor(v){
            for(let i=0;i<gradientStops.length-1;i++){
                const a = gradientStops[i];
                const b = gradientStops[i+1];
                if(v>=a.stop && v<=b.stop){
                    const localT = (v - a.stop) / (b.stop - a.stop);
                    return interpColor(p.color(a.col), p.color(b.col), localT);
                }
            }
            return p.color(gradientStops[gradientStops.length-1].col);
        }

        /* Convert barycentric (xA,xB,xC) to Cartesian */
        function baryToXY(xA, xB, xC){
            const px = xA*Ax + xB*Bx + xC*Cx;
            const py = xA*Ay + xB*By + xC*Cy;
            return [px, py];
        }

        /* Draw a filled small triangle given barycentric vertices */
        function drawSmallTriangle(b1, b2, b3){
            // Centroid
            const xc = (b1[0]+b2[0]+b3[0])/3;
            const yc = (b1[1]+b2[1]+b3[1])/3;
            const zc = (b1[2]+b2[2]+b3[2])/3;

            /* Vapor-phase mole fractions */
            const a1 = alphaAC;
            const a2 = alphaBC;
            const a3 = 1.0;
            const D  = a1*xc + a2*yc + a3*zc;
            const y1 = (a1*xc)/D,
                  y2 = (a2*yc)/D,
                  y3 = (a3*zc)/D;

            /* Function F and normalization */
            const F  = Math.pow(xc-y1,2) + Math.pow(yc-y2,2) + Math.pow(zc-y3,2);
            const v  = Math.min(F/2.0, 1);  // normalized [0,1]

            /* Color for this triangle */
            const col = scalarToColor(v);

            /* Convert vertices to Cartesian and draw */
            const P1 = baryToXY(...b1);
            const P2 = baryToXY(...b2);
            const P3 = baryToXY(...b3);

            p.stroke(0);
            p.strokeWeight(0.3);
            p.fill(col);
            p.beginShape();
            p.vertex(P1[0], P1[1]);
            p.vertex(P2[0], P2[1]);
            p.vertex(P3[0], P3[1]);
            p.endShape(p.CLOSE);
        }

        /* Draw tick marks and labels along edges */
        function drawTicks(){
            p.noStroke();
            p.fill(0);
            p.textSize(10);
            p.textAlign(p.CENTER, p.CENTER);

            for(let k=0;k<=10;k++){
                const f = k/10;

                /* Edge AB */
                let xAB = Ax + (Bx-Ax)*f;
                let yAB = Ay + (By-Ay)*f;
                p.text(f.toFixed(1), xAB-14, yAB-6);

                /* Edge AC */
                let xAC = Ax + (Cx-Ax)*f;
                let yAC = Ay + (Cy-Ay)*f;
                p.text(f.toFixed(1), xAC+14, yAC-6);

                /* Edge BC */
                let xBC = Bx + (Cx-Bx)*f;
                let yBC = By + (Cy-By)*f;
                p.text(f.toFixed(1), xBC,    yBC+14);
            }
        }

        /* ------  Main draw routine  ------ */
        p.draw = ()=>{
            p.background(255);

            /* Iterate through subdivision grid (produces N² triangles) */
            for(let i=0;i<N;i++){
                for(let j=0;j<N-i;j++){

                    // Upward-pointing triangle
                    const t1 = [ i   /N,  j   /N,  1 - i   /N - j   /N];
                    const t2 = [(i+1)/N,  j   /N,  1 - (i+1)/N - j   /N];
                    const t3 = [ i   /N, (j+1)/N,  1 - i   /N - (j+1)/N];
                    drawSmallTriangle(t1,t2,t3);

                    // Downward-pointing triangle (if inside)
                    if(j < N-i-1){
                        const s1 = [(i+1)/N,  j   /N,  1 - (i+1)/N - j   /N];
                        const s2 = [(i+1)/N,(j+1)/N,  1 - (i+1)/N - (j+1)/N];
                        const s3 = [ i   /N,(j+1)/N,  1 - i   /N - (j+1)/N];
                        drawSmallTriangle(s1,s2,s3);
                    }
                }
            }

            /* Outline of main triangle */
            p.noFill();
            p.stroke(0);
            p.strokeWeight(1);
            p.triangle(Ax,Ay, Bx,By, Cx,Cy);

            /* Axis labels */
            p.noStroke();
            p.fill(0);
            p.textSize(16);
            p.textAlign(p.CENTER, p.CENTER);
            p.text('A', Ax, Ay-18);
            p.text('B', Bx-18, By+18);
            p.text('C', Cx+18, Cy+18);

            /* Tick marks */
            drawTicks();
        };
    };

    /* Create global p5 instance so event handlers can call redraw() */
    window.mysketch = new p5(sketch);
})();
</script>
</body>
</html>
