<body>
    <h1>Band Structure of a Quantum Wire with Rashba and Zeeman Interactions</h1>

    <div id="main-container">
        <!-- ==================== CONTROL PANEL ==================== -->
        <div id="control-panel">
            <!-- Rashba -->
            <label for="slider-wso">Rashba spin-orbit interaction strength (wso)</label>
            <input type="range" id="slider-wso" min="0" max="2" step="0.1" value="0">
            <span id="value-wso">0.0</span>

            <hr>

            <!-- Magnetic Field -->
            <h2 style="margin:0;">magnetic field</h2>

            <!-- Subband -->
            <label for="slider-n">subband (n)</label>
            <input type="range" id="slider-n" min="0" max="10" step="1" value="0">
            <span id="value-n">0</span>

            <h3 style="margin-bottom:5px;">strengths</h3>

            <!-- BX -->
            <label for="slider-bx">x direction (BX)</label>
            <input type="range" id="slider-bx" min="0" max="2" step="0.1" value="0">
            <span id="value-bx">0.0</span>

            <!-- BY -->
            <label for="slider-by">y direction (BY)</label>
            <input type="range" id="slider-by" min="0" max="2" step="0.1" value="0">
            <span id="value-by">0.0</span>

            <!-- BZ -->
            <label for="slider-bz">z direction (BZ)</label>
            <input type="range" id="slider-bz" min="0" max="2" step="0.1" value="0">
            <span id="value-bz">0.0</span>
        </div>

        <!-- ==================== PLOT AREA ==================== -->
        <div id="plot-container"></div>
    </div>


    <script>
        // Utility to format numbers
        function fmt(val, decimals = 1){
            return +parseFloat(val).toFixed(decimals);
        }

        // Grab elements
        const sliders = {
            wso : document.getElementById('slider-wso'),
            n   : document.getElementById('slider-n'),
            bx  : document.getElementById('slider-bx'),
            by  : document.getElementById('slider-by'),
            bz  : document.getElementById('slider-bz')
        };

        const spans = {
            wso : document.getElementById('value-wso'),
            n   : document.getElementById('value-n'),
            bx  : document.getElementById('value-bx'),
            by  : document.getElementById('value-by'),
            bz  : document.getElementById('value-bz')
        };

        // Attach listeners
        Object.keys(sliders).forEach(key=>{
            sliders[key].addEventListener('input', ()=>{
                // Update span
                let value = sliders[key].value;
                spans[key].textContent = (key==='n') ? value : fmt(value);
                // Update plot
                updatePlot();
            });
        });

        // Core plotting function
        function updatePlot(){
            const n   = parseInt(sliders.n.value);
            const wso = parseFloat(sliders.wso.value);
            const BX  = parseFloat(sliders.bx.value);
            const BY  = parseFloat(sliders.by.value);
            const BZ  = parseFloat(sliders.bz.value);

            // k array
            const k = [];
            for(let x=-5; x<=5.001; x+=0.1){ k.push(parseFloat(x.toFixed(2))); }

            // Energy calculations
            let E_plus = [];
            let E_minus = [];
            const base = (2*n + 1);

            const sqrtIsZero = (wso===0 && BX===0 && BY===0 && BZ===0);

            k.forEach(kk=>{
                const sqrtTerm = Math.sqrt( Math.pow(BZ + wso*kk,2) + BX*BX + BY*BY );
                E_plus.push( base + kk*kk + sqrtTerm );
                E_minus.push( base + kk*kk - sqrtTerm );
            });

            // Prepare traces
            const traces = [];
            if(sqrtIsZero){
                traces.push({
                    x: k,
                    y: E_plus,
                    mode:'lines',
                    line:{color:'red', dash:'solid', width:2},
                    name:'degenerate'
                });
            }else{
                traces.push({
                    x: k,
                    y: E_plus,
                    mode:'lines',
                    line:{color:'red', dash:'solid', width:2},
                    name:'E\u208A (plus)'
                });
                traces.push({
                    x: k,
                    y: E_minus,
                    mode:'lines',
                    line:{color:'red', dash:'dash', width:2},
                    name:'E\u208B (minus)'
                });
            }

            // Dynamic title creation
            const parts = [`n = ${n}`];
            if(BX!==0) parts.push(`BX = ${fmt(BX)}`);
            if(BY!==0) parts.push(`BY = ${fmt(BY)}`);
            if(BZ!==0) parts.push(`BZ = ${fmt(BZ)}`);
            if(wso!==0) parts.push(`wso = ${fmt(wso)}`);
            const titleStr = parts.join(', ');

            // Layout
            const layout = {
                title: { text: titleStr, font:{size:18} },
                xaxis:{
                    title:'normalized k length (l\u209C\u1D62)',
                    range:[-5,5],
                    zeroline:true
                },
                yaxis:{
                    title:'2 E / &#x210F;&omega;',
                    range:[0,40],
                    zeroline:true
                },
                margin:{l:60,r:30,t:60,b:50}
            };

            Plotly.react('plot-container', traces, layout, {responsive:true});
        }

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
