<body>
<div id="app-container">

    <!------------   CONTROL PANEL   ------------>
    <div id="controls-container">
        <div id="controls-header">
            <h3>Controls</h3>
            <button id="toggle-controls">−</button>
        </div>

        <div id="sliders-area">
            <h4>precipitation</h4>
            <div class="slider-row">
                <label for="slider-precip-1">hour&nbsp;1</label>
                <input id="slider-precip-1" type="range" min="0" max="10" step="1" value="1">
                <span id="value-precip-1">1</span>
            </div>
            <div class="slider-row">
                <label for="slider-precip-2">hour&nbsp;2</label>
                <input id="slider-precip-2" type="range" min="0" max="10" step="1" value="0">
                <span id="value-precip-2">0</span>
            </div>
            <div class="slider-row">
                <label for="slider-precip-3">hour&nbsp;3</label>
                <input id="slider-precip-3" type="range" min="0" max="10" step="1" value="0">
                <span id="value-precip-3">0</span>
            </div>

            <h4>UH streamflow rates</h4>
            <div class="slider-row">
                <label for="slider-uh-1">hour&nbsp;1</label>
                <input id="slider-uh-1" type="range" min="0" max="10" step="1" value="1">
