</div>

<script>
/* ----------------  GLOBAL DATA  ---------------- */
let P=[1,0,0];           // precipitation (3)
let U=[1,5,3,1];         // unit hydrograph (4)
let C1=[],C2=[],C3=[],Q=[];

const precipIds=[1,2,3];
const uhIds=[1,2,3,4];

/* ----------------  SLIDER HANDLING  ---------------- */
function initSliders(){
    // precipitation sliders
    precipIds.forEach(i=>{
        const slider=document.getElementById('slider-precip-'+i);
        const span=document.getElementById('value-precip-'+i);
        slider.addEventListener('input',()=>{
            span.textContent=slider.value;
            updateData();
        });
    });
    // UH sliders
    uhIds.forEach(i=>{
        const slider=document.getElementById('slider-uh-'+i);
        const span=document.getElementById('value-uh-'+i);
        slider.addEventListener('input',()=>{
            span.textContent=slider.value;
            updateData();
        });
    });
}

/* ----------------  TOGGLE CONTROLS  ---------------- */
document.getElementById('toggle-controls').addEventListener('click',()=>{
    const area=document.getElementById('sliders-area');
    const btn=document.getElementById('toggle-controls');
    if(area.style.display==='none'){
        area.style.display='block';
        btn.textContent='−';
    }else{
        area.style.display='none';
        btn.textContent='+';
    }
});

/* ----------------  COMPUTATION  ---------------- */
function updateData(){
    // read sliders
    P=precipIds.map(i=>Number(document.getElementById('slider-precip-'+i).value));
    U=uhIds.map(i=>Number(document.getElementById('slider-uh-'+i).value));

    // component hydrographs
    C1=U.map(u=>u*P[0]);
    C2=U.map(u=>u*P[1]);
    C3=U.map(u=>u*P[2]);

    // composite Q (length 6)
    Q=[
        P[0]*U[0],
        P[0]*U[1] + P[1]*U[0],
        P[0]*U[2] + P[1]*U[1] + P[2]*U[0],
        P[0]*U[3] + P[1]*U[2] + P[2]*U[1],
        P[1]*U[3] + P[2]*U[2],
        P[2]*U[3]
    ];

    // redraw plots
    plotInstances.forEach(p=>p.redraw());
}

/* ----------------  DRAWING UTILITIES  ---------------- */
function drawPlot(p, data, xLabel, yLabel, xMax, yMax, xTick, yTick, offset){
    const margin=40;
    const w=p.width, h=p.height;
    const plotW=w-2*margin;
    const plotH=h-2*margin;

    // helpers
    const xPixel=t=>margin + (t/xMax)*plotW;
    const yPixel=v=>margin + plotH - (v/yMax)*plotH;

    p.clear();
    p.background(255);

    /* Axes */
    p.stroke(0);
    p.strokeWeight(1);
    // y-axis
    p.line(margin,margin,margin,margin+plotH);
    // x-axis
    p.line(margin,margin+plotH,margin+plotW,margin+plotH);

    // ticks
    p.textSize(11);
    p.textAlign(p.RIGHT,p.CENTER);
    for(let y=0;y<=yMax;y+=yTick){
        const yPos=yPixel(y);
        p.line(margin-5,yPos,margin,yPos);
        p.noStroke();
        p.text(y,margin-8,yPos);
        p.stroke(0);
    }
    p.textAlign(p.CENTER,p.TOP);
    for(let x=0;x<=xMax;x+=xTick){
        const xPos=xPixel(x);
        p.line(xPos,margin+plotH,xPos,margin+plotH+5);
        p.noStroke();
        p.text(x,xPos,margin+plotH+8);
        p.stroke(0);
    }

    // labels
    p.noStroke();
    p.textAlign(p.CENTER,p.BOTTOM);
    p.text(xLabel,margin+plotW/2,h-4);
    p.push();
    p.translate(12,margin+plotH/2);
    p.rotate(-p.HALF_PI);
    p.text(yLabel,0,0);
    p.pop();

    /* Step chart fill */
    p.stroke('#2e86c1');
    p.strokeWeight(2);
    p.fill('#d4e6f1');
    p.beginShape();
    // start at bottom-left
    p.vertex(xPixel(offset), yPixel(0));
    for(let i=0;i<data.length;i++){
        const xLeft=xPixel(offset+i);
        const xRight=xPixel(offset+i+1);
        const yVal=yPixel(data[i]);
        // vertical up
        p.vertex(xLeft,yVal);
        // horizontal right
        p.vertex(xRight,yVal);
    }
    // last down to axis
    p.vertex(xPixel(offset+data.length),yPixel(0));
    p.endShape(p.CLOSE);

    /* Step chart outline */
    p.noFill();
    for(let i=0;i<data.length;i++){
        const yVal=yPixel(data[i]);
        const xLeft=xPixel(offset+i);
        const xRight=xPixel(offset+i+1);
        p.line(xLeft,yVal,xRight,yVal); // horizontal
        if(i<data.length-1){
            const yNext=yPixel(data[i+1]);
            p.line(xRight,yVal,xRight,yNext); // vertical
        }
    }
}

/* ----------------  P5 INSTANCES  ---------------- */
const plotInstances=[];

function createPlot(parentId,getData){
    const sketch=(p)=>{
        p.setup=()=>{ 
            const parent=document.getElementById(parentId);
            const canvas=p.createCanvas(parent.clientWidth,parent.clientHeight);
            canvas.parent(parent);
            p.noLoop();
        };
        p.windowResized=()=>{
            const parent=document.getElementById(parentId);
            p.resizeCanvas(parent.clientWidth,parent.clientHeight);
            p.redraw();
        };
        p.draw=()=>{ getData(p); };
    };
    return new p5(sketch);
}

// Plot 1: Hyetograph
plotInstances.push(createPlot('plot-canvas-1',p=>{
    drawPlot(p,P,'t (hr)','P (in)',5,10,1,2,0);
}));

// Plot 2: Unit Hydrograph
plotInstances.push(createPlot('plot-canvas-2',p=>{
    drawPlot(p,U,'t (hr)','Q (cfs/in)',5,10,1,2,0);
}));

// Plot 3: Component 1
plotInstances.push(createPlot('plot-canvas-3',p=>{
    drawPlot(p,C1,'t (hr)','Q (cfs)',8,100,2,20,0);
}));

// Plot 4: Component 2
plotInstances.push(createPlot('plot-canvas-4',p=>{
    drawPlot(p,C2,'t (hr)','Q (cfs)',8,100,2,20,1);
}));

// Plot 5: Component 3
plotInstances.push(createPlot('plot-canvas-5',p=>{
    drawPlot(p,C3,'t (hr)','Q (cfs)',8,100,2,20,2);
}));

// Plot 6: Composite Hydrograph
plotInstances.push(createPlot('plot-canvas-6',p=>{
    drawPlot(p,Q,'t (hr)','Q (cfs)',8,100,2,20,0);
}));

/* ----------------  INITIALISE  ---------------- */
initSliders();
updateData();      // draw first time
</script>
</body>
</html>
