------------------------------------------------------ */
function updatePayoffTable(){
    const cells=[['11','a11','b11'],['12','a12','b12'],['21','a21','b21'],['22','a22','b22']];
    cells.forEach(([suffix,a,b])=>{
        document.getElementById('cell-'+suffix).textContent=`(${values[a]}, ${values[b]})`;
    });
}

/* ------------------------------------------------------
   ---------- FRACTION HELPER ----------------------------
------------------------------------------------------ */
function toFraction(x,tol=1e-6,maxDen=20){
    if(Math.abs(x-Math.round(x))<tol) return String(Math.round(x));
    let bestNum=1,bestDen=1,minErr=Infinity;
    for(let den=1;den<=maxDen;den++){
        const num=Math.round(x*den);
        const err=Math.abs(x - num/den);
        if(err<minErr-tol){
            minErr=err; bestNum=num; bestDen=den;
        }
    }
    return `\\frac{${bestNum}}{${bestDen}}`;
}

/* ------------------------------------------------------
   ---------- SEGMENT GENERATORS -------------------------
------------------------------------------------------ */
function getPlayer1Segments(a11,a12,a21,a22){
    const segs=[];
    const den=a11 - a12 - a21 + a22;
    const eps=1e-9;
    if(Math.abs(den)<eps){ // dominant strategy
        if(a12 - a22 > 0){ // choose x1=1
            segs.push({type:'vertical',x:1,y1:0,y2:1});
        }else{
            segs.push({type:'vertical',x:0,y1:0,y2:1});
        }
    }else{
        const yIndiff=(a22 - a12)/den;
        if(yIndiff>0 && yIndiff<1){
            segs.push({type:'horizontal',y:yIndiff,x1:0,x2:1});
            if(den>0){
                segs.push({type:'vertical',x:0,y1:0,y2:yIndiff});
                segs.push({type:'vertical',x:1,y1:yIndiff,y2:1});
            }else{
                segs.push({type:'vertical',x:0,y1:yIndiff,y2:1});
                segs.push({type:'vertical',x:1,y1:0,y2:yIndiff});
            }
        }else{ // outside unit interval -> dominant
            // test at y=0.5
            const pay1 = 0.5*a11 + 0.5*a12;
            const pay0 = 0.5*a21 + 0.5*a22;
            if(pay1>pay0){ // choose strategy 1 (x=1)
                segs.push({type:'vertical',x:1,y1:0,y2:1});
            }else{
                segs.push({type:'vertical',x:0,y1:0,y2:1});
            }
        }
    }
    return segs;
}

function getPlayer2Segments(b11,b12,b21,b22){
    const segs=[];
    const den=b11 - b12 - b21 + b22;
    const eps=1e-9;
    if(Math.abs(den)<eps){ // dominant
        // test at x=0.5
        const payY1 = 0.5*b11 + 0.5*b21;
        const payY0 = 0.5*b12 + 0.5*b22;
        if(payY1>payY0){ // choose y1=1
            segs.push({type:'horizontal',y:1,x1:0,x2:1});
        }else{
            segs.push({type:'horizontal',y:0,x1:0,x2:1});
        }
    }else{
        const xIndiff=(b22 - b21)/den;
        if(xIndiff>0 && xIndiff<1){
            segs.push({type:'vertical',x:xIndiff,y1:0,y2:1});
            if(den>0){
                segs.push({type:'horizontal',y:0,x1:0,x2:xIndiff});
                segs.push({type:'horizontal',y:1,x1:xIndiff,x2:1});
            }else{
                segs.push({type:'horizontal',y:0,x1:xIndiff,x2:1});
                segs.push({type:'horizontal',y:1,x1:0,x2:xIndiff});
            }
        }else{ // dominant
            // again use preference test
            const payY1 = 0.5*b11 + 0.5*b21;
            const payY0 = 0.5*b12 + 0.5*b22;
            if(payY1>payY0){
                segs.push({type:'horizontal',y:1,x1:0,x2:1});
            }else{
                segs.push({type:'horizontal',y:0,x1:0,x2:1});
            }
        }
    }
    return segs;
}

/* ------------------------------------------------------
   ---------- INTERSECTION CALCULATIONS ------------------
------------------------------------------------------ */
function intersectSegments(s1,s2){
    const results=[];
    if(s1.type==='vertical' && s2.type==='vertical'){
        if(Math.abs(s1.x - s2.x)<1e-9){
            const y_low=Math.max(s1.y1,s2.y1);
            const y_high=Math.min(s1.y2,s2.y2);
            if(y_low<=y_high){
                results.push({type:'vertical',x:s1.x,y1:y_low,y2:y_high});
            }
        }
    }else if(s1.type==='horizontal' && s2.type==='horizontal'){
        if(Math.abs(s1.y - s2.y)<1e-9){
            const x_low=Math.max(s1.x1,s2.x1);
            const x_high=Math.min(s1.x2,s2.x2);
            if(x_low<=x_high){
                results.push({type:'horizontal',y:s1.y,x1:x_low,x2:x_high});
            }
        }
    }else{
        // one vertical, one horizontal
        let v,h;
        if(s1.type==='vertical'){v=s1;h=s2;}else{v=s2;h=s1;}
        if(v.x>=h.x1-1e-9 && v.x<=h.x2+1e-9 &&
           h.y>=v.y1-1e-9 && h.y<=v.y2+1e-9){
            results.push({type:'point',x:v.x,y:h.y});
        }
    }
    return results;
}

function computeIntersections(blueSegs,greenSegs){
    const inter=[];
    blueSegs.forEach(b=>{
        greenSegs.forEach(g=>{
            inter.push(...intersectSegments(b,g));
        });
    });
    return inter;
}

/* ------------------------------------------------------
   ---------- EQUILIBRIA DISPLAY -------------------------
------------------------------------------------------ */
function updateEquilibriaDisplay(interSegs){
    const verts=[];
    function addVert(pt){
        // check uniqueness
        for(const v of verts){
            if(Math.abs(v.x-pt.x)<1e-6 && Math.abs(v.y-pt.y)<1e-6) return;
        }
        verts.push(pt);
    }
    interSegs.forEach(s=>{
        if(s.type==='point'){
            addVert({x:s.x,y:s.y});
        }else if(s.type==='vertical'){
            addVert({x:s.x,y:s.y1});
            addVert({x:s.x,y:s.y2});
        }else if(s.type==='horizontal'){
            addVert({x:s.x1,y:s.y});
            addVert({x:s.x2,y:s.y});
        }
    });

    // Sort vertices for stable output
    verts.sort((p,q)=>p.x!==q.x ? p.x-q.x : p.y-q.y);

    if(verts.length===0){
        document.getElementById('equilibria-display').innerHTML='\\( \\varnothing \\)';
    }else{
        const parts=verts.map(v=>`(${toFraction(v.x)},\\;${toFraction(v.y)})`);
        const latex=`\\( \\{ ${parts.join(', ')} \\} \\)`;
        document.getElementById('equilibria-display').innerHTML=latex;
    }
    // ask MathJax to typeset
    if(window.MathJax){MathJax.typesetPromise(["#equilibria-display"]); }
}

/* ------------------------------------------------------
   ---------- P5.js  DRAWING -----------------------------
------------------------------------------------------ */
let blueSegs=[],greenSegs=[],interSegs=[];
const canvasSize=400, margin=40;
function setup(){
    const cnv=createCanvas(canvasSize,canvasSize);
    cnv.parent('canvas-container');
    noLoop();
    redrawCanvas(); // initial draw
}

function toCanvasX(x){return margin + x*(canvasSize-2*margin);}
function toCanvasY(y){return canvasSize - (margin + y*(canvasSize-2*margin));}

function redrawCanvas(){
    clear();
    // Axes
    stroke(0); strokeWeight(1);
    line(toCanvasX(0),toCanvasY(0),toCanvasX(1),toCanvasY(0)); // x axis
    line(toCanvasX(0),toCanvasY(0),toCanvasX(0),toCanvasY(1)); // y axis
    // ticks
    for(let t=0;t<=1.0001;t+=0.2){
        const x=toCanvasX(t);
        const y=toCanvasY(t);
        // x ticks
        line(x,toCanvasY(0)-4,x,toCanvasY(0)+4);
        textAlign(CENTER,TOP);
        noStroke(); fill(0);
        text(t.toFixed(1),x,toCanvasY(0)+6);
        // y ticks
        stroke(0);
        line(toCanvasX(0)-4,y,toCanvasX(0)+4,y);
        noStroke(); fill(0);
        textAlign(RIGHT,CENTER);
        text(t.toFixed(1),toCanvasX(0)-6,y);
        stroke(0);
    }
    // axis labels
    noStroke(); fill(0); textAlign(RIGHT,TOP); text('x₁',toCanvasX(1)+15,toCanvasY(0)-2);
    textAlign(RIGHT,BOTTOM); text('y₁',toCanvasX(0)+2,toCanvasY(1)-15);

    /* Blue segments */
    strokeWeight(2);
    stroke('blue');
    blueSegs.forEach(s=>{
        if(s.type==='vertical'){
            line(toCanvasX(s.x),toCanvasY(s.y1),toCanvasX(s.x),toCanvasY(s.y2));
        }else if(s.type==='horizontal'){
            line(toCanvasX(s.x1),toCanvasY(s.y),toCanvasX(s.x2),toCanvasY(s.y));
        }
    });
    /* Green segments */
    stroke('green');
    greenSegs.forEach(s=>{
        if(s.type==='vertical'){
            line(toCanvasX(s.x),toCanvasY(s.y1),toCanvasX(s.x),toCanvasY(s.y2));
        }else if(s.type==='horizontal'){
            line(toCanvasX(s.x1),toCanvasY(s.y),toCanvasX(s.x2),toCanvasY(s.y));
        }
    });
    /* Red intersections */
    strokeWeight(4); stroke('red'); fill('red');
    interSegs.forEach(s=>{
        if(s.type==='point'){
            circle(toCanvasX(s.x),toCanvasY(s.y),8);
        }else if(s.type==='vertical'){
            line(toCanvasX(s.x),toCanvasY(s.y1),toCanvasX(s.x),toCanvasY(s.y2));
            circle(toCanvasX(s.x),toCanvasY(s.y1),8);
            circle(toCanvasX(s.x),toCanvasY(s.y2),8);
        }else if(s.type==='horizontal'){
            line(toCanvasX(s.x1),toCanvasY(s.y),toCanvasX(s.x2),toCanvasY(s.y));
            circle(toCanvasX(s.x1),toCanvasY(s.y),8);
            circle(toCanvasX(s.x2),toCanvasY(s.y),8);
        }
    });
}

/* ------------------------------------------------------
   ---------- MAIN UPDATE HANDLER ------------------------
------------------------------------------------------ */
function handleChange(){
    updatePayoffTable();

    // regenerate segments
    blueSegs=getPlayer1Segments(values.a11,values.a12,values.a21,values.a22);
    greenSegs=getPlayer2Segments(values.b11,values.b12,values.b21,values.b22);
    interSegs=computeIntersections(blueSegs,greenSegs);

    updateEquilibriaDisplay(interSegs);
    redrawCanvas();
}

/* First initialization after page load */
handleChange();
</script>
</body>
</html>
