            <label for="checkbox-family">family</label>
        </div>
    </div>

    <!-- Canvas Parent -->
    <div id="canvas-container"></div>
</div>

<script>
/* ------------------  GLOBALS & DOM --------------------- */
const sliderT   = document.getElementById('slider-t');
const inputT    = document.getElementById('input-t');
const chkFamily = document.getElementById('checkbox-family');
const btnReset  = document.getElementById('btn-reset');

let tVal = parseFloat(sliderT.value);            // parameter t
let showFamily = false;                          // checkbox state

const CANVAS_W = 600, CANVAS_H = 500;
const X_RANGE  = 6,  Y_RANGE  = 5;               // ± range visible
const SCALE    = 50;                             // pixels per unit

/* locator points (math coordinates) */
let locators = [
    {x:-4, y: 1,  r:0.15},
    {x:-2, y: 1.8,r:0.15},
    {x: 1, y:-1,  r:0.15},
    {x: 4, y:-1,  r:0.15},
];

let draggingIndex = -1;          // which locator is being dragged
let cubicCoeffs   = [0,0,0,0];   // a,b,c,d
let quadCoeffs    = [0,0,0];     // p,q,r

/* ------------------  EVENT HANDLERS --------------------- */
sliderT.addEventListener('input',  evt => {  tVal = parseFloat(evt.target.value);  inputT.value = tVal; });
inputT.addEventListener('input',   evt => {  
    let v = parseFloat(evt.target.value);
    if(isNaN(v)) return;
    v = Math.min(5,Math.max(-5,v));
    tVal = v;
    sliderT.value = v;
});
chkFamily.addEventListener('change', evt => { showFamily = chkFamily.checked; });
btnReset.addEventListener('click', resetDemo);

/* ------------------  p5.js SKETCH --------------------- */
let sketch = p => {

    p.setup = () =>{
        const canvas = p.createCanvas(CANVAS_W,CANVAS_H);
        canvas.parent('canvas-container');
        p.colorMode(p.HSB,360,100,100,100);
        p.textFont('Courier New');
        p.textSize(11);
        solveCubic();
    };

    p.draw = () =>{
        p.background(255);
        drawAxes();
        solveCubic();                     // refresh coefficients each frame
        if(showFamily){
            drawQuadraticFamily();
            drawQuadratic(quadCoeffs,   '#f57c00',3,false); // highlighted current quadratic
        }else{
            drawCubic();
            drawQuadratic(quadCoeffs,   '#f57c00',3,false);
        }
        drawLocators();
    };

    /* --------------   Interaction -------------- */
    p.mousePressed = () =>{
        const m = screenToMath(p.mouseX,p.mouseY);
        locators.forEach( (pt,idx) =>{
            if( distance(pt,m) < pt.r*2 ){ draggingIndex = idx; }
        });
    };
    p.mouseDragged = () =>{
        if(draggingIndex>-1){
            const m = screenToMath(p.mouseX,p.mouseY);
            locators[draggingIndex].x = clamp(m.x,-X_RANGE,X_RANGE);
            locators[draggingIndex].y = clamp(m.y,-Y_RANGE,Y_RANGE);
        }
    };
    p.mouseReleased = () =>{ draggingIndex = -1; };

    /* --------------   Drawing Helpers -------------- */
    function drawAxes(){
        p.push();
        p.translate(CANVAS_W/2, CANVAS_H/2);
        p.scale(1,-1);
        p.stroke(180); p.strokeWeight(1);

        // X axis
        p.line(-CANVAS_W/2,0, CANVAS_W/2,0);
        // Y axis
        p.line(0,-CANVAS_H/2, 0,CANVAS_H/2);

        // ticks & labels
        p.textAlign(p.CENTER,p.CENTER);
        for(let x=-X_RANGE; x<=X_RANGE; x++){
            let xs = x*SCALE;
            p.line(xs,-4,xs,4);
            if(x!==0) { p.scale(1,-1); p.text(x, xs, -12); p.scale(1,-1); }
        }
        for(let y=-Y_RANGE; y<=Y_RANGE; y++){
            let ys = y*SCALE;
            p.line(-4,ys,4,ys);
            if(y!==0) { p.scale(1,-1); p.text(y,  -14, -ys); p.scale(1,-1);}
        }
        p.pop();
    }

    function drawLocators(){
        p.push();
        p.translate(CANVAS_W/2, CANVAS_H/2);
        p.scale(1,-1);
        locators.forEach(pt=>{
            const xs = pt.x*SCALE, ys=pt.y*SCALE;
            p.fill(0,0,100); p.stroke(0); p.strokeWeight(1.5);
            p.circle(xs,ys,pt.r*2*SCALE);
            p.line(xs-pt.r*SCALE*0.8, ys, xs+pt.r*SCALE*0.8, ys);
            p.line(xs, ys-pt.r*SCALE*0.8, xs, ys+pt.r*SCALE*0.8);
        });
        p.pop();
    }

    function drawCubic(){
        p.push();
        p.translate(CANVAS_W/2, CANVAS_H/2);
        p.scale(1,-1);
        p.noFill(); p.stroke('#3f51b5'); p.strokeWeight(3);
        p.beginShape();
        for(let px=-X_RANGE; px<=X_RANGE; px+=0.05){
            let y = evaluateCubic(px);
            p.vertex(px*SCALE, y*SCALE);
        }
        p.endShape();
        p.pop();
    }

    function drawQuadratic(coeffs, strokeCol, weight=2, filled=false){
        if(!coeffs) return;
        const [A,B,C]=coeffs;
        const yBoundaryUp   =  Y_RANGE;
        const yBoundaryDown = -Y_RANGE;

        p.push();
        p.translate(CANVAS_W/2, CANVAS_H/2);
        p.scale(1,-1);

        if(filled){
            p.noStroke();
            p.fill(strokeCol);
            p.beginShape();
            if(A>0){
                p.vertex(-X_RANGE*SCALE, yBoundaryUp*SCALE);
                for(let x=-X_RANGE; x<=X_RANGE; x+=0.05){
                    p.vertex(x*SCALE, (A*x*x+B*x+C)*SCALE);
                }
                p.vertex(X_RANGE*SCALE, yBoundaryUp*SCALE);
            }else{
                p.vertex(-X_RANGE*SCALE, yBoundaryDown*SCALE);
                for(let x=-X_RANGE; x<=X_RANGE; x+=0.05){
                    p.vertex(x*SCALE, (A*x*x+B*x+C)*SCALE);
                }
                p.vertex(X_RANGE*SCALE, yBoundaryDown*SCALE);
            }
            p.endShape(p.CLOSE);
        }

        p.noFill(); p.stroke(strokeCol); p.strokeWeight(weight);
        p.beginShape();
        for(let x=-X_RANGE; x<=X_RANGE; x+=0.05){
            p.vertex(x*SCALE, (A*x*x+B*x+C)*SCALE);
        }
        p.endShape();
        p.pop();
    }

    function drawQuadraticFamily(){
        const steps = 20;
        for(let i=0;i<=steps;i++){
            const _t = -5 + 10*i/steps;
            const q  = getOsculatingQuadraticCoeffs(_t, cubicCoeffs);
            const hue = p.map(i,0,steps,0,360);
            const col = p.color(hue,70,80,40);     // semi-transparent
            drawQuadratic(q,col,1,true);
        }
    }

    /* --------------   Math Helpers -------------- */
    function solveCubic(){
        cubicCoeffs = solveCubicCoefficients(locators);
        quadCoeffs  = getOsculatingQuadraticCoeffs(tVal, cubicCoeffs);
    }

    function evaluateCubic(x){
        const [a,b,c,d] = cubicCoeffs;
        return ((a*x + b)*x + c)*x + d;
    }

};

/* --------------   Utility Functions -------------- */
function screenToMath(px,py){
    return {
        x:(px - CANVAS_W/2)/SCALE,
        y:(CANVAS_H/2 - py)/SCALE
    };
}
function distance(p1,p2){
    const dx=p1.x-p2.x, dy=p1.y-p2.y;
    return Math.sqrt(dx*dx+dy*dy);
}
function clamp(val,min,max){ return Math.min(max,Math.max(min,val)); }

/* ----------   Linear Solver: 4x4 Gaussian Elimination ---------- */
function solveCubicCoefficients(pts){
    // Build augmented matrix [M|Y]
    let M = pts.map( p => [Math.pow(p.x,3), Math.pow(p.x,2), p.x, 1, p.y] );

    // Forward elimination
    for(let col=0; col<4; col++){
        // pivot
        let pivotRow = col;
        for(let r=col+1;r<4;r++)
            if(Math.abs(M[r][col])>Math.abs(M[pivotRow][col])) pivotRow=r;
        if(pivotRow!==col){ [M[col],M[pivotRow]] = [M[pivotRow],M[col]]; }
        const pivot = M[col][col] || 1e-12;
        // normalize row
        for(let c=col;c<5;c++) M[col][c]/=pivot;
        // eliminate
        for(let r=0;r<4;r++){
            if(r===col) continue;
            const factor = M[r][col];
            for(let c=col;c<5;c++) M[r][c]-=factor*M[col][c];
        }
    }
    return [M[0][4],M[1][4],M[2][4],M[3][4]];
}

function getOsculatingQuadraticCoeffs(t, cubicC){
    const [a,b,c,d] = cubicC;
    const p = 3*a*t + b;
    const q = -3*a*t*t + c;
    const r =  a*t*t*t + d;
    return [p,q,r];      // y = p x^2 + q x + r
}

/* --------------   Reset -------------- */
function resetDemo(){
    sliderT.value = 0.5;
    inputT.value  = 0.5;
    chkFamily.checked = false;
    tVal       = 0.5;
    showFamily = false;
    locators = [
        {x:-4, y: 1,  r:0.15},
        {x:-2, y: 1.8,r:0.15},
        {x: 1, y:-1,  r:0.15},
        {x: 4, y:-1,  r:0.15},
    ];
}

/* ------------------  LAUNCH SKETCH --------------------- */
new p5(sketch);
</script>
</body>
