<body>
<div id="demo-container">

    <!-- ================= CONTROL PANEL ================= -->
    <div id="control-panel">

        <!-- initial volume -->
        <div>
            <label for="slider-v0">initial volume v₀</label>
            <input type="range" id="slider-v0" min="100" max="500" step="1" value="211">
            <span id="v0-readout"></span>
        </div>

        <!-- temperature -->
        <div>
            <label for="slider-T">temperature T (<span id="temp-unit-label">°C</span>)</label>
            <input type="range" id="slider-T" min="-273" max="727" step="1" value="270">
            <span id="T-readout"></span>
        </div>

        <!-- units -->
        <div>
            <label>temperature units</label>
            <div class="radio-group">
                <input type="radio" id="radio-kelvin" name="temp-units" value="K">
                <label for="radio-kelvin">Kelvin</label>

                <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
                <label for="radio-celsius">Celsius</label>
            </div>
        </div>
    </div>

    <!-- ================= VISUALIZATION AREA ================= -->
    <div id="visualization-area">
        <!-- Plot -->
        <div id="plot-container"></div>
        <!-- Piston -->
        <div id="piston-container"></div>
    </div>

</div> <!-- demo-container -->

<script>
/* ------- GLOBAL CONSTANTS & STATE ------- */
const T0_K = 273.15;     // reference temperature in Kelvin
let unit = 'C';          // 'C' or 'K'
let v0 = 211;            // current v0
let T_current = 270;     // slider value (in current unit)
let k = v0 / T0_K;       // proportionality constant (v0/T0)

/* DOM ELEMENTS */
const sliderV0 = document.getElementById('slider-v0');
const sliderT  = document.getElementById('slider-T');
const radioK   = document.getElementById('radio-kelvin');
const radioC   = document.getElementById('radio-celsius');

const v0Readout = document.getElementById('v0-readout');
const TReadout  = document.getElementById('T-readout');
const tempUnitLabel = document.getElementById('temp-unit-label');

/* ------- UTILITY FUNCTIONS ------- */
// convert temperature from current unit -> Kelvin
function toKelvin(T){
    return unit === 'C' ? (parseFloat(T) + 273.15) : parseFloat(T);
}
// convert Kelvin -> current unit
function fromKelvin(TK){
    return unit === 'C' ? (TK - 273.15) : TK;
}
// linear interpolation helper
function lerp(a,b,t){ return a + (b-a)*t; }

/* Map Kelvin temperature to color between purple (128,0,128), yellow (255,255,0), red (255,0,0) */
function tempToColor(TK){
    const low  = [128,0,128];
    const mid  = [255,255,0];
    const high = [255,0,0];

    let c=[0,0,0];
    if(TK<=500){
        const t = TK/500;
        for(let i=0;i<3;i++) c[i]=Math.round(lerp(low[i],mid[i],t));
    }else{
        const t = Math.min((TK-500)/500,1);
        for(let i=0;i<3;i++) c[i]=Math.round(lerp(mid[i],high[i],t));
    }
    return `rgb(${c[0]},${c[1]},${c[2]})`;
}

/* ------------------ PLOTLY ------------------ */
function generateLineData(){
    const x=[], y=[];
    const points = 200;
    const xmin = unit==='C' ? -273 : 0;
    const xmax = unit==='C' ? 727 : 1000;
    const step = (xmax - xmin)/points;
    for(let i=0;i<=points;i++){
        const tVal = xmin + i*step;
        const TK = unit==='C' ? (tVal + 273.15) : tVal;
        x.push(tVal);
        y.push(k*TK);
    }
    return {x,y};
}

function drawPlot(){
    const line = generateLineData();
    const markerColor = tempToColor(toKelvin(T_current));

    const lineTrace = {
        x: line.x,
        y: line.y,
        mode: 'lines',
        line: {width:3, color: 'rgba(0,0,0,0.2)'},
        hoverinfo:'none'
    };

    const markerTrace = {
        x:[T_current],
        y:[k*toKelvin(T_current)],
        mode:'markers',
        marker:{
            size:14,
            color: markerColor,
            line:{color:'#000',width:1}
        },
        hovertemplate:`T: %{x:.2f} ${unit}&nbsp;<br>V: %{y:.2f}<extra></extra>`
    };

    const layout = {
        xaxis:{
            title:`temperature ${unit==='C'?'°C':'K'}`,
            range: unit==='C'?[-273,727]:[0,1000],
            zeroline:false
        },
        yaxis:{
            title:'volume',
            range:[0,2000],
            zeroline:false
        },
        margin:{t:20,r:10,l:60,b:55},
        showlegend:false,
        height:400
    };

    Plotly.react('plot-container',[lineTrace,markerTrace],layout,{responsive:true});
}

/* ------------------ p5.js PISTON ------------------ */
let pistonSketch = function(p){
    let gasColor;
    p.setup = function(){
        const canvas = p.createCanvas(p.select('#piston-container').width, 400);
        canvas.parent('piston-container');
        p.noStroke();
    };

    p.windowResized = function(){
        const newW = document.getElementById('piston-container').clientWidth;
        p.resizeCanvas(newW,400);
    };

    p.draw = function(){
        p.clear();
        const W = p.width, H = p.height;
        const cylWidth = W*0.4;
        const cylX = (W - cylWidth)/2;
        const cylY = 20;
        const cylH = H - 40;

        // Draw cylinder walls
        p.stroke(0);
        p.strokeWeight(2);
        p.line(cylX, cylY, cylX, cylY+cylH);
        p.line(cylX+cylWidth, cylY, cylX+cylWidth, cylY+cylH);
        p.line(cylX, cylY+cylH, cylX+cylWidth, cylY+cylH); // base
        p.noStroke();

        // Calculate piston position based on volume (map 0-2000 -> top-bottom)
        const Vcur = window.currentVolume || k*toKelvin(T_current);
        const pistonY = p.map(Vcur, 0, 2000, cylY+20, cylY+cylH-20);
        const gasH = (cylY+cylH) - pistonY;

        // Gas color
        const TK = window.currentTempK || toKelvin(T_current);
        const col = tempToColor(TK);
        p.fill(col);
        p.rect(cylX, pistonY, cylWidth, gasH);

        // Piston
        p.fill(160);
        p.rect(cylX, pistonY-10, cylWidth, 10); // piston plate

        // Pressure arrows (static)
        p.stroke(255,165,0);
        p.strokeWeight(2);
        for(let i=0;i<5;i++){
            const ax = cylX + (i+1)*(cylWidth/6);
            p.line(ax, pistonY-20, ax, pistonY-10);
            p.line(ax, pistonY-20, ax-4, pistonY-16);
            p.line(ax, pistonY-20, ax+4, pistonY-16);
        }
        p.noStroke();
    };
};
new p5(pistonSketch);

/* ------------------ EVENT HANDLERS ------------------ */
function updateStateFromV0(){
    v0 = parseFloat(sliderV0.value);
    k  = v0 / T0_K;
    v0Readout.textContent = ` ${v0.toFixed(0)}`;
    updateVisuals();
}

function updateStateFromT(){
    T_current = parseFloat(sliderT.value);
    TReadout.textContent = ` ${T_current.toFixed(0)}`;
    updateVisuals();
}

function updateUnit(u){
    if(unit === u) return; // no change
    // Convert current temperature to new unit
    const TK = toKelvin(T_current);
    unit = u;

    // slider attributes
    if(unit==='C'){
        sliderT.min = -273; sliderT.max = 727; sliderT.step=1;
        T_current = TK - 273.15;
        tempUnitLabel.textContent = '°C';
    }else{
        sliderT.min = 0; sliderT.max = 1000; sliderT.step=1;
        T_current = TK;
        tempUnitLabel.textContent = 'K';
    }
    sliderT.value = T_current;
    TReadout.textContent = ` ${T_current.toFixed(0)}`;

    updateVisuals();
}

/* single place to update both plot and piston */
function updateVisuals(){
    // Update current globals for piston sketch
    window.currentTempK = toKelvin(T_current);
    window.currentVolume = k * window.currentTempK;

    drawPlot(); // redraw plot
}

/* ------------------ INIT ------------------ */
function init(){
    v0Readout.textContent = ` ${v0.toFixed(0)}`;
    TReadout.textContent  = ` ${T_current.toFixed(0)}`;
    drawPlot();
}

/* listeners */
sliderV0.addEventListener('input', updateStateFromV0);
sliderT .addEventListener('input', updateStateFromT);

radioK.addEventListener('change', ()=> updateUnit('K'));
radioC.addEventListener('change', ()=> updateUnit('C'));

init();
</script>
</body>
</html>
