        <!-- totals -->
        <span class="info-total-label">Total</span>
        <span id="arc-total">?</span>
        <span class="info-total-label"></span>
        <span id="vertex-total">?</span>
    </div>
</div>

<!-- -------------------- Controls -------------------- -->
<div id="controls-container">
    <!-- Vertex selector -->
    <div>
        <label style="margin-right:8px;">vertex</label>
        <input type="radio" id="radio-a"   name="vertex-select" value="A">
        <label for="radio-a">A</label>

        <input type="radio" id="radio-b"   name="vertex-select" value="B">
        <label for="radio-b">B</label>

        <input type="radio" id="radio-c"   name="vertex-select" value="C" checked>
        <label for="radio-c">C</label>

        <input type="radio" id="radio-none" name="vertex-select" value="None">
        <label for="radio-none">None</label>
    </div>

    <!-- Sliders -->
    <div>
        <label for="slider-a">A</label>
        <input type="range" id="slider-a"   min="0" max="360" step="0.5" value="306">
    </div>
    <div>
        <label for="slider-b">B</label>
        <input type="range" id="slider-b"   min="0" max="360" step="0.5" value="54">
    </div>
    <div>
        <label for="slider-c">C</label>
        <input type="range" id="slider-c"   min="0" max="360" step="0.5" value="180">
    </div>
</div>

<script>
/* ------------------------------------------------------------------ */
/* ------------------------- App ‑ State ---------------------------- */
let angleA = 306;     // degrees
let angleB = 54;
let angleC = 180;
let selectedVertex = 'C';   // 'A' | 'B' | 'C' | 'None'

/* dragging helpers */
let draggingVertex = null;  // 'A' | 'B' | 'C' | null
const dragThreshold = 15;

/* DOM refs */
const sliderA  = document.getElementById('slider-a');
const sliderB  = document.getElementById('slider-b');
const sliderC  = document.getElementById('slider-c');
const radios   = document.querySelectorAll('input[name="vertex-select"]');

/* info panel elements */
const infoEls = {
    arcLabel  : [document.getElementById('arc-label-1'),
                 document.getElementById('arc-label-2'),
                 document.getElementById('arc-label-3')],
    arcValue  : [document.getElementById('arc-value-1'),
                 document.getElementById('arc-value-2'),
                 document.getElementById('arc-value-3')],
    vertLabel : [document.getElementById('vertex-label-1'),
                 document.getElementById('vertex-label-2'),
                 document.getElementById('vertex-label-3')],
    vertValue : [document.getElementById('vertex-value-1'),
                 document.getElementById('vertex-value-2'),
                 document.getElementById('vertex-value-3')],
    arcTotal  : document.getElementById('arc-total'),
    vertTotal : document.getElementById('vertex-total')
};

/* ------------------------------------------------------------------ */
/* ---------------------- Utility functions ------------------------- */
function degToRad(d){ return d * Math.PI / 180; }
function radToDeg(r){ return r * 180 / Math.PI; }
function formatDeg(d){
    return (Math.round(d*10)/10).toFixed(1) + '°';
}

/* Ensure an angle is in [0,360) */
function norm(deg){
    deg = deg % 360;
    return (deg < 0)? deg+360 : deg;
}

/* ------------------------------------------------------------------ */
/* -------------------- p5.js sketch functions ---------------------- */
let canvas, centerX, centerY, R;

function setup(){
    const size = 420;
    canvas = createCanvas(size, size);
    canvas.parent('canvas-container');
    angleMode(DEGREES);     // so we can work directly in degrees
    noLoop();               // draw only when state changes
    R = 150;
    centerX = width/2;
    centerY = height/2;
    redraw();               // initial draw
}

function draw(){
    background(255);

    /* ---------- compute vertex positions ---------- */
    const posA = {x:centerX + R*cos(angleA), y:centerY + R*sin(angleA)};
    const posB = {x:centerX + R*cos(angleB), y:centerY + R*sin(angleB)};
    const posC = {x:centerX + R*cos(angleC), y:centerY + R*sin(angleC)};

    /* ---------- compute arcs & vertex angles ---------- */
    const arcAB = norm(angleB - angleA);
    const arcBC = norm(angleC - angleB);
    const arcCA = norm(angleA - angleC);

    const vertA = arcBC/2;
    const vertB = arcCA/2;
    const vertC = arcAB/2;

    /* ---------- Draw background sectors ---------- */
    noStroke();
    fill('#e0f0e0');    // light green  (Arc AB)
    arc(centerX, centerY, R*2, R*2, angleA, angleB, PIE);

    fill('#f5f5d5');    // light yellow (Arc BC)
    arc(centerX, centerY, R*2, R*2, angleB, angleC, PIE);

    fill('#e0e8f8');    // light blue   (Arc CA)
    arc(centerX, centerY, R*2, R*2, angleC, angleA, PIE);

    /* ---------- Draw main circle ---------- */
    stroke('#666');
    strokeWeight(2);
    noFill();
    circle(centerX, centerY, R*2);

    /* ---------- Triangle ---------- */
    stroke('#666');
    strokeWeight(2);
    fill('rgba(173,216,230,0.5)');
    beginShape();
        vertex(posA.x, posA.y);
        vertex(posB.x, posB.y);
        vertex(posC.x, posC.y);
    endShape(CLOSE);

    /* ---------- Vertex labels ---------- */
    noStroke();
    fill('#00008B');
    textSize(18);
    textStyle(BOLD);
    textAlign(CENTER, CENTER);

    placeLabel('A', posA);
    placeLabel('B', posB);
    placeLabel('C', posC);

    /* ---------- Highlight depending on selected vertex ---------- */
    if(selectedVertex !== 'None'){
        highlightRelation(selectedVertex,
            {A:posA, B:posB, C:posC},
            {arcAB, arcBC, arcCA},
            {vertA, vertB, vertC});
    }

    /* ---------- Center dot ---------- */
    fill('#666');
    noStroke();
    circle(centerX, centerY, 6);

    /* ---------- Update info panel ---------- */
    updateInfoPanel(
        {arcAB, arcBC, arcCA},
        {vertA, vertB, vertC}
    );
}

/* Draw label slightly outside circle */
function placeLabel(label, pos){
    const v = createVector(pos.x-centerX, pos.y-centerY);
    v.setMag(R+18);
    text(label, centerX+v.x, centerY+v.y);
}

/* Highlight the relation for selected vertex */
function highlightRelation(sel, positions, arcs, verts){
    let arcStart, arcEnd, arcVal, vertPos, vertVal;

    strokeWeight(5);

    switch(sel){
        case 'A':
            arcStart = angleB;
            arcEnd   = angleC;
            arcVal   = arcs.arcBC;
            vertPos  = positions.A;
            vertVal  = verts.vertA;
            break;
        case 'B':
            arcStart = angleC;
            arcEnd   = angleA;
            arcVal   = arcs.arcCA;
            vertPos  = positions.B;
            vertVal  = verts.vertB;
            break;
        case 'C':
        default:
            arcStart = angleA;
            arcEnd   = angleB;
            arcVal   = arcs.arcAB;
            vertPos  = positions.C;
            vertVal  = verts.vertC;
    }

    /* Central angle θ (green) */
    noFill();
    stroke('#008000');
    arc(centerX, centerY, R*2+8, R*2+8, arcStart, arcEnd);

    /* Arrow head at arcEnd */
    const arrowLen = 14;
    const endVec = createVector(cos(arcEnd), sin(arcEnd)).mult(R+4);
    const endPt = {x:centerX+endVec.x, y:centerY+endVec.y};
    push();
        translate(endPt.x, endPt.y);
        rotate(arcEnd+90);
        fill('#008000');
        noStroke();
        triangle(0,0, -arrowLen/2, -arrowLen, arrowLen/2, -arrowLen);
    pop();

    /* θ label */
    noStroke();
    fill('#008000');
    textSize(14);
    text('θ = '+formatDeg(arcVal), centerX, centerY- R - 25);

    /* Inscribed angle θ/2 (red) at selected vertex */
    const toStart = angleOfLine(vertPos, sel==='A'?positions.C: sel==='B'?positions.A:positions.B);
    const toEnd   = angleOfLine(vertPos, sel==='A'?positions.B: sel==='B'?positions.C:positions.A);

    stroke('#FF0000');
    noFill();
    arc(vertPos.x, vertPos.y, 60, 60, toStart, toEnd);

    /* label θ/2 */
    noStroke();
    fill('#FF0000');
    const midAng = (toStart + toEnd)/2;
    let labelVec = createVector(cos(midAng), sin(midAng)).mult(42);
    text('θ/2 = '+formatDeg(vertVal),
         vertPos.x + labelVec.x,
         vertPos.y + labelVec.y);
}

/* angle from p to q (in degrees) */
function angleOfLine(p, q){
    return atan2(q.y - p.y, q.x - p.x);
}

/* ------------------------------------------------------------------ */
/* ------------------------- Event handlers ------------------------- */
sliderA.addEventListener('input', e=>{
    angleA = parseFloat(e.target.value);
    redraw();
});
sliderB.addEventListener('input', e=>{
    angleB = parseFloat(e.target.value);
    redraw();
});
sliderC.addEventListener('input', e=>{
    angleC = parseFloat(e.target.value);
    redraw();
});

radios.forEach(r=>{
    r.addEventListener('change', e=>{
        if(e.target.checked){
            selectedVertex = e.target.value;
            redraw();
        }
    });
});

/* dragging on canvas */
function mousePressed(){
    if(!overCanvas()) return;

    if(dist(mouseX, mouseY, centerX + R*cos(angleA), centerY + R*sin(angleA)) < dragThreshold){
        draggingVertex = 'A';
    }else if(dist(mouseX, mouseY, centerX + R*cos(angleB), centerY + R*sin(angleB)) < dragThreshold){
        draggingVertex = 'B';
    }else if(dist(mouseX, mouseY, centerX + R*cos(angleC), centerY + R*sin(angleC)) < dragThreshold){
        draggingVertex = 'C';
    }
}

function mouseDragged(){
    if(!draggingVertex || !overCanvas()) return;

    let ang = norm(radToDeg(atan2(mouseY - centerY, mouseX - centerX)));
    switch(draggingVertex){
        case 'A': angleA = ang; sliderA.value = angleA; break;
        case 'B': angleB = ang; sliderB.value = angleB; break;
        case 'C': angleC = ang; sliderC.value = angleC; break;
    }
    redraw();
}

function mouseReleased(){
    draggingVertex = null;
}

/* Check if mouse is within canvas bounds */
function overCanvas(){
    return (mouseX>=0 && mouseX<=width && mouseY>=0 && mouseY<=height);
}

/* ------------------------------------------------------------------ */
/* ---------------------- Info panel update ------------------------- */
function updateInfoPanel(arcs, verts){
    const order = [
        {arc:'Arc AB', valArc:arcs.arcAB, vert:'Vertex C', valVert:verts.vertC},
        {arc:'Arc BC', valArc:arcs.arcBC, vert:'Vertex A', valVert:verts.vertA},
        {arc:'Arc CA', valArc:arcs.arcCA, vert:'Vertex B', valVert:verts.vertB}
    ];

    if(selectedVertex==='None'){
        // show all three
        for(let i=0;i<3;i++){
