</style>
</head>
<body>

<div id="main-container">
    <!-- CONTROL PANEL ---------------------------------------------------- -->
    <div id="control-panel">
        <!-- τ -->
        <div class="control-group">
            <label>&tau;</label>
            <button id="btn-dec-tau">-</button>
            <input id="slider-tau" type="range" min="0.01" max="5" step="0.01" value="1.0">
            <button id="btn-inc-tau">+</button>
            <span id="value-tau">1.00</span>
        </div>
        <!-- φ -->
        <div class="control-group">
            <label>&phi;</label>
            <button id="btn-dec-phi">-</button>
            <input id="slider-phi" type="range" min="0.1" max="10" step="0.1" value="2.0">
            <button id="btn-inc-phi">+</button>
            <span id="value-phi">2.0</span>
        </div>
        <!-- β -->
        <div class="control-group">
            <label>&beta;</label>
            <button id="btn-dec-beta">-</button>
            <input id="slider-beta" type="range" min="-0.5" max="0.5" step="0.01" value="0.2">
            <button id="btn-inc-beta">+</button>
            <span id="value-beta">0.20</span>
        </div>
        <!-- γ -->
        <div class="control-group">
            <label>&gamma;</label>
            <button id="btn-dec-gamma">-</button>
            <input id="slider-gamma" type="range" min="0" max="40" step="1" value="20">
            <button id="btn-inc-gamma">+</button>
            <span id="value-gamma">20</span>
        </div>
        <!-- ξ -->
        <div class="control-group">
            <label>&xi;</label>
            <button id="btn-dec-xi">-</button>
            <input id="slider-xi" type="range" min="0.001" max="1.0" step="0.001" value="0.400">
            <button id="btn-inc-xi">+</button>
            <span id="value-xi">0.400</span>
        </div>
    </div>

    <!-- VISUALIZATION AREA ------------------------------------------------ -->
    <div id="visualization-panel">
        <div id="plot-concentration"></div>
        <div id="plot-temperature"></div>
    </div>
</div>

<script>
/* --------------------------- NUMERICAL SOLVER --------------------------- */
const GRID_POINTS = 101;           // spatial discretisation
const dx = 1.0 / (GRID_POINTS-1);  // space step

// Enforces boundary conditions (Dirichlet at surface, symmetry at center)
function enforceBoundary(c, th){
    c[GRID_POINTS-1] = 1;
    th[GRID_POINTS-1] = 1;
    // Neumann at center is handled in derivative by ghost point
}

// Calculates spatial derivatives and RHS of PDEs
function derivatives(c, th, dc, dth, phi, beta, gamma){
    const dx2 = dx*dx;

    for(let i=0;i<GRID_POINTS;i++){
        if(i === GRID_POINTS-1){       // fixed boundary
            dc[i]=0; dth[i]=0;
            continue;
        }
        let c_im1, c_ip1, t_im1, t_ip1;
        // left neighbour (ghost for center)
        if(i===0){
            c_im1 = c[1];
            t_im1 = th[1];
        }else{
            c_im1 = c[i-1];
            t_im1 = th[i-1];
        }
        // right neighbour (always exists, because i != last)
        c_ip1 = c[i+1];
        t_ip1 = th[i+1];

        const d2c = (c_im1 - 2*c[i] + c_ip1)/dx2;
        const d2t = (t_im1 - 2*th[i] + t_ip1)/dx2;

        const reaction = phi*phi * c[i] * Math.exp( gamma * (1 - 1/Math.max(th[i],1e-9)) );

        dc[i]  = d2c - reaction;
        dth[i] = d2t + beta*reaction;
    }
}

// RK4 time stepping
function simulate(tau, phi, beta, gamma){
    // initialise arrays
    let c   = new Float64Array(GRID_POINTS);
    let th  = new Float64Array(GRID_POINTS);
    // IC: c=0 (except boundary), th=1
    for(let i=0;i<GRID_POINTS;i++){ c[i]=0; th[i]=1; }
    enforceBoundary(c,th);

    const steps = Math.max(10, Math.round(200*tau)); // ~200 steps per unit τ
    const dt = tau/steps;

    const k1c = new Float64Array(GRID_POINTS);
    const k1t = new Float64Array(GRID_POINTS);
    const k2c = new Float64Array(GRID_POINTS);
    const k2t = new Float64Array(GRID_POINTS);
    const k3c = new Float64Array(GRID_POINTS);
    const k3t = new Float64Array(GRID_POINTS);
    const k4c = new Float64Array(GRID_POINTS);
    const k4t = new Float64Array(GRID_POINTS);

    const tmpC = new Float64Array(GRID_POINTS);
    const tmpT = new Float64Array(GRID_POINTS);

    for(let step=0; step<steps; step++){
        // k1
        derivatives(c,th,k1c,k1t,phi,beta,gamma);

        // k2
        for(let i=0;i<GRID_POINTS;i++){ tmpC[i]=c[i]+0.5*dt*k1c[i]; tmpT[i]=th[i]+0.5*dt*k1t[i]; }
        enforceBoundary(tmpC,tmpT);
        derivatives(tmpC,tmpT,k2c,k2t,phi,beta,gamma);

        // k3
        for(let i=0;i<GRID_POINTS;i++){ tmpC[i]=c[i]+0.5*dt*k2c[i]; tmpT[i]=th[i]+0.5*dt*k2t[i]; }
        enforceBoundary(tmpC,tmpT);
        derivatives(tmpC,tmpT,k3c,k3t,phi,beta,gamma);

        // k4
        for(let i=0;i<GRID_POINTS;i++){ tmpC[i]=c[i]+dt*k3c[i]; tmpT[i]=th[i]+dt*k3t[i]; }
        enforceBoundary(tmpC,tmpT);
        derivatives(tmpC,tmpT,k4c,k4t,phi,beta,gamma);

        // advance solution
        for(let i=0;i<GRID_POINTS;i++){
            c[i]  += dt*(k1c[i] + 2*k2c[i] + 2*k3c[i] + k4c[i])/6;
            th[i] += dt*(k1t[i] + 2*k2t[i] + 2*k3t[i] + k4t[i])/6;
        }
        enforceBoundary(c,th);
    }

    const x = Array.from({length:GRID_POINTS}, (_,i)=>i*dx);
    return {x, c:Array.from(c), th:Array.from(th)};
}

/* ----------------------------- PLOTTING -------------------------------- */
let dataCache = null;  // holds latest simulation result

function makePlots(){
    const xiSlider = document.getElementById('slider-xi');
    const xiVal    = parseFloat(xiSlider.value);
    const tauVal   = parseFloat(document.getElementById('slider-tau').value);

    // Interpolate c and th at chosen ξ
    const idx = Math.min(Math.floor(xiVal/dx), GRID_POINTS-2);
    const frac = (xiVal - idx*dx)/dx;
    const cXi  = dataCache.c[idx]*(1-frac) + dataCache.c[idx+1]*frac;
    const tXi  = dataCache.th[idx]*(1-frac) + dataCache.th[idx+1]*frac;

    // Common
    const markerC = {x:[xiVal,xiVal,0], y:[0,cXi,cXi], mode:'lines', line:{color:'#333',width:1}, hoverinfo:'skip'};
    const markerT = {x:[xiVal,xiVal,0], y:[Math.min(...dataCache.th),tXi,tXi], mode:'lines', line:{color:'#333',width:1}, hoverinfo:'skip'};

    // Concentration plot
    const concData = [
        {x:dataCache.x, y:dataCache.c, mode:'lines', line:{color:'#1f77b4'}, name:'c(ξ)'},
        markerC
    ];
    const concLayout = {
        margin:{l:60,r:10,b:40,t:10},
        xaxis:{title:'ξ', range:[0,1]},
        yaxis:{title:'c', range:[0,1.1]},
        annotations:[{
            x:0.95, y:0.5, xref:'paper', yref:'paper',
            text:`τ = ${tauVal.toFixed(2)}<br>ξ = ${xiVal.toFixed(3)}<br>c = ${cXi.toFixed(5)}`,
            showarrow:false, align:'left'
        }],
        showlegend:false
    };

    // Temperature plot
    const tempData = [
        {x:dataCache.x, y:dataCache.th, mode:'lines', line:{color:'#d62728'}, name:'θ(ξ)'},
        markerT
    ];
    const ymin = Math.min(...dataCache.th);
    const ymax = Math.max(...dataCache.th);

    const tempLayout = {
        margin:{l:60,r:10,b:40,t:10},
        xaxis:{title:'ξ', range:[0,1]},
        yaxis:{title:'θ', autorange:true},
        annotations:[{
            x:0.95, y:0.5, xref:'paper', yref:'paper',
            text:`τ = ${tauVal.toFixed(2)}<br>ξ = ${xiVal.toFixed(3)}<br>θ = ${tXi.toFixed(5)}`,
            showarrow:false, align:'left'
        }],
        showlegend:false
    };

    Plotly.react('plot-concentration', concData, concLayout, {displayModeBar:false});
    Plotly.react('plot-temperature',   tempData, tempLayout, {displayModeBar:false});
}

// Update only marker position & annotations (fast)
function updateXiMarker(){
    if(!dataCache) return;
    const xiSlider = document.getElementById('slider-xi');
    const xiVal    = parseFloat(xiSlider.value);
    const tauVal   = parseFloat(document.getElementById('slider-tau').value);

    const idx = Math.min(Math.floor(xiVal/dx), GRID_POINTS-2);
    const frac = (xiVal - idx*dx)/dx;
    const cXi  = dataCache.c[idx]*(1-frac) + dataCache.c[idx+1]*frac;
    const tXi  = dataCache.th[idx]*(1-frac) + dataCache.th[idx+1]*frac;

    // Concentration marker
    Plotly.restyle('plot-concentration',
        {x:[[xiVal,xiVal,0]], y:[[0,cXi,cXi]]}, 1);
    Plotly.relayout('plot-concentration', {
        'annotations[0].text':`τ = ${tauVal.toFixed(2)}<br>ξ = ${xiVal.toFixed(3)}<br>c = ${cXi.toFixed(5)}`
    });

    // Temperature marker
    Plotly.restyle('plot-temperature',
        {x:[[xiVal,xiVal,0]], y:[[Math.min(...dataCache.th),tXi,tXi]]}, 1);
    Plotly.relayout('plot-temperature', {
        'annotations[0].text':`τ = ${tauVal.toFixed(2)}<br>ξ = ${xiVal.toFixed(3)}<br>θ = ${tXi.toFixed(5)}`
    });
}

/* ------------------------------ UI HOOKS ------------------------------- */

// Mapping buttons to sliders
const btnSliderMap = {
    'btn-dec-tau':'slider-tau',   'btn-inc-tau':'slider-tau',
    'btn-dec-phi':'slider-phi',   'btn-inc-phi':'slider-phi',
    'btn-dec-beta':'slider-beta', 'btn-inc-beta':'slider-beta',
    'btn-dec-gamma':'slider-gamma','btn-inc-gamma':'slider-gamma',
    'btn-dec-xi':'slider-xi',     'btn-inc-xi':'slider-xi'
};

// Attach button handlers
Object.keys(btnSliderMap).forEach(btnId=>{
    const isInc = btnId.includes('inc');
    document.getElementById(btnId).addEventListener('click',()=>{
        const slider = document.getElementById(btnSliderMap[btnId]);
        const step = parseFloat(slider.step);
        let newVal = parseFloat(slider.value) + (isInc?step:-step);
        if(newVal < parseFloat(slider.min)) newVal = slider.min;
        if(newVal > parseFloat(slider.max)) newVal = slider.max;
        slider.value = newVal;
        slider.dispatchEvent(new Event('input',{bubbles:true}));
    });
});

// Slider handlers
document.querySelectorAll('#control-panel input[type=range]').forEach(slider=>{
    slider.addEventListener('input', e=>{
        const id = e.target.id;
        // update display span
        document.getElementById('value-'+id.split('-')[1])
                .textContent = parseFloat(e.target.value).toFixed(e.target.step.length-2);

        if(id === 'slider-xi'){
            updateXiMarker();
        }else{ // parameter change -> new simulation
            runSimulationAndPlot();
        }
    });
});

// Run simulation with current slider values
function runSimulationAndPlot(){
    const tau   = parseFloat(document.getElementById('slider-tau').value);
    const phi   = parseFloat(document.getElementById('slider-phi').value);
    const beta  = parseFloat(document.getElementById('slider-beta').value);
    const gamma = parseFloat(document.getElementById('slider-gamma').value);

    // time-intensive, so small delay to keep UI responsive
    setTimeout(()=>{
        dataCache = simulate(tau,phi,beta,gamma);
        makePlots();
    },0);
}

/* --------------------------- INITIALISATION ---------------------------- */
window.addEventListener('load',()=>{
    // initialise value displays
    document.querySelectorAll('#control-panel input[type=range]').forEach(slider=>{
        document.getElementById('value-'+slider.id.split('-')[1])
                .textContent = parseFloat(slider.value).toFixed(slider.step.length-2);
    });
    runSimulationAndPlot();
});
</script>
</body>
</html>
