                    </thead>
                    <tbody id="data-table-body"></tbody>
                </table>
            </div>
        </div>
        <div id="results-display">
            <p id="result-u">rejuvenation bias u = —</p>
            <p id="result-p">site productivity p = —</p>
        </div>
    </div>
</div>

<script>
    // ---------- STATIC DATA ----------
    const ages   = [30, 35, 40, 45, 50, 55];
    const volumes= [289, 445, 565, 704, 833, 924];

    // ---------- INITIAL POPULATION ----------
    function populateTable(){
        const tbody = document.getElementById('data-table-body');
        tbody.innerHTML = '';
        for(let i=0;i<ages.length;i++){
            const tr=document.createElement('tr');
            tr.innerHTML = `<td>${ages[i]}</td><td>${volumes[i]}</td>`;
            tbody.appendChild(tr);
        }
    }

    populateTable();

    // ---------- PLOT INITIALIZATION ----------
    const tMin=15, tMax=80, tStep=0.5;
    const tArray = [];
    for(let t=tMin;t<=tMax;t+=tStep){ tArray.push(t); }

    const dataPointsTrace = {
        x: ages,
        y: volumes,
        mode:'markers',
        marker:{color:'blue', size:8},
        hovertemplate:'Age %{x}<br>Volume %{y}<extra></extra>'
    };

    const richardsTrace = {
        x: tArray,
        y: new Array(tArray.length).fill(null), // placeholder
        mode:'lines',
        line:{color:'orange', width:3}
    };

    const fourFifthTrace = {
        x: tArray,
        y: new Array(tArray.length).fill(null), // placeholder
        mode:'lines',
        line:{color:'blue', width:2, dash:'solid'}
    };

    const layout = {
        title:'forest growth curve',
        xaxis:{
            title:'stand age (years)',
            range:[tMin,tMax],
            gridcolor:'lightgrey',
            zeroline:false,
            dtick:5
        },
        yaxis:{
            title:'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
            range:[0,1600],
            gridcolor:'lightgrey',
            zeroline:false
        },
        plot_bgcolor:'#FFFFF0',
        showlegend:false,
        margin:{l:70,r:20,t:40,b:60}
    };

    Plotly.newPlot('plot-container',[dataPointsTrace,richardsTrace,fourFifthTrace],layout,{responsive:true});

    // ---------- UPDATE LOGIC ----------
    const sliders = [
        {id:'slider-bmax', display:'value-bmax', decimals:0},
        {id:'slider-b0',   display:'value-b0',   decimals:1},
        {id:'slider-r',    display:'value-r',    decimals:3},
        {id:'slider-beta', display:'value-beta', decimals:3}
    ];

    sliders.forEach(s=>{
        document.getElementById(s.id).addEventListener('input',update);
    });

    function formatNumber(num,dec){
        return Number(num).toFixed(dec);
    }

    function update(){
        // read sliders and update value displays
        const b_max = parseFloat(document.getElementById('slider-bmax').value);
        const b0    = parseFloat(document.getElementById('slider-b0').value);
        const r     = parseFloat(document.getElementById('slider-r').value);
        const beta  = parseFloat(document.getElementById('slider-beta').value);

        sliders.forEach(s=>{
            const value=parseFloat(document.getElementById(s.id).value);
            document.getElementById(s.display).textContent = formatNumber(value,s.decimals);
        });

        // calculations
        const M = Math.pow(b_max/b0, beta) - 1;

        // Richards curve
        const V_richards = tArray.map(t=>{
            const denom = Math.pow(1 + M*Math.exp(-r*t), 1/beta);
            return b_max/denom;
        });

        // Inflection point
        let u,p;
        let V_fourFifth = new Array(tArray.length).fill(null);
        try{
            const t_i = Math.log(M*beta)/r;
            const V_i = b_max * Math.pow((beta+1)/(2*beta), 1/beta);
            const V_i_prime = (r/beta) * V_i * (1 - Math.pow(V_i/b_max, beta));

            u = t_i - 0.8 * V_i / V_i_prime;

            if(t_i - u > 0){
                p = V_i / Math.pow(t_i - u, 0.8);

                V_fourFifth = tArray.map(t=>{
                    return t>u ? p*Math.pow(t-u,0.8) : null;
                });
            }else{
                u = NaN;
                p = NaN;
            }
        }catch(e){
            u = NaN;
            p = NaN;
        }

        // Update results display
        document.getElementById('result-u').textContent = `rejuvenation bias u = ${isFinite(u)?formatNumber(u,4):'—'}`;
        document.getElementById('result-p').textContent = `site productivity p = ${isFinite(p)?formatNumber(p,4):'—'}`;

        // Update plot
        Plotly.react('plot-container',[
            dataPointsTrace,
            {...richardsTrace, y: V_richards},
            {...fourFifthTrace, y: V_fourFifth}
        ],layout);
    }

    // run initial update
    update();

    // After content loads, typeset MathJax
    window.MathJax && MathJax.typeset();
</script>
