<script src="https://unpkg.com/three@0.160.0/build/three.min.js"></script>

<script>
/* ------------------- THREE.JS SCENE SETUP ------------------- */
const canvas   = document.getElementById('main-canvas');
const renderer = new THREE.WebGLRenderer({canvas, antialias:true});
renderer.setPixelRatio(window.devicePixelRatio);

const scene  = new THREE.Scene();
scene.background = new THREE.Color(0xffffff);

const camera = new THREE.PerspectiveCamera(45, 2, 0.1, 2000); // aspect fixed later
camera.position.set(0, 0, 300);

const ambient = new THREE.AmbientLight(0xffffff, 0.6);
scene.add(ambient);

const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
dirLight.position.set(100, 100, 200);
scene.add(dirLight);

const mainGroup = new THREE.Group();
scene.add(mainGroup);

/* ------------------- HELPERS ------------------- */
function regularPolygonShape(sides, radius=10){
    const shape = new THREE.Shape();
    const angleStep = Math.PI*2 / sides;
    shape.moveTo(radius,0);
    for(let i=1;i<=sides;i++){
        const theta = i*angleStep;
        shape.lineTo(radius*Math.cos(theta), radius*Math.sin(theta));
    }
    return shape;
}

function getSlider(id){ return document.getElementById(id); }

const sliderUnits     = getSlider('slider-units');
const sliderAngle     = getSlider('slider-angle');
const sliderConical   = getSlider('slider-conical');
const sliderRotateX   = getSlider('slider-rotate-x');
const sliderRotateZ   = getSlider('slider-rotate-z');

/* ------------------- REDRAW FUNCTION ------------------- */
function redraw(){

    // 1. read control values
    const nSides   = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
    const nUnits   = parseInt(sliderUnits.value);
    const angleDeg = parseFloat(sliderAngle.value);
    const conical  = parseFloat(sliderConical.value);
    const rotX     = parseFloat(sliderRotateX.value) * Math.PI/180;
    const rotZ     = parseFloat(sliderRotateZ.value) * Math.PI/180;

    // 2. remove old meshes
    while(mainGroup.children.length){
        const obj = mainGroup.children.pop();
        obj.geometry.dispose();
        obj.material.dispose();
    }

    // 3. base geometry
    const baseShape = regularPolygonShape(nSides);
    const baseGeometry = new THREE.ShapeGeometry(baseShape);

    // 4. loop segments
    const advanceRad = angleDeg * Math.PI/180;
    const TWO_PI = Math.PI*2;
    for(let i=0; i<nUnits; i++){
        const angle   = i * advanceRad;
        const radius  = 4*Math.sqrt(i);
        const scale   = 0.5*Math.log(i+2);

        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const z = conical * radius;

        const material = new THREE.MeshStandardMaterial({color:0xffffff});
        material.color.setHSL((angle/TWO_PI)%1.0, 0.8, 0.6);

        const mesh = new THREE.Mesh(baseGeometry, material);

        mesh.position.set(x, y, z);
        mesh.scale.set(scale, scale, scale);
        mesh.rotation.z = angle; // align to tangent

        mainGroup.add(mesh);
    }

    // 5. master rotations
    mainGroup.rotation.set(rotX, 0, rotZ);

    // 6. render
    render();
}

/* ------------------- RENDER & RESIZE ------------------- */
function resizeRenderer(){
    const container = document.getElementById('canvas-container');
    const width  = container.clientWidth;
    const height = container.clientHeight;
    const needResize = canvas.width !== width || canvas.height !== height;
    if(needResize){
        renderer.setSize(width, height, false);
        camera.aspect = width/height;
        camera.updateProjectionMatrix();
    }
    return needResize;
}

function render(){
    resizeRenderer();
    renderer.render(scene, camera);
}

/* ------------------- EVENT BINDINGS ------------------- */
function attachSliderListener(slider){
    slider.addEventListener('input', redraw);
}
[sliderUnits, sliderAngle, sliderConical, sliderRotateX, sliderRotateZ].forEach(attachSliderListener);

document.querySelectorAll('input[name="base-polygon"]').forEach(radio=>{
    radio.addEventListener('change', redraw);
});

/* plus buttons */
function plusHandler(slider){
    const step = parseFloat(slider.step)||1;
    slider.value = parseFloat(slider.value)+step;
    slider.dispatchEvent(new Event('input'));
}
document.getElementById('btn-units-plus'   ).addEventListener('click', ()=>plusHandler(sliderUnits   ));
document.getElementById('btn-angle-plus'   ).addEventListener('click', ()=>plusHandler(sliderAngle   ));
document.getElementById('btn-conical-plus' ).addEventListener('click', ()=>plusHandler(sliderConical ));
document.getElementById('btn-rotate-x-plus').addEventListener('click', ()=>plusHandler(sliderRotateX ));
document.getElementById('btn-rotate-z-plus').addEventListener('click', ()=>plusHandler(sliderRotateZ ));

/* ------------------- INITIALIZE ------------------- */
window.addEventListener('resize', ()=>{
    resizeRenderer();
    render();
});
redraw(); // first draw
</script>

</body>
</html>
