<body>
<div id="main-container">
    <!-- Control Panel -->
    <div id="control-panel">
        <div id="lattice-controls">
            <label>lattice:</label>
            <button id="btn-sc" class="active">SC</button>
            <button id="btn-bcc">BCC</button>
            <button id="btn-fcc">FCC</button>
        </div>

        <div id="miller-controls">
            <label>Miller indices:</label>
            <label for="input-h">h</label>
            <input type="number" id="input-h" value="1" min="-10" max="10" step="1">
            <label for="input-k">k</label>
            <input type="number" id="input-k" value="1" min="-10" max="10" step="1">
            <label for="input-l">l</label>
            <input type="number" id="input-l" value="1" min="-10" max="10" step="1">
        </div>

        <div id="opacity-controls">
            <label for="slider-opacity">plane opacity</label>
            <input type="range" id="slider-opacity" value="0.8" min="0" max="1" step="0.01">
        </div>
    </div>

    <!-- Visualization -->
    <div id="canvas-container"></div>
</div>

<!-- three.js and OrbitControls -->
<script src="https://unpkg.com/three@0.150.0/build/three.min.js"></script>
<script src="https://unpkg.com/three@0.150.0/examples/js/controls/OrbitControls.js"></script>

<script>
// === GLOBALS ======================================================
let scene, camera, renderer, controls;
let atomsGroup = new THREE.Group();
let planesGroup = new THREE.Group();
let unitCellEdges;
let latticeType = 'SC';    // default
const canvasContainer = document.getElementById('canvas-container');

// --- INITIALIZATION ----------------------------------------------
initScene();
attachEventListeners();
updateScene();
animate();

// === FUNCTION DEFINITIONS ========================================
function initScene(){
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xe0e0e0);

    const W = canvasContainer.clientWidth;
    const H = canvasContainer.clientHeight;
    camera = new THREE.PerspectiveCamera(45, W/H, 0.1, 100);
    camera.position.set(2,2,2);

    renderer = new THREE.WebGLRenderer({antialias:true});
    renderer.setSize(W, H);
    canvasContainer.appendChild(renderer.domElement);

    controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.08;

    // Lights
    scene.add(new THREE.AmbientLight(0xffffff, 0.5));
    const dir1 = new THREE.DirectionalLight(0xffffff, 0.8);
    dir1.position.set(2,3,4);
    scene.add(dir1);
    const dir2 = new THREE.DirectionalLight(0xffffff, 0.6);
    dir2.position.set(-2,-3,-4);
    scene.add(dir2);

    // Unit cell wireframe
    const cubeGeom = new THREE.BoxGeometry(1,1,1);
    const edges = new THREE.EdgesGeometry(cubeGeom);
    const lineMat = new THREE.LineBasicMaterial({color:0x000000});
    unitCellEdges = new THREE.LineSegments(edges, lineMat);
    scene.add(unitCellEdges);

    // Axes
    const axisLen = 0.7;
    scene.add(new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), axisLen, 0xff0000));
    scene.add(new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), axisLen, 0x00ff00));
    scene.add(new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), axisLen, 0x0000ff));

    scene.add(atomsGroup);
    scene.add(planesGroup);

    window.addEventListener('resize', onWindowResize);
}

function onWindowResize(){
    const W = canvasContainer.clientWidth;
    const H = canvasContainer.clientHeight;
    camera.aspect = W/H;
    camera.updateProjectionMatrix();
    renderer.setSize(W, H);
}

function attachEventListeners(){
    document.getElementById('btn-sc').addEventListener('click', ()=>selectLattice('SC'));
    document.getElementById('btn-bcc').addEventListener('click', ()=>selectLattice('BCC'));
    document.getElementById('btn-fcc').addEventListener('click', ()=>selectLattice('FCC'));

    document.getElementById('input-h').addEventListener('change', updateScene);
    document.getElementById('input-k').addEventListener('change', updateScene);
    document.getElementById('input-l').addEventListener('change', updateScene);

    document.getElementById('slider-opacity').addEventListener('input', updatePlaneOpacity);
}

function selectLattice(type){
    latticeType = type;
    // button active styles
    ['btn-sc','btn-bcc','btn-fcc'].forEach(id=>{
        document.getElementById(id).classList.toggle('active', id.toUpperCase().includes(type.toLowerCase()));
    });
    updateScene();
}

function updatePlaneOpacity(){
    const opacity = parseFloat(document.getElementById('slider-opacity').value);
    planesGroup.children.forEach(mesh=>{
        mesh.material.opacity = opacity;
    });
}

function updateScene(){
    // Clear previous lattice objects
    atomsGroup.clear();
    planesGroup.clear();

    const h = parseInt(document.getElementById('input-h').value);
    const k = parseInt(document.getElementById('input-k').value);
    const l = parseInt(document.getElementById('input-l').value);
    const opacity = parseFloat(document.getElementById('slider-opacity').value);

    drawAtoms(latticeType);
    if(!(h===0 && k===0 && l===0)){
        drawPlanes(h,k,l, opacity);
    }
}

function drawAtoms(type){
    const sphereGeom = new THREE.SphereGeometry(0.05, 16, 16);
    const sphereMat = new THREE.MeshPhongMaterial({color:0x006060, specular:0x88ffff, shininess:80});
    let positions = [];

    switch(type){
        case 'SC':
            positions = [
                [-0.5,-0.5,-0.5],[0.5,-0.5,-0.5],[0.5,0.5,-0.5],[-0.5,0.5,-0.5],
                [-0.5,-0.5,0.5],[0.5,-0.5,0.5],[0.5,0.5,0.5],[-0.5,0.5,0.5]
            ];
            break;
        case 'BCC':
            positions = [
                [-0.5,-0.5,-0.5],[0.5,-0.5,-0.5],[0.5,0.5,-0.5],[-0.5,0.5,-0.5],
                [-0.5,-0.5,0.5],[0.5,-0.5,0.5],[0.5,0.5,0.5],[-0.5,0.5,0.5],
                [0,0,0]
            ];
            break;
        case 'FCC':
            positions = [
                [-0.5,-0.5,-0.5],[0.5,-0.5,-0.5],[0.5,0.5,-0.5],[-0.5,0.5,-0.5],
                [-0.5,-0.5,0.5],[0.5,-0.5,0.5],[0.5,0.5,0.5],[-0.5,0.5,0.5],
                [0.5,0,0],[ -0.5,0,0],
                [0,-0.5,0],[0,0.5,0],
                [0,0,0.5],[0,0,-0.5]
            ];
            break;
    }

    positions.forEach(p=>{
        const mesh = new THREE.Mesh(sphereGeom, sphereMat);
        mesh.position.set(...p);
        atomsGroup.add(mesh);
    });
}

function drawPlanes(h,k,l, opacity){
    // Reduce miller indices to simplest form
    const g = gcd3(Math.abs(h), Math.abs(k), Math.abs(l));
    const h1 = h/g, k1 = k/g, l1 = l/g;

    // Determine n range across cube vertices
    const vertices = [
        new THREE.Vector3(-0.5,-0.5,-0.5),
        new THREE.Vector3(0.5,-0.5,-0.5),
        new THREE.Vector3(0.5,0.5,-0.5),
        new THREE.Vector3(-0.5,0.5,-0.5),
        new THREE.Vector3(-0.5,-0.5,0.5),
        new THREE.Vector3(0.5,-0.5,0.5),
        new THREE.Vector3(0.5,0.5,0.5),
        new THREE.Vector3(-0.5,0.5,0.5)
    ];
    let min = Infinity, max = -Infinity;
    vertices.forEach(v=>{
        const d = h1*v.x + k1*v.y + l1*v.z;
        min = Math.min(min,d);
        max = Math.max(max,d);
    });
    const nMin = Math.floor(min);
    const nMax = Math.ceil(max);

    for(let n=nMin; n<=nMax; n++){
        const points = intersectPlaneCube(h1,k1,l1,n);
        if(points.length>=3){
            const mesh = buildPlaneMesh(points, new THREE.Vector3(h1,k1,l1), opacity);
            planesGroup.add(mesh);
        }
    }
}

// returns array of Vector3 points (unique) on intersection polygon
function intersectPlaneCube(h,k,l,n){
    const cubeVerts = [
        new THREE.Vector3(-0.5,-0.5,-0.5),
        new THREE.Vector3(0.5,-0.5,-0.5),
        new THREE.Vector3(0.5,0.5,-0.5),
        new THREE.Vector3(-0.5,0.5,-0.5),
        new THREE.Vector3(-0.5,-0.5,0.5),
        new THREE.Vector3(0.5,-0.5,0.5),
        new THREE.Vector3(0.5,0.5,0.5),
        new THREE.Vector3(-0.5,0.5,0.5)
    ];
    const edgesIdx = [
        [0,1],[1,2],[2,3],[3,0],
        [4,5],[5,6],[6,7],[7,4],
        [0,4],[1,5],[2,6],[3,7]
    ];
    const pts=[];
    edgesIdx.forEach(pair=>{
        const v1 = cubeVerts[pair[0]];
        const v2 = cubeVerts[pair[1]];
        const d1 = h*v1.x + k*v1.y + l*v1.z - n;
        const d2 = h*v2.x + k*v2.y + l*v2.z - n;
        const denom = d1 - d2;
        if(Math.abs(denom) < 1e-8) return; // parallel or lies on plane
        const t = d1 / denom; // from v1 towards v2
        if(t>=0 && t<=1){
            const p = new THREE.Vector3().copy(v1).lerp(v2, t);
            addUniquePoint(pts, p);
        }
    });
    return sortPointsOnPlane(pts, new THREE.Vector3(h,k,l));
}

// prevent duplicate points
function addUniquePoint(arr, p){
    const eps = 1e-6;
    for(let q of arr){
        if(q.distanceToSquared(p)<eps*eps) return;
    }
    arr.push(p);
}

function sortPointsOnPlane(points, normal){
    if(points.length<3) return points;
    // create orthonormal basis (u,v) on plane
    const n = normal.clone().normalize();
    const aux = Math.abs(n.x)<0.9 ? new THREE.Vector3(1,0,0):new THREE.Vector3(0,1,0);
    const u = new THREE.Vector3().crossVectors(n,aux).normalize();
    const v = new THREE.Vector3().crossVectors(n,u).normalize();

    // compute centroid
    const centroid = new THREE.Vector3();
    points.forEach(p=>centroid.add(p));
    centroid.divideScalar(points.length);

    // sort by angle in (u,v) coordinate
    points.sort((a,b)=>{
        const aVec = to2D(a, centroid, u, v);
        const bVec = to2D(b, centroid, u, v);
        return Math.atan2(aVec.y, aVec.x) - Math.atan2(bVec.y, bVec.x);
    });
    return points;
}

function to2D(p, centroid, u, v){
    const vec = p.clone().sub(centroid);
    return {x: vec.dot(u), y: vec.dot(v)};
}

function buildPlaneMesh(points, normal, opacity){
    // basis
    normal = normal.clone().normalize();
    const aux = Math.abs(normal.x)<0.9 ? new THREE.Vector3(1,0,0):new THREE.Vector3(0,1,0);
    const u = new THREE.Vector3().crossVectors(normal,aux).normalize();
    const v = new THREE.Vector3().crossVectors(normal,u).normalize();

    // project to 2D
    const verts2D = [];
    points.forEach(p=>{
        verts2D.push(new THREE.Vector2(p.dot(u), p.dot(v)));
    });

    // triangulate
    const indices = THREE.ShapeUtils.triangulateShape(verts2D, []);
    const positions = [];
    points.forEach(p=>{
        positions.push(p.x, p.y, p.z);
    });
    const geometry = new THREE.BufferGeometry();
    geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions,3));
    geometry.setIndex(indices.flat());
    geometry.computeVertexNormals();

    const material = new THREE.MeshBasicMaterial({
        color:0x3f7fff,
        side:THREE.DoubleSide,
        transparent:true,
        opacity:opacity,
        depthWrite:false
    });

    return new THREE.Mesh(geometry, material);
}

function gcd2(a,b){ return b===0?a:gcd2(b,a%b); }
function gcd3(a,b,c){ return gcd2(gcd2(a,b),c); }

function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene,camera);
}
</script>
</body>
</html>
