        <div class="slider-group">
            <label for="slider-angle">angle relative to ecliptic (°)</label>
            <input id="slider-angle" type="range" min="0" max="60" step="0.01" value="23.45">
            <span id="value-angle">23.45</span>
        </div>
        <div class="slider-group">
            <label for="slider-longitude">longitude of periapsis (°)</label>
            <input id="slider-longitude" type="range" min="0" max="360" step="0.01" value="102.95">
            <span id="value-longitude">102.95</span>
        </div>
        <div class="slider-group">
            <label for="slider-eccentricity">orbit eccentricity</label>
            <input id="slider-eccentricity" type="range" min="0" max="0.99" step="0.000001" value="0.016722">
            <span id="value-eccentricity">0.016722</span>
        </div>
    </div>

    <!-- ─── Visualization Area ─────────────────────────────────────── -->
    <div id="visualization">
        <canvas id="orbit-canvas"></canvas>
        <canvas id="plot-canvas"></canvas>
    </div>
</div>

<!-- ─── Libraries ─────────────────────────────────────────────────── -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<!-- ─── Application Logic ─────────────────────────────────────────── -->
<script>
/* ===========================================================
   Helper: current slider values & DOM references
   =========================================================== */
const sliderAngle       = document.getElementById('slider-angle');
const sliderLongitude   = document.getElementById('slider-longitude');
const sliderEccentricity= document.getElementById('slider-eccentricity');

const valueAngle        = document.getElementById('value-angle');
const valueLongitude    = document.getElementById('value-longitude');
const valueEcc          = document.getElementById('value-eccentricity');

function getParams(){
    return {
        // radians where appropriate
        eps: (+sliderAngle.value) * Math.PI/180,
        omega: (+sliderLongitude.value) * Math.PI/180,
        ecc: +sliderEccentricity.value
    };
}

function updateDisplays(){
    valueAngle.textContent     = (+sliderAngle.value).toFixed(2);
    valueLongitude.textContent = (+sliderLongitude.value).toFixed(2);
    valueEcc.textContent       = (+sliderEccentricity.value).toFixed(6);
}

/* ===========================================================
   p5 Sketch: ORBIT
   =========================================================== */
const orbitSketch = (s) => {
    let canvasHolder = document.getElementById('orbit-canvas');
    let cnv;
    s.setup = function(){
        cnv = s.createCanvas(canvasHolder.clientWidth, canvasHolder.clientHeight);
        // replace placeholder canvas with p5 canvas
        canvasHolder.replaceWith(cnv.elt);
        cnv.elt.setAttribute('id','orbit-canvas');
        s.noLoop();
    };

    s.windowResized = function(){
        s.resizeCanvas(document.getElementById('orbit-canvas').clientWidth,
                       document.getElementById('orbit-canvas').clientHeight);
        s.redraw();
    };

    s.draw = function(){
        const {omega,ecc} = getParams();

        s.background('#f0f0f0');
        s.push();
        s.translate(s.width/2, s.height/2);

        // styling
        s.stroke('#a0522d');
        s.noFill();
        s.strokeWeight(2);

        // ellipse parameters
        const a = s.width * 0.4;            // semi-major axis
        const b = a * Math.sqrt(1 - ecc*ecc); // semi-minor axis

        s.rotate(omega); // rotate by longitude of periapsis

        // p5 ellipse uses width/height = 2a/2b
        s.ellipse(0,0, a*2, b*2);

        // draw focus points for reference (optional)
        const c = Math.sqrt(a*a - b*b);
        s.fill('#a0522d');
        s.noStroke();
        s.circle(-c,0,6); // focus closer to periapsis
        s.pop();
    };
};

/* ===========================================================
   p5 Sketch: Equation of Time Plot
   =========================================================== */
const plotSketch = (s) => {
    let canvasHolder = document.getElementById('plot-canvas');
    let cnv;
    const nPts = 600;

    s.setup = function(){
        cnv = s.createCanvas(canvasHolder.clientWidth, canvasHolder.clientHeight);
        canvasHolder.replaceWith(cnv.elt);
        cnv.elt.setAttribute('id','plot-canvas');
        s.noLoop();
    };

    s.windowResized = function(){
        s.resizeCanvas(document.getElementById('plot-canvas').clientWidth,
                       document.getElementById('plot-canvas').clientHeight);
        s.redraw();
    };

    function computeEOT(){
        const {eps,omega,ecc} = getParams();
        const TWO_PI = Math.PI*2;
        let data = [];
        let prev = 0;
        for(let i=0;i<=nPts;i++){
            let M = TWO_PI * i / nPts; // mean anomaly

            // --- Solve Kepler's Equation: M = E - e sinE
            let E = M;
            for(let k=0;k<8;k++){
                E = E - (E - ecc * Math.sin(E) - M) / (1 - ecc * Math.cos(E));
            }

            // true anomaly
            let nu = 2 * Math.atan2(
                        Math.sqrt(1+ecc) * Math.sin(E/2),
                        Math.sqrt(1-ecc) * Math.cos(E/2)
                      );
            let lambda = nu + omega;                           // true longitude
            let alpha  = Math.atan2(Math.cos(eps)*Math.sin(lambda), Math.cos(lambda)); // right ascension

            let eotRad = M - alpha;

            // unwrap to keep continuity
            if(i>0){
                while(eotRad - prev > Math.PI)  eotRad -= TWO_PI;
                while(eotRad - prev < -Math.PI) eotRad += TWO_PI;
            }
            prev = eotRad;

            let eotMin = eotRad * (720 / Math.PI); // convert to minutes
            data.push({M, eotMin});
        }
        return data;
    }

    s.draw = function(){
        const data = computeEOT();
        // determine min/max for Y-axis
        let ymin = Math.min(...data.map(d=>d.eotMin));
        let ymax = Math.max(...data.map(d=>d.eotMin));
        // add small padding
        const yPad = (ymax - ymin)*0.1 || 1;
        ymin -= yPad; ymax += yPad;

        // plotting area margins
        const left   = 50;
        const right  = 20;
        const top    = 20;
        const bottom = 50;

        const plotW = s.width - left - right;
        const plotH = s.height - top - bottom;

        // background
        s.background('#ffffff');

        // grid & axes
        s.stroke('#cccccc');
        s.strokeWeight(1);
        // vertical gridlines at integer radian values 1..6
        for(let t=1;t<=6;t++){
            let x = left + (t / (2*Math.PI)) * plotW;
            s.line(x, top, x, top+plotH);
        }
        // horizontal gridlines at 0 and midway between min/max (every ~yStep)
        let yStep = (ymax-ymin)/4;
        for(let gy = ymin; gy <= ymax; gy += yStep){
            let y = top + (ymax-gy)/(ymax-ymin)*plotH;
            s.line(left, y, left+plotW, y);
        }

        // axes
        s.stroke('#000000');
        s.strokeWeight(2);
        // x-axis
        let xAxisY = top + (ymax - 0) /(ymax-ymin)*plotH;
        if(xAxisY < top || xAxisY > top+plotH) xAxisY = top+plotH; // clamp
        s.line(left, xAxisY, left+plotW, xAxisY);
        // y-axis
        s.line(left, top, left, top+plotH);

        // axis labels
        s.noStroke();
        s.fill('#000000');
        s.textSize(12);
        // X labels
        for(let t=1;t<=6;t++){
            let x = left + (t / (2*Math.PI)) * plotW;
            s.textAlign(s.CENTER, s.TOP);
            s.text(t.toString(), x, top+plotH+6);
        }
        // Y label
        s.push();
        s.textAlign(s.CENTER, s.BOTTOM);
        s.translate(12, top + plotH/2);
        s.rotate(-Math.PI/2);
        s.text("minutes", 0,0);
        s.pop();

        // --- Plot curve ---
        s.stroke('#ff8c00');
        s.strokeWeight(2);
        s.noFill();
        s.beginShape();
        data.forEach(d=>{
            let x = left + (d.M / (2*Math.PI)) * plotW;
            let y = top + (ymax - d.eotMin)/(ymax - ymin)*plotH;
            s.vertex(x,y);
        });
        s.endShape();
    };
};

/* ===========================================================
   Instantiate sketches
   =========================================================== */
const orbitP5 = new p5(orbitSketch);
const plotP5  = new p5(plotSketch);

/* ===========================================================
   Slider interaction
   =========================================================== */
function refresh(){
    updateDisplays();
    orbitP5.redraw();
    plotP5.redraw();
}
[sliderAngle, sliderLongitude, sliderEccentricity].forEach(sl=>{
    sl.addEventListener('input', refresh);
});

// initial render
refresh();
</script>
</body>
</html>
