            <input id="slider-plot-range" type="range" min="0.5" max="2" step="0.01" value="1">
        </div>
    </div>

    <!-- =================== VISUALIZATION AREA ============================ -->
    <div id="visualization">
        <!-- p5.js will insert the canvas here -->
    </div>
</div>


<script>
/* ===================== GLOBAL STATE ==================================== */
let params = {
    c: 4,
    hC: 1.9,
    delta: 0.5,
    plotRange: 1,
    step: 5,
    verification: true
};

/* ===================== DOM ELEMENTS ==================================== */
const sliderC          = document.getElementById('slider-c');
const sliderHC         = document.getElementById('slider-h_C');
const sliderDelta      = document.getElementById('slider-delta');
const sliderPlotRange  = document.getElementById('slider-plot-range');
const spanC            = document.getElementById('c-value');
const spanHC           = document.getElementById('h_C-value');
const spanDelta        = document.getElementById('delta-value');
const spanPlotRange    = document.getElementById('plot-range-value');
const verificationCB   = document.getElementById('check-verification');
const stepRadios       = document.querySelectorAll('input[name="steps"]');

/* Utility to update spans and state */
function updateParam(elem, span, key){
    let val = parseFloat(elem.value);
    if(key === 'delta'){
        // keep three decimals for angle difference
        span.textContent = val.toFixed(2);
    }else{
        span.textContent = val.toFixed(2).replace(/\.00$/,'');
    }
    params[key] = val;
}

/* Event bindings -------------------------------------------------------- */
sliderC.addEventListener('input', ()=>{ updateParam(sliderC, spanC, 'c'); redrawScene(); });
sliderHC.addEventListener('input', ()=>{ updateParam(sliderHC, spanHC, 'hC'); redrawScene(); });
sliderDelta.addEventListener('input', ()=>{ updateParam(sliderDelta, spanDelta, 'delta'); redrawScene(); });
sliderPlotRange.addEventListener('input', ()=>{ updateParam(sliderPlotRange, spanPlotRange, 'plotRange'); redrawScene(); });

verificationCB.addEventListener('change', ()=>{
    params.verification = verificationCB.checked;
    redrawScene();
});

stepRadios.forEach(radio=>{
    radio.addEventListener('change', ()=>{
        if(radio.checked){
            params.step = parseInt(radio.value);
            redrawScene();
        }
    });
});


/* ===================== p5.js Sketch ==================================== */
const sketch = (p)=>{
    const baseScale = 80;       // world unit -> pixel multiplier
    let canvas;

    p.setup = ()=>{
        canvas = p.createCanvas(800, 700);
        canvas.parent('visualization');
        p.noLoop();
        redrawScene();          // initial draw
    };

    /* =============== Geometry computation ============================== */
    function computeConstruction(){
        const c   = params.c;
        const h   = params.hC;
        let delta = params.delta;

        // avoid tan blow-up near ±π/2
        const eps = 1e-4;
        if(Math.abs(Math.abs(delta) - Math.PI/2) < eps){
            delta = delta>0 ? (Math.PI/2 - eps) : (-Math.PI/2 + eps);
        }

        const t   = Math.tan(delta);     // tan(δ)
        const t2  = 2*t;

        const a1  = 2*h;                 // coefficient for y in n
        const b   = -h*h + c*c/4;        // constant part in n

        // Quadratic coefficients A y^2 + B y + C = 0
        const A = t2 * (1 + (a1*a1)/(c*c));
        const B = t2 * ((2*a1*b)/(c*c) - 1.5*a1) - c;
        const C = t2 * ((b*b)/(c*c)      - 1.5*b    + c*c/2);

        let disc = B*B - 4*A*C;
        if(disc < 0){ disc = 0; }
        const sqrtDisc = Math.sqrt(disc);

        const y1 = (-B + sqrtDisc)/(2*A);
        const y2 = (-B - sqrtDisc)/(2*A);
        // choose negative root (per description)
        const yM = (y1<y2) ? (y1<0?y1:y2) : (y2<0?y2:y1);

        const xM = (2*h*yM - h*h + c*c/4)/c;

        // Radius and other points
        const Fx = 0, Fy = 0;
        const Cx = 0, Cy = h;
        const Dx = c/2, Dy = 0;
        const Kx = c,   Ky = 0;

        const MCx = Cx - xM, MCy = Cy - yM;
        const R   = Math.hypot(MCx, MCy);

        const xB = 2*xM - c/2;          // choose the second intersection
        const Bx = xB,  By = 0;
        const Ax = xB - c, Ay = 0;

        // Verification point E
        const Ex = c - xB;
        const Ey = 0;

        return {
            F:{x:Fx,y:Fy}, C:{x:Cx,y:Cy}, D:{x:Dx,y:Dy}, K:{x:Kx,y:Ky},
            M:{x:xM,y:yM}, R,
            B:{x:Bx,y:By}, A:{x:Ax,y:Ay},
            E:{x:Ex,y:Ey}
        };
    }

    /* =============== Drawing helpers =================================== */
    function worldToScreen(pt){
        const scaleFactor = baseScale * params.plotRange;
        return {
            x: pt.x * scaleFactor,
            y: pt.y * scaleFactor
        };
    }

    function drawPoint(pt, label){
        const r = 4;
        p.circle(pt.x, pt.y, r*2);
        // draw label upright
        p.push();
        p.scale(1, -1);
        p.fill(0);
        p.noStroke();
        p.textSize(14);
        p.text(label, pt.x + 6, -pt.y - 6);
        p.pop();
    }

    function drawLine(pt1, pt2, dashed=false, weight=1){
        p.push();
        if(dashed){ p.drawingContext.setLineDash([6,4]); }
        p.strokeWeight(weight);
        p.line(pt1.x, pt1.y, pt2.x, pt2.y);
        p.pop();
    }

    function drawCircle(center, R, dashed=false){
        p.push();
        if(dashed){ p.drawingContext.setLineDash([6,4]); }
        p.noFill();
        p.circle(center.x, center.y, R*2);
        p.pop();
    }

    /* =============== Main redraw ======================================= */
    window.redrawScene = function(){
        p.clear();
        p.background('#E0E0E0');

        // Coordinate system setup
        p.push();
        const scaleFactor = baseScale * params.plotRange;
        p.translate(p.width/2, p.height*0.7);
        p.scale(scaleFactor, -scaleFactor);    // flip Y
        p.stroke(0);
        p.fill(0);

        const geo = computeConstruction();

        /* ---------- Step-wise drawing --------------------------------- */
        // Step >=1
        if(params.step >= 1){
            // infinite line λ (x-axis)
            drawLine({x:-1000,y:0},{x:1000,y:0});
            drawPoint(geo.F,'F');
            drawPoint(geo.C,'C');
            drawPoint(geo.D,'D');
            drawLine(geo.F, geo.C);              // FC
        }

        // Step >=2
        if(params.step >= 2){
            drawPoint(geo.K,'K');

            // dashed CD and DK
            drawLine(geo.C, geo.D, true);
            drawLine(geo.D, geo.K, true);

            // point M
            drawPoint(geo.M,'M');

            // dashed MC, MD, MK
            drawLine(geo.M, geo.C, true);
            drawLine(geo.M, geo.D, true);
            drawLine(geo.M, geo.K, true);

            // circle κ
            drawCircle(geo.M, geo.R);
        }

        // Step >=3
        if(params.step >= 3){
            drawPoint(geo.B,'B');
        }

        // Step >=4
        if(params.step >= 4){
            drawPoint(geo.A,'A');
        }

        // Step >=5
        if(params.step >= 5){
            p.strokeWeight(2.5/scaleFactor);  // keep visually constant
            drawLine(geo.A, geo.B);
            drawLine(geo.B, geo.C);
            drawLine(geo.C, geo.A);
        }

        // Verification
        if(params.verification){
            drawPoint(geo.E,'E');
            drawLine(geo.C, geo.E, true);
            drawLine(geo.K, geo.B, true);
        }

        p.pop();   // restore
    };
};

new p5(sketch);
</script>
</body>
</html>
