<body>
<div id="main-container">
    <!-- CONTROL PANEL -->
    <div id="control-panel">
        <label for="slider-z">z&nbsp;(m)</label>
        <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
        <span id="label-z">0.</span>
    </div>

    <!-- PLOTS -->
    <div id="plots-container">
        <div id="plot-intensity"></div>
        <div id="plot-population"></div>
        <div id="plot-coherence"></div>
    </div>
</div>

<script>
/*************************************************
 * GLOBAL CONSTANTS & HELPER FUNCTIONS
 *************************************************/
const LIGHT_SPEED = 0.3;                     // metres / ns (approx.)
const t_values = Array.from({length:301}, (_,i)=> i-50);  // -50 … 250 ns

// Helper: integer?  (floating-point safe)
function isAlmostInteger(x, eps=1e-9){
    return Math.abs(x - Math.round(x)) < eps;
}
// Helper: pretty print z
function formatZ(z){
    return isAlmostInteger(z) ? (Math.round(z) + ".") : z.toFixed(2);
}

// Generate RED (z = 0) curves once
const I_red = t_values.map(t=>{
    if(t < -15 || t > 170) return 0;
    return (t > 15) ? 1 : 0.5*(1 + Math.tanh(t/4));
});
const P_red = t_values.map(t=>{
    return (t < -15) ? 0 : 4.5*(1-Math.exp(-(t+15)/50));
});
const C_red = t_values.map(t=>{
    return (t < -15) ? 0 : 2.2*(1-Math.exp(-(t+15)/50));
});

/*************************************************
 * DYNAMIC BLUE CURVE GENERATORS
 *************************************************/
function generateBlueCurves(z){
    const t_delay = z / LIGHT_SPEED;
    const I = [];
    const P = [];
    const C = [];
    const pScale = 1 - 0.45*(z/32);
    const cScale = 1 - 0.45*(z/32);

    for(let i=0;i<t_values.length;i++){
        const t = t_values[i];
        const t_prime = t - t_delay;

        // INTENSITY
        let I_base;
        if(t_prime < -15) I_base = 0;
        else if(t_prime > 15) I_base = 1;
        else I_base = 0.5*(1+Math.tanh(t_prime/4));
        const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
        const I_val = (t > 170 + t_delay) ? null : I_base * envelope;
        I.push(I_val);

        // POPULATION
        const P_val = (t > 170 + t_delay) ? null :
                      pScale * ((t_prime < -15) ? 0 : 4.5*(1-Math.exp(-(t_prime+15)/50)));
        P.push(P_val);

        // COHERENCE
        const C_val = (t > 170 + t_delay) ? null :
                      cScale * ((t_prime < -15) ? 0 : 2.2*(1-Math.exp(-(t_prime+15)/50)));
        C.push(C_val);
    }
    return {I, P, C, t_delay};
}

/*************************************************
 * PLOT INITIALISATION
 *************************************************/
function buildPlot(divId, yRed, yBlue, yTitle, yRange, annotationLabel){
    const data = [
        {   x: t_values,
            y: yRed,
            name: "z = 0 meters",
            mode:'lines',
            line:{color:'red', width:2}
        },
        {   x: t_values,
            y: yBlue,
            name: "z = 0. meters",
            mode:'lines',
            line:{color:'blue', width:2, dash:'dash'}
        }
    ];

    const layout = {
        xaxis: {title:'time (ns)', range:[-50,250], zeroline:true},
        yaxis: Object.assign({zeroline:true}, yRange, {title: yTitle}),
        margin:{l:80,r:20,t:40,b:40},
        showlegend:true,
        legend:{x:0.7,y:0.9},
        shapes:[
            {type:'line', x0:0, x1:0, y0:0, y1:1, yref:'paper',
             line:{color:'black', width:2, dash:'dash'}},
            {type:'line', x0:0, x1:0, y0:0, y1:1, yref:'paper',
             line:{color:'black', width:2, dash:'dash'}}
        ],
        annotations:[
            {x:-45, y:yRange.range ? yRange.range[1]*0.9 : 0.9, 
             xref:'x', yref:'y',
             text: annotationLabel,
             showarrow:false,
             font:{size:14, weight:'bold'}}
        ]
    };

    Plotly.newPlot(divId, data, layout, {responsive:true});
}

// Initial blue data for z=0
const {I:I_blue0, P:P_blue0, C:C_blue0} = generateBlueCurves(0);

buildPlot('plot-intensity',   I_red, I_blue0,
          'intensity (arb)',          {range:[-0.1,1.1]},     '(a)');
buildPlot('plot-population',  P_red, P_blue0,
          'excited-state population × 10<sup>4</sup>', {range:[-0.5,5.5]}, '(b)');
buildPlot('plot-coherence',   C_red, C_blue0,
          'coherence × 10<sup>2</sup>',               {range:[-0.2,2.4]}, '(c)');

/*************************************************
 * SLIDER INTERACTION
 *************************************************/
const slider = document.getElementById('slider-z');
const labelZ = document.getElementById('label-z');

function updatePlots(z){
    const {I, P, C, t_delay} = generateBlueCurves(z);
    const zLabel = `z = ${formatZ(z)} meters`;

    // Intensity
    Plotly.restyle('plot-intensity',
        {y:[I], name:[zLabel]}, [1]);
    Plotly.relayout('plot-intensity',
        {'shapes[1].x0':t_delay, 'shapes[1].x1':t_delay});

    // Population
    Plotly.restyle('plot-population',
        {y:[P], name:[zLabel]}, [1]);
    Plotly.relayout('plot-population',
        {'shapes[1].x0':t_delay, 'shapes[1].x1':t_delay});

    // Coherence
    Plotly.restyle('plot-coherence',
        {y:[C], name:[zLabel]}, [1]);
    Plotly.relayout('plot-coherence',
        {'shapes[1].x0':t_delay, 'shapes[1].x1':t_delay});
}

slider.addEventListener('input', (e)=>{
    const zVal = parseFloat(e.target.value);
    labelZ.textContent = formatZ(zVal);
    updatePlots(zVal);
});
</script>
</body>
</html>
