<body>
<div id="demo-container">

    <!-- ---------- Control Panel ---------- -->
    <div id="control-panel">

        <!-- Heat Transfer Coefficient Slider -->
        <div>
            <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
            <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
            <span id="ua-value">0.0</span>
        </div>

        <!-- Reverse Reaction Pre-exponential Factor Slider -->
        <div>
            <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
            <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
            <span id="kr0-value">0.0 × 10¹²</span>
        </div>

        <!-- Feed Temperature Slider -->
        <div>
            <label for="slider-tf">feed temperature (K)</label>
            <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
            <span id="tf-value">265</span>
        </div>

        <!-- Residence Time Slider -->
        <div>
            <label for="slider-tau">residence time (s)</label>
            <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
            <span id="tau-value">400</span>
        </div>

    </div>

    <!-- ---------- Visualization ---------- -->
    <div id="plot-container"></div>

</div>

<script>
// ----------- CONSTANTS -----------
const C_A0   = 10;        // mmol/dm³
const R      = 1.987;     // cal/mol/K
const E_f    = 25000;     // cal/mol
const dH_rxn = -200000;   // cal/mol
const E_r    = 225000;    // cal/mol
const k_f0   = 1e15;      // 1/s
const rhoCp  = 20000;     // cal/(dm³ K)

// Temperature grid for plotting
const T_min = 250;
const T_max = 400;
const nPts  = 151;
const T_arr = Array.from({length:nPts},(_,i)=>T_min + i*(T_max-T_min)/(nPts-1));

// ----------- DOM ELEMENTS -----------
const sliderUA  = document.getElementById('slider-ua');
const sliderKr0 = document.getElementById('slider-kr0');
const sliderTf  = document.getElementById('slider-tf');
const sliderTau = document.getElementById('slider-tau');

const valueUA  = document.getElementById('ua-value');
const valueKr0 = document.getElementById('kr0-value');
const valueTf  = document.getElementById('tf-value');
const valueTau = document.getElementById('tau-value');

// ----------- HELPER FUNCTIONS -----------
function formatSci(val){                 // val between 0 and 10
    return val.toFixed(1) + ' × 10¹²';
}

function calcCurves(UA,kr0_slider,Tf,tau){
    // kr0 actual
    const kr0_pre = kr0_slider * 1e12;

    const massBalance = [];
    const energyBalance = [];

    for (let T of T_arr){
        const k_f = k_f0 * Math.exp(-E_f/(R*T));
        const k_r = kr0_pre * Math.exp(-E_r/(R*T));

        const CB_mass = (tau * k_f * C_A0) / (1 + tau*(k_f + k_r));
        massBalance.push(CB_mass);

        const numerator = rhoCp*(T - Tf) + UA*tau*(T - Tf);
        const CB_energy = numerator / (-dH_rxn);
        energyBalance.push(CB_energy);
    }
    return {massBalance,energyBalance};
}

function createPlot(dataObj){
    const traceMass = {
        x:T_arr,
        y:dataObj.massBalance,
        mode:'lines',
        line:{color:'#008000',width:3},
        name:'mass balance'
    };

    const traceEnergy = {
        x:T_arr,
        y:dataObj.energyBalance,
        mode:'lines',
        line:{color:'#0000FF',width:3},
        name:'energy balance'
    };

    // choose points for annotation (closest indices)
    const idxMass = T_arr.findIndex(t=>t>=360);
    const idxEnergy = T_arr.findIndex(t=>t>=280);

    const annotations = [
        {
            x:T_arr[idxMass],
            y:dataObj.massBalance[idxMass],
            text:'mass balance',
            font:{color:'#008000'},
            showarrow:false,
            xanchor:'left'
        },
        {
            x:T_arr[idxEnergy],
            y:dataObj.energyBalance[idxEnergy],
            text:'energy balance',
            font:{color:'#0000FF'},
            showarrow:false,
            xanchor:'left'
        }
    ];

    const layout = {
        margin:{l:70,r:20,t:20,b:60},
        xaxis:{
            title:'temperature (K)',
            range:[250,400],
            gridcolor:'#ddd'
        },
        yaxis:{
            title:'product concentration (mmol/dm³)',
            range:[0,12],
            gridcolor:'#ddd'
        },
        font:{family:'Arial,Helvetica,sans-serif'},
        annotations:annotations,
        showlegend:false
    };

    Plotly.react('plot-container',[traceMass,traceEnergy],layout,{responsive:true});
}

// ----------- MAIN UPDATE -----------
function update(){
    const UA   = parseFloat(sliderUA.value);
    const kr0  = parseFloat(sliderKr0.value);
    const Tf   = parseFloat(sliderTf.value);
    const tau  = parseFloat(sliderTau.value);

    // Update numeric displays
    valueUA.textContent  = UA.toFixed(1);
    valueKr0.textContent = formatSci(kr0);
    valueTf.textContent  = Tf;
    valueTau.textContent = tau;

    // Recompute and plot
    const curves = calcCurves(UA,kr0,Tf,tau);
    createPlot(curves);
}

// ----------- EVENT LISTENERS -----------
[sliderUA,sliderKr0,sliderTf,sliderTau].forEach(el=>{
    el.addEventListener('input',update);
});

// ----------- INITIAL RENDER -----------
update();
</script>
</body>
</html>
