            <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
            <span id="label-rotation" class="num-label">0.00</span>
        </div>
    </div>

    <!-- Visualization Canvas -->
    <div id="canvas-container"></div>

</div>

<!-- CDN Library -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.min.js"></script>
<script>
/* ---------- GLOBAL STATE ---------- */
let k          = parseFloat(document.getElementById('slider-eccentricity').value); // eccentricity ratio
let phi        = parseFloat(document.getElementById('slider-rotation').value);     // shaft rotation angle
let refFrame   = document.querySelector('input[name="ref-frame"]:checked').value;
let envType    = document.querySelector('input[name="envelope-type"]:checked').value;

const RESOLUTION = 360;          // points per curve (smoothness)
const R = 1;                     // base radius for rotor parametric equations

/* ---------- DOM HANDLERS ---------- */
const eccSlider  = document.getElementById('slider-eccentricity');
const phiSlider  = document.getElementById('slider-rotation');

eccSlider.addEventListener('input', () => {
    k = parseFloat(eccSlider.value);
    document.getElementById('label-eccentricity').textContent = k.toFixed(4);
});
phiSlider.addEventListener('input', () => {
    phi = parseFloat(phiSlider.value);
    document.getElementById('label-rotation').textContent = phi.toFixed(2);
});

document.querySelectorAll('input[name="ref-frame"]').forEach(el=>{
    el.addEventListener('change', e=>{
        refFrame = e.target.value;
    })
});
document.querySelectorAll('input[name="envelope-type"]').forEach(el=>{
    el.addEventListener('change', e=>{
        envType = e.target.value;
    })
});


/* ---------- p5.js SKETCH ---------- */
const sketch = (p) => {

    /* convenient: cached trigonometric arrays for speed (optional) */
    let cosTable = [], sinTable = [];

    p.setup = () => {
        const container = document.getElementById('canvas-container');
        const c = p.createCanvas(container.clientWidth, container.clientHeight);
        c.parent(container);
        p.frameRate(60);
        // Precompute sin/cos table
        for(let i=0;i<=RESOLUTION;i++){
            const t = i * p.TWO_PI / RESOLUTION;
            cosTable[i]=Math.cos(t);
            sinTable[i]=Math.sin(t);
        }
    };

    p.windowResized = () =>{
        const container = document.getElementById('canvas-container');
        p.resizeCanvas(container.clientWidth, container.clientHeight);
    };

    /* ----------- DRAW LOOP ----------- */
    p.draw = () => {
        p.background(255);

        /* Collect curve points based on current parameters */
        const blueCurve   = buildEpitrochoid();
        const orangeCurve = buildEnvelopeCurves();

        /* Merge visible curves for scaling and bounding-box calculation */
        let allPts = blueCurve;
        orangeCurve.forEach(arr=> allPts = allPts.concat(arr));

        /* Determine bounding box */
        let minX=Infinity,maxX=-Infinity,minY=Infinity,maxY=-Infinity;
        allPts.forEach(pt=>{
            if(pt.x<minX)minX=pt.x;
            if(pt.x>maxX)maxX=pt.x;
            if(pt.y<minY)minY=pt.y;
            if(pt.y>maxY)maxY=pt.y;
        });
        const rangeX = maxX - minX;
        const rangeY = maxY - minY;
        const padding = 0.2; // 20% padding
        const scaleFactor = 0.9 * Math.min(p.width, p.height) / (2*Math.max(rangeX, rangeY) * (1+padding));

        /* Draw in world coordinates: origin at canvas center, y up */
        p.push();
            p.translate(p.width/2, p.height/2);
            p.scale(scaleFactor, -scaleFactor);    // flip y-axis

            drawAxes(minX, maxX, minY, maxY);

            /* Draw curves */
            drawCurve(blueCurve, p.color(50, 100, 200));  // blue curve
            orangeCurve.forEach(arr=>{
                drawCurve(arr, p.color(230,150,0));       // orange curve(s)
            });
        p.pop();
    };

    /* ---------- HELPER: build blue epitrochoid ---------- */
    function buildEpitrochoid(){
        const pts = [];
        const phi_shaft = 2*phi;
        const theta = -phi_shaft/3;
        const Cx = k*Math.cos(phi_shaft);
        const Cy = k*Math.sin(phi_shaft);

        for(let i=0;i<=RESOLUTION;i++){
            const t = i * p.TWO_PI / RESOLUTION;
            const x0 = Math.cos(t) - k*Math.cos(3*t);
            const y0 = Math.sin(t) - k*Math.sin(3*t);
            let x=x0, y=y0;

            if(refFrame === 'envelope'){        // inverse transformation
                let xt = x - Cx;
                let yt = y - Cy;
                const cosT = Math.cos(-theta), sinT = Math.sin(-theta);
                x = xt * cosT - yt * sinT;
                y = xt * sinT + yt * cosT;
            }
            // For 'epitrochoid' and 'fixed centers', blue stays untransformed.
            pts.push({x,y});
        }
        return pts;
    }

    /* ---------- HELPER: build envelope curves (inner/outer/both) ---------- */
    function buildEnvelopeCurves(){
        const phi_shaft = 2*phi;
        const theta = -phi_shaft/3;
        const Cx = k*Math.cos(phi_shaft);
        const Cy = k*Math.sin(phi_shaft);

        const types = (envType==='both') ? ['inner','outer'] : [envType];
        const curves = [];

        types.forEach(type=>{
            const pts=[];
            for(let i=0;i<=RESOLUTION;i++){
                const t = i * p.TWO_PI / RESOLUTION;
                // base rotor point in its own frame
                let xr, yr;
                if(type==='inner'){
                    xr = (2/3)*R*Math.cos(t) + k*Math.cos(2*t);
                    yr = (2/3)*R*Math.sin(t) - k*Math.sin(2*t);
                }else{ // outer
                    xr = (2/3)*R*Math.cos(t) - k*Math.cos(2*t);
                    yr = (2/3)*R*Math.sin(t) + k*Math.sin(2*t);
                }

                let x=xr, y=yr;

                // Apply transformation depending on reference frame
                if(refFrame === 'epitrochoid' || refFrame === 'fixed centers'){
                    // forward transformation (rotation then translation)
                    const cosT = Math.cos(theta), sinT = Math.sin(theta);
                    const xrot = xr*cosT - yr*sinT;
                    const yrot = xr*sinT + yr*cosT;
                    x = xrot + Cx;
                    y = yrot + Cy;
                }
                // in 'envelope' frame, orange curve is stationary at origin (no transform)

                pts.push({x,y});
            }
            curves.push(pts);
        });

        // In reference frame 'fixed centers', nothing else to do (already handled above)
        // In reference frame 'envelope', orange is stationary (handled implicitly)
        return curves;
    }

    /* ---------- HELPER: draw axes ---------- */
    function drawAxes(minX, maxX, minY, maxY){
        p.stroke(150);
        p.strokeWeight(1/ p._pixelDensity); // scale-independent thin line
        p.line(minX-1, 0, maxX+1, 0);       // X axis
        p.line(0, minY-1, 0, maxY+1);       // Y axis

        const tickLen = 0.03 * (maxX - minX + maxY - minY); // nicely scales
        const step = 0.5;

        // X ticks
        for(let x=Math.ceil(minX); x<=maxX; x+=step){
            p.line(x, -tickLen, x, tickLen);
            if(Math.abs(x) < 1e-6) continue; // skip labeling origin
            p.push();
                p.scale(1, -1); // flip back for text
                p.noStroke();
                p.fill(100);
                p.textSize(12/scaleFactor());
                p.text(x.toFixed(1), x*scaleFactor(), 15/scaleFactor());
            p.pop();
        }

        // Y ticks
        for(let y=Math.ceil(minY); y<=maxY; y+=step){
            p.line(-tickLen, y, tickLen, y);
            if(Math.abs(y) < 1e-6) continue;
            p.push();
                p.scale(1, -1);
                p.noStroke();
                p.fill(100);
                p.textSize(12/scaleFactor());
                p.text(y.toFixed(1), -28/scaleFactor(), -y*scaleFactor()+4/scaleFactor());
            p.pop();
        }

        // helper to get current scale factor (inverse of p's).
        function scaleFactor(){
            return p._renderer._curTransform.mat3[0];
        }
    }

    /* ---------- HELPER: draw a curve (array of pts) ---------- */
    function drawCurve(pts, strokeCol){
        p.noFill();
        p.stroke(strokeCol);
        p.strokeWeight(2 / p._pixelDensity);
        p.beginShape();
            pts.forEach(pt=> p.vertex(pt.x, pt.y));
        p.endShape();
    }
};

new p5(sketch);
</script>
</body>
</html>
