<body>

<div id="container">

    <!-- CONTROL PANEL -->
    <div id="control-panel">
        <h4>stability of multilayered emulsions</h4>

        <!-- φ Slider -->
        <label for="slider-phi">\( \phi \) <span id="phi-value-display" class="value-display"></span></label>
        <input type="range" id="slider-phi">

        <hr>

        <!-- Physical parameters -->
        <label for="slider-gamma-sat">\( \Gamma_{sat}\) (mg/m²) <span id="gamma-sat-value-display" class="value-display"></span></label>
        <input type="range" id="slider-gamma-sat">

        <label for="slider-r">\( r \) (nm) <span id="r-value-display" class="value-display"></span></label>
        <input type="range" id="slider-r">

        <label for="slider-rpe">\( r_{pe} \) (nm) <span id="rpe-value-display" class="value-display"></span></label>
        <input type="range" id="slider-rpe">

        <label for="slider-mw">\( M_{W} \) (kDa) <span id="mw-value-display" class="value-display"></span></label>
        <input type="range" id="slider-mw">

        <hr>

        <!-- Axis limits -->
        <label for="slider-phi-axis">\( \phi \) axis max. <span id="phi-axis-value-display" class="value-display"></span></label>
        <input type="range" id="slider-phi-axis">

        <label for="slider-c-axis">C axis max. <span id="c-axis-value-display" class="value-display"></span></label>
        <input type="range" id="slider-c-axis">
    </div>

    <!-- VISUALIZATION AREA -->
    <div id="vis-area">
        <div id="info-display">
            <p id="formula-display">
                <span style="color:blue;">\(y_1(\phi)\)</span>,
                <span style="color:red;">\(y_2(\phi)\)</span>,
                <span style="color:green;">\(y_3(\phi)\)</span>
            </p>
            <p id="phimax-display"></p>
            <p id="range-display"></p>
        </div>
        <div id="plot-container"></div>
    </div>
</div>

<script>
/* ----------------------- CONSTANTS ----------------------- */
const C_UNIT_CONVERSION = 1000;   // kg/m³
const K_DEPLETION = 0.137;
const NUM_POINTS = 250;

/* ------------------- ELEMENT REFERENCES ------------------ */
const plotDiv           = document.getElementById('plot-container');

const sliderPhi         = document.getElementById('slider-phi');
const sliderGammaSat    = document.getElementById('slider-gamma-sat');
const sliderR           = document.getElementById('slider-r');
const sliderRpe         = document.getElementById('slider-rpe');
const sliderMw          = document.getElementById('slider-mw');
const sliderPhiAxis     = document.getElementById('slider-phi-axis');
const sliderCAxis       = document.getElementById('slider-c-axis');

// Display spans
const dispPhi           = document.getElementById('phi-value-display');
const dispGammaSat      = document.getElementById('gamma-sat-value-display');
const dispR             = document.getElementById('r-value-display');
const dispRpe           = document.getElementById('rpe-value-display');
const dispMw            = document.getElementById('mw-value-display');
const dispPhiAxis       = document.getElementById('phi-axis-value-display');
const dispCAxis         = document.getElementById('c-axis-value-display');

const phimaxDisplay     = document.getElementById('phimax-display');
const rangeDisplay      = document.getElementById('range-display');

/* -------------------- INITIAL SETTINGS ------------------- */
const sliderSetup = [
  {el: sliderPhi,      min:0.0001, max:0.05,  step:0.0001, value:0.005},
  {el: sliderGammaSat, min:0.1,    max:10,    step:0.1,    value:1.5},
  {el: sliderR,        min:50,     max:1000,  step:10,     value:300},
  {el: sliderRpe,      min:5,      max:50,    step:1,      value:20},
  {el: sliderMw,       min:10,     max:1000,  step:1,      value:166},
  {el: sliderPhiAxis,  min:0.01,   max:0.5,   step:0.01,   value:0.05},
  {el: sliderCAxis,    min:1.0,    max:5.0,   step:0.1,    value:2.0}
];

sliderSetup.forEach(cfg=>{
    const e = cfg.el;
    e.min   = cfg.min;
    e.max   = cfg.max;
    e.step  = cfg.step;
    e.value = cfg.value;
});

/* --------------- UTILITY / MATH FUNCTIONS ---------------- */
function y1(phi, gamma_sat, r){
    return (3*gamma_sat/r)*C_UNIT_CONVERSION*phi;
}
function y2(phi, gamma_sat, r){
    return y1(phi, gamma_sat, r)/(1-phi);
}
function y3(phi, gamma_sat, r, mw, rpe){
    const A = (3*gamma_sat/r)*C_UNIT_CONVERSION;
    const B = (K_DEPLETION*mw)/(Math.pow(rpe,3));
    return A*phi + B/phi;
}
function computePhiMax(gamma_sat, r, mw, rpe){
    const A = (3*gamma_sat/r)*C_UNIT_CONVERSION;
    const B = (K_DEPLETION*mw)/(Math.pow(rpe,3));
    return Math.sqrt(B/A);
}

/* -------------------- DISPLAY HELPERS -------------------- */
function setDisplay(span, val, decimals=3){
    span.textContent = Number(val).toFixed(decimals);
}
function updateAllDisplays(){
    setDisplay(dispPhi, sliderPhi.value,     4);
    setDisplay(dispGammaSat, sliderGammaSat.value,1);
    setDisplay(dispR, sliderR.value,0);
    setDisplay(dispRpe, sliderRpe.value,0);
    setDisplay(dispMw, sliderMw.value,0);
    setDisplay(dispPhiAxis, sliderPhiAxis.value,2);
    setDisplay(dispCAxis, sliderCAxis.value,2);
}

/* ---------------------- PLOT LOGIC ----------------------- */
let markerTraceIndex = 5; // will stay constant with fixed order

function generateData(){
    const gamma_sat = Number(sliderGammaSat.value);
    const r         = Number(sliderR.value);
    const rpe       = Number(sliderRpe.value);
    const mw        = Number(sliderMw.value);
    const xMax      = Number(sliderPhiAxis.value);

    /* ----- create continuous curves ----- */
    const xVals = [];
    const y1Vals=[], y2Vals=[], y3Vals=[];
    for(let i=0;i<=NUM_POINTS;i++){
        const phi = i*xMax/NUM_POINTS;
        xVals.push(phi);
        y1Vals.push( y1(phi, gamma_sat, r) );
        y2Vals.push( y2(phi, gamma_sat, r) );
        y3Vals.push( y3(phi, gamma_sat, r, mw, rpe) );
    }

    /* ----- shaded region (until phi_max) ---- */
    const phi_max = computePhiMax(gamma_sat,r,mw,rpe);
    const phiShadeMax = Math.min(phi_max, xMax);
    const xShade=[], y2Shade=[], y3Shade=[];
    const steps = Math.floor(phiShadeMax/xMax*NUM_POINTS);
    for(let i=0;i<=steps;i++){
        const phi = i*phiShadeMax/steps;
        xShade.push(phi);
        y2Shade.push(y2(phi, gamma_sat, r));
        y3Shade.push(y3(phi, gamma_sat, r, mw, rpe));
    }

    /* ----- marker values at current phi ----- */
    const phiVal   = Number(sliderPhi.value);
    const y1Val    = y1(phiVal, gamma_sat, r);
    const y2Val    = y2(phiVal, gamma_sat, r);
    const y3Val    = y3(phiVal, gamma_sat, r, mw, rpe);

    /* ----- traces ----- */
    const data = [
        // Shaded region helpers (invisible lines)
        {
            x:xShade,
            y:y2Shade,
            mode:'lines',
            line:{color:'rgba(255,0,0,0)'},
            hoverinfo:'skip',
            showlegend:false
        },
        {
            x:xShade,
            y:y3Shade,
            mode:'lines',
            line:{color:'rgba(0,255,0,0)'},
            fill:'tonexty',
            fillcolor:'rgba(173,216,230,0.5)',
            hoverinfo:'skip',
            showlegend:false
        },
        // Actual curves
        {
            x:xVals,
            y:y1Vals,
            mode:'lines',
            line:{color:'blue'},
            name:'y1'
        },
        {
            x:xVals,
            y:y2Vals,
            mode:'lines',
            line:{color:'red'},
            name:'y2'
        },
        {
            x:xVals,
            y:y3Vals,
            mode:'lines',
            line:{color:'green'},
            name:'y3'
        },
        // Markers
        {
            x:[phiVal, phiVal, phiVal],
            y:[y1Val, y2Val, y3Val],
            mode:'markers',
            marker:{color:['blue','red','green'], size:8},
            showlegend:false
        }
    ];

    /* ----- layout ----- */
    const layout = {
        xaxis:{title:'ϕ (volume fraction)', range:[0, xMax]},
        yaxis:{title:'C (kg/m³)', range:[0, Number(sliderCAxis.value)]},
        showlegend:false,
        margin:{t:20},
        shapes:[
            {
                type:'line',
                x0:phiVal,
                x1:phiVal,
                y0:0,
                y1:Number(sliderCAxis.value),
                line:{color:'grey', dash:'dash'}
            }
        ]
    };

    return {data, layout, phi_max, y1Val, y2Val, y3Val};
}

function updateInfoDisplays(phi_max, y2Val, y3Val){
    phimaxDisplay.innerHTML = `\\( \\phi_{max} = ${phi_max.toFixed(4)} \\)`;
    rangeDisplay.innerHTML  = `Stability range: \\( [${y2Val.toFixed(2)} ,\\; ${y3Val.toFixed(2)}] \\;\\text{kg/m^{3}} \\)`;
    MathJax.typesetPromise();
}

function drawPlot(){
    const {data, layout, phi_max, y1Val, y2Val, y3Val} = generateData();
    Plotly.react(plotDiv, data, layout, {responsive:true});
    markerTraceIndex = data.length -1; // should be 5
    updateInfoDisplays(phi_max, y2Val, y3Val);
}

/* --------------- REALTIME PHI UPDATE ONLY ---------------- */
function updatePhiOnly(){
    const gamma_sat = Number(sliderGammaSat.value);
    const r         = Number(sliderR.value);
    const rpe       = Number(sliderRpe.value);
    const mw        = Number(sliderMw.value);
    const phiVal    = Number(sliderPhi.value);

    const y1Val = y1(phiVal, gamma_sat, r);
    const y2Val = y2(phiVal, gamma_sat, r);
    const y3Val = y3(phiVal, gamma_sat, r, mw, rpe);

    // Update marker trace
    Plotly.restyle(plotDiv, {
        x:[[phiVal, phiVal, phiVal]],
        y:[[y1Val, y2Val, y3Val]]
    }, [markerTraceIndex]);

    // Update vertical line
    const updateShape = {
        'shapes[0].x0':phiVal,
        'shapes[0].x1':phiVal
    };
    Plotly.relayout(plotDiv, updateShape);

    // Update display texts
    const phi_max = computePhiMax(gamma_sat, r, mw, rpe);
    updateInfoDisplays(phi_max, y2Val, y3Val);
}

/* --------------------- EVENT BINDINGS -------------------- */
function addListeners(){
    // φ slider (only marker/line move)
    sliderPhi.addEventListener('input', ()=>{
        setDisplay(dispPhi, sliderPhi.value,4);
        updatePhiOnly();
    });

    // Parameter sliders -> full redraw
    [sliderGammaSat, sliderR, sliderRpe, sliderMw].forEach(sl=>{
        sl.addEventListener('input', ()=>{
            updateAllDisplays();
            drawPlot();
        });
    });

    // Axis sliders
    sliderPhiAxis.addEventListener('input', ()=>{
        updateAllDisplays();
        // update phi slider max, clamp value if needed
        sliderPhi.max = sliderPhiAxis.value;
        if(Number(sliderPhi.value) > Number(sliderPhi.max)){
            sliderPhi.value = sliderPhi.max;
            setDisplay(dispPhi, sliderPhi.value,4);
        }
        drawPlot();
    });

    sliderCAxis.addEventListener('input', ()=>{
        updateAllDisplays();
        drawPlot();
    });
}

/* ------------------------ BOOTSTRAP ---------------------- */
updateAllDisplays();
addListeners();
drawPlot();
</script>
</body>
</html>
